                  "notEquals": "true"
              }
              ]
          },
          {
              "allOf": [
              {
                  "field": "type",
                  "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                  "field": "Microsoft.Compute/virtualMachineScaleSets/virtualMachineProfile.securityProfile.encryptionAtHost",
                  "notEquals": "true"
              }
              ]
          }
          ]
        },
        "then": {
            "effect": "Audit"
        }
  }
  POLICY_RULE
}

