  {
        "if": {
            "not": {
                "allOf": [
                    {
                        "field": "tags['CostCenter']",
                        "exists": "true"
                    },
                    {
                        "field": "tags['Owner']",
                        "exists": "true"
                    },
                    {
                        "field": "tags['Department']",
                        "exists": "true"
                    }
                ]
            }
        },
        "then": {
            "effect": "Modify",
            "details": {
                "roleDefinitionIds": [
                    "/providers/microsoft.authorization/roleDefinitions/9f1af436-2d4a-4c3c-bfd0-4aa7c3c4d54e"
