                "operations": [
                    {
                        "operation": "add",
                        "field": "tags",
                        "value": {
                            "CostCenter": "[concat('CC-',utcNow())]",
                            "Owner": "[concat('Owner-',utcNow())]",
                            "Department": "[concat('Dept-',utcNow())]"
                        }
                    }
                ]
            }
        }
    }
  POLICY_RULE
}

