resource "aws_lambda_permission" "llm_logs" {
  statement_id   = "invoked_by_container_app_logs"
  action         = "lambda:InvokeFunction"
  principal      = "logs.${local.region}.amazonaws.com"
  function_name  = aws_lambda_function.log_to_s3.function_name
  source_arn     = "${module.ecs_cluster.cloudwatch_log_group_arn}/${var.container_name}:*"
  source_account = local.account_id
}

resource "aws_iam_role_policy_attachment" "lambda_basic_execution" {
  role       = aws_iam_role.lambda_llm_logs.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

resource "aws_iam_role_policy_attachment" "cw_subscription" {
  role       = aws_iam_role.lambda_llm_logs.name
  policy_arn = "arn:aws:iam::aws:policy/CloudWatchLogsFullAccess"
}

resource "aws_cloudwatch_log_group" "llm_logs" {
  name              = "/aws/lambda/${aws_lambda_function.log_to_s3.function_name}"
  retention_in_days = 7
}
locals {
  embedding_model_arn = "arn:aws:bedrock:${local.region}::foundation-model/amazon.titan-embed-g1-text-02"
}

resource "aws_bedrockagent_knowledge_base" "main" {
  name        = var.name
  description = "kb for ${var.name}"
  role_arn    = aws_iam_role.bedrock_kb_role.arn

  knowledge_base_configuration {
    type = "VECTOR"
    vector_knowledge_base_configuration {
      embedding_model_arn = local.embedding_model_arn
    }
  }

  storage_configuration {
    type = "RDS"
    rds_configuration {
      credentials_secret_arn = local.bedrock_user_secret
      database_name          = local.database_name
      resource_arn           = local.aurora_cluster_arn
      table_name             = "${local.schema}.${local.table}"
      field_mapping {
        metadata_field    = local.metadata_field
        primary_key_field = local.pkey_field
        text_field        = local.text_field
        vector_field      = local.vector_field
      }
    }
  }

  # ensure that the postgres bedrock schema/table
  # is created before the knowledge base is created
  depends_on = [null_resource.postgres_setup]
}

resource "aws_bedrockagent_data_source" "main" {
  knowledge_base_id = aws_bedrockagent_knowledge_base.main.id
  name              = aws_s3_bucket.main.bucket
  data_source_configuration {
    type = "S3"
    s3_configuration {
      bucket_arn = aws_s3_bucket.main.arn
    }
  }
  data_deletion_policy = "RETAIN"
}

resource "aws_iam_role" "bedrock_kb_role" {
  name               = "BedrockExecutionRoleForKnowledgeBase-${var.name}"
  assume_role_policy = data.aws_iam_policy_document.kb_assume.json
}

resource "aws_iam_role_policy" "kb" {
  role   = aws_iam_role.bedrock_kb_role.name
  policy = data.aws_iam_policy_document.kb.json
}

data "aws_iam_policy_document" "kb_assume" {
  statement {
    sid     = "AmazonBedrockKnowledgeBaseTrustPolicy"
    effect  = "Allow"
    actions = ["sts:AssumeRole"]
    principals {
      type        = "Service"
      identifiers = ["bedrock.amazonaws.com"]
    }
    condition {
      test     = "StringEquals"
      variable = "aws:SourceAccount"
      values   = [local.account_id]
    }
    condition {
      test     = "ArnLike"
      variable = "aws:SourceArn"
      values   = ["arn:aws:bedrock:${local.region}:${local.account_id}:knowledge-base/*"]
    }
  }
}

data "aws_iam_policy_document" "kb" {
  statement {
    sid       = "BedrockInvokeModelStatement"
    effect    = "Allow"
    actions   = ["bedrock:InvokeModel"]
    resources = [local.embedding_model_arn]
  }

  statement {
    sid       = "RdsDescribeStatementID"
    effect    = "Allow"
    actions   = ["rds:DescribeDBClusters"]
    resources = [local.aurora_cluster_arn]
  }

  statement {
    sid    = "DataAPIStatementID"
    effect = "Allow"
    actions = [
      "rds-data:BatchExecuteStatement",
      "rds-data:ExecuteStatement",
    ]
    resources = [local.aurora_cluster_arn]
  }

  statement {
    sid       = "S3ListBucketStatement"
    effect    = "Allow"
    actions   = ["s3:ListBucket"]
    resources = [aws_s3_bucket.main.arn]
    condition {
      test     = "StringEquals"
      variable = "aws:ResourceAccount"
      values   = [local.account_id]
    }
  }

  statement {
    sid       = "S3GetObjectStatement"
    effect    = "Allow"
    actions   = ["s3:GetObject"]
    resources = ["${aws_s3_bucket.main.arn}/*"]
    condition {
      test     = "StringEquals"
      variable = "aws:ResourceAccount"
      values   = [local.account_id]
    }
  }

  statement {
    sid       = "SecretsManagerGetStatement"
    effect    = "Allow"
    actions   = ["secretsmanager:GetSecretValue"]
    resources = [local.bedrock_user_secret]
  }
}

# schedule a KB sync every hour
resource "aws_scheduler_schedule" "main" {
  name                = "${var.name}-kb-sync"
  schedule_expression = "rate(1 days)"

  target {
    arn      = "arn:aws:scheduler:::aws-sdk:bedrockagent:startIngestionJob"
    role_arn = aws_iam_role.event_bridge_scheduler.arn

    input = jsonencode({
      KnowledgeBaseId = aws_bedrockagent_knowledge_base.main.id
      DataSourceId    = aws_bedrockagent_data_source.main.data_source_id
    })
  }

  flexible_time_window {
    mode = "OFF"
  }
}

resource "aws_iam_role" "event_bridge_scheduler" {
  name = "${var.name}-event-bridge-scheduler-role"

  assume_role_policy = <<-EOF
  {
    "Statement": [
      {
        "Effect": "Allow",
        "Principal": {
          "Service": "scheduler.amazonaws.com"
        },
        "Action": "sts:AssumeRole"
      }
    ]
  }
  EOF
}

data "aws_iam_policy_document" "kb_sync" {
  statement {
    effect    = "Allow"
    actions   = ["bedrock:StartIngestionJob"]
    resources = [aws_bedrockagent_knowledge_base.main.arn]
  }
}

resource "aws_iam_role_policy" "event_bridge_scheduler" {
  role   = aws_iam_role.event_bridge_scheduler.name
  policy = data.aws_iam_policy_document.kb_sync.json
}

# deliver kb sync logs to s3
resource "awscc_logs_delivery_source" "kb_logs" {
  name         = "${var.name}-kb-logs"
  log_type     = "APPLICATION_LOGS"
  resource_arn = aws_bedrockagent_knowledge_base.main.arn
  tags = [for k, v in var.tags : {
    key   = k
    value = v
  }]
}

resource "awscc_logs_delivery_destination" "kb_logs" {
  name                     = "${var.name}-kb-logs"
  destination_resource_arn = aws_s3_bucket.kb_logs.arn
  tags = [for k, v in var.tags : {
    key   = k
    value = v
  }]
}

resource "awscc_logs_delivery" "kb_logs" {
  delivery_source_name     = awscc_logs_delivery_source.kb_logs.name
  delivery_destination_arn = awscc_logs_delivery_destination.kb_logs.arn
  tags = [for k, v in var.tags : {
    key   = k
    value = v
  }]
}

resource "aws_s3_bucket" "kb_logs" {
  bucket = "${var.name}-kb-logs-${local.account_id}"
}

resource "aws_s3_bucket_public_access_block" "kb_logs" {
  bucket                  = aws_s3_bucket.main.id
  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

module "ecs_cluster" {
  source  = "terraform-aws-modules/ecs/aws//modules/cluster"

  cluster_name = var.name

  fargate_capacity_providers = {
    FARGATE      = {}
    FARGATE_SPOT = {}
  }

  tags = var.tags
}

module "ecs_service" {
  source  = "terraform-aws-modules/ecs/aws//modules/service"

  name        = var.name
  cluster_arn = module.ecs_cluster.arn

  cpu    = 1024
  memory = 2048

  # supports external task def deployments
  # by ignoring changes to task definition and desired count
  ignore_task_definition_changes = true
  desired_count                  = 1

  # Task Definition
  enable_execute_command = false

  container_definitions = {
    (var.container_name) = {

      image = var.image

      port_mappings = [
        {
          protocol      = "tcp",
          containerPort = var.container_port
        }
      ]

      environment = [
        {
          "name" : "PORT",
          "value" : "${var.container_port}"
        },
        {
          "name" : "HEALTHCHECK",
          "value" : "${var.health_check}"
        },
        {
          "name" : "POSTGRES_DB"
          "value" : local.database_name
        },
        {
          "name" : "POSTGRES_HOST"
          "value" : module.aurora_postgres.cluster_endpoint
        },
        {
          "name" : "KNOWLEDGE_BASE_ID",
          "value" : aws_bedrockagent_knowledge_base.main.id
        },
        {
          "name" : "DB_SECRET_ARN",
          "value" : local.aurora_secret_arn
        },
        {
          "name" : "OTEL_RESOURCE_ATTRIBUTES",
          "value" : "service.namespace=${var.name},service.name=orchestrator"
        },
      ]

      readonly_root_filesystem = false

      dependsOn = [
        {
          containerName = "otel"
          condition     = "HEALTHY"
        }
      ]
    },
    otel = {
      image   = "public.ecr.aws/aws-observability/aws-otel-collector:v0.41.2"
      command = ["--config=/etc/ecs/ecs-default-config.yaml"]
      healthCheck = {
        command     = ["/healthcheck"]
        interval    = 5
        timeout     = 6
        retries     = 5
        startPeriod = 1
      }
    },
  }

  load_balancer = {
    service = {
      target_group_arn = module.alb.target_groups["ecs-task"].arn
      container_name   = var.container_name
      container_port   = var.container_port
    }
  }

  subnet_ids = module.vpc.private_subnets

  security_group_rules = {
    ingress_alb_service = {
      type                     = "ingress"
      from_port                = var.container_port
      to_port                  = var.container_port
      protocol                 = "tcp"
      description              = "Service port"
      source_security_group_id = module.alb.security_group_id
    }
    egress_all = {
      type        = "egress"
      from_port   = 0
      to_port     = 0
      protocol    = "-1"
      cidr_blocks = ["0.0.0.0/0"]
    }
  }

  tasks_iam_role_name        = "${var.name}-tasks"
  tasks_iam_role_description = "role for ${var.name}"

  tasks_iam_role_statements = [
    {
      actions   = ["secretsmanager:GetSecretValue"]
      resources = [local.aurora_secret_arn]
    },
    {
      actions   = ["bedrock:Retrieve"]
      resources = ["arn:aws:bedrock:${local.region}:${local.account_id}:knowledge-base/*"]
    },
    {
      actions   = ["bedrock:InvokeModel"]
      resources = ["arn:aws:bedrock:${local.region}::foundation-model/*"]
    },
    {
      actions = [
        "xray:PutTraceSegments",
        "xray:PutTelemetryRecords"
      ]
      resources = ["*"]
    }
  ]

  tags = var.tags
}

module "alb" {
  source  = "terraform-aws-modules/alb/aws"
  version = "~> 9.0"

  name = var.name

  enable_deletion_protection = false

  vpc_id  = module.vpc.vpc_id
  subnets = module.vpc.public_subnets

  security_group_ingress_rules = {
    all_http = {
      from_port   = 80
      to_port     = 80
      ip_protocol = "tcp"
      description = "HTTP web traffic"
      cidr_ipv4   = "0.0.0.0/0"
    }
  }

  security_group_egress_rules = { for cidr_block in module.vpc.private_subnets_cidr_blocks :
    (cidr_block) => {
      ip_protocol = "-1"
      cidr_ipv4   = cidr_block
    }
  }

  listeners = {
    http = {
      port     = "80"
      protocol = "HTTP"

      forward = {
        target_group_key = "ecs-task"
      }
    }
  }

  target_groups = {

    ecs-task = {
      backend_protocol = "HTTP"
      backend_port     = var.container_port
      target_type      = "ip"

      health_check = {
        enabled             = true
        healthy_threshold   = 5
        interval            = 10
        matcher             = "200-299"
        path                = var.health_check
        port                = "traffic-port"
        protocol            = "HTTP"
        timeout             = 5
        unhealthy_threshold = 2
      }

      create_attachment = false
    }
  }

  tags = var.tags
}
resource "aws_ecr_repository" "main" {
  name                 = var.name
  image_tag_mutability = "IMMUTABLE"
  force_delete         = true

  image_scanning_configuration {
    scan_on_push = true
  }
}
locals {
