}

# Private Route Table
resource "aws_route_table" "private" {
  vpc_id = var.vpc_id

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = aws_nat_gateway.natgw.id
  }

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-rt-private"
  }

  lifecycle {
    ignore_changes = [
      route,
    ]
  }
}

# Associate Public Route Table with Subnet
resource "aws_route_table_association" "public_association" {
  subnet_id      = var.public_subnet_id
  route_table_id = aws_route_table.public.id
}

# Associate Private Route Table with Subnet
resource "aws_route_table_association" "private_association" {
  subnet_id      = var.private_subnet_id
  route_table_id = aws_route_table.private.id
}
# Security Group for Database
resource "aws_security_group" "db_sg" {
  name        = "${lower(var.vendor)}-${lower(var.environment)}-db-sg"
  description = "Security Group for Database"
  vpc_id      = var.vpc_id

  # Allow all outbound traffic
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  # Allow inbound MySQL traffic from App instance
  ingress {
    from_port   = 3306
    to_port     = 3306
    protocol    = "tcp"
    cidr_blocks = ["${var.app_private_ip}/32"]
  }

  # Allow SSH access (if required, from a specific range or trusted IPs)
  ingress {
    from_port   = 22
    to_port     = 22
    protocol    = "tcp"
    cidr_blocks = var.allowed_ssh_cidr_blocks
  }

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-db-sg"
  }
}

# Security Group for Application
resource "aws_security_group" "app_sg" {
  name        = "${lower(var.vendor)}-${lower(var.environment)}-app-sg"
  description = "Security Group for Application"
  vpc_id      = var.vpc_id

  # Allow all outbound traffic
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  # Allow inbound HTTP traffic
  ingress {
    from_port   = 80
    to_port     = 80
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  # Allow inbound HTTPS traffic
  ingress {
    from_port   = 443
    to_port     = 443
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  # Allow SSH access (if required, from a specific range or trusted IPs)
  ingress {
    from_port   = 22
    to_port     = 22
    protocol    = "tcp"
    cidr_blocks = var.allowed_ssh_cidr_blocks
  }

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-app-sg"
  }
}
# Create Subnets

# Define Availability Zones for multi-AZ support
locals {
  availability_zones = ["${var.region}a", "${var.region}b"]  # Support for multiple AZs if needed
}

# Public Subnet
resource "aws_subnet" "public" {
  for_each = toset(local.availability_zones)

  vpc_id            = var.vpc_id
  cidr_block        = element(var.public_cidr_blocks, index(local.availability_zones, each.value))
  availability_zone = each.value

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-public-${each.value}"
  }
}

# Private Subnet
resource "aws_subnet" "private" {
  for_each = toset(local.availability_zones)

  vpc_id            = var.vpc_id
  cidr_block        = element(var.private_cidr_blocks, index(local.availability_zones, each.value))
  availability_zone = each.value

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-private-${each.value}"
  }
}
# Create VPC
resource "aws_vpc" "development_vpc" {
  cidr_block = var.vpc_cidr_block

  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-vpc"
  }
}

# Query the created VPC (optional)
data "aws_vpc" "existing_vpc" {
  id = aws_vpc.development_vpc.id
}
data "aws_ami" "latest_amazon_linux_img" {
  most_recent = true
  owners      = ["amazon"]

  filter {
    name   = "name"
    values = ["amzn2-ami-hvm-*-gp2"]
  }
  filter {
    name   = "virtualization-type"
    values = ["hvm"]
  }
}

# Private Network Interface for App Instance
resource "aws_network_interface" "private_network_interface" {
  subnet_id       = var.public_subnet_id  # Ensure this variable is declared in variables.tf
  security_groups = [var.app_sg_id]       # Ensure this variable is declared in variables.tf
  private_ips     = [var.app_private_ip]  # Ensure this variable is declared in variables.tf
}

# App Instance
resource "aws_instance" "app" {
  ami                       = data.aws_ami.latest_amazon_linux_img.id
  instance_type             = var.app_instance_type  # Ensure this variable is declared in variables.tf
  key_name                  = var.key_name           # Ensure this variable is declared in variables.tf
  associate_public_ip_address = true

  root_block_device {
    volume_type = "gp2"
    volume_size = var.app_volume_size  # Ensure this variable is declared in variables.tf
  }

  network_interface {
    network_interface_id = aws_network_interface.private_network_interface.id
    device_index         = 0
  }

  tags = {
    Name = "${var.vendor}-${var.environment}-app"
  }

  lifecycle {
    ignore_changes = [
      ami,
    ]
  }
}

# Private Network Interface for DB Instance
resource "aws_network_interface" "db_network_interface" {
  subnet_id       = var.private_subnet_id  # Ensure this variable is declared in variables.tf
  security_groups = [var.db_sg_id]         # Ensure this variable is declared in variables.tf
  private_ips     = [var.db_private_ip]    # Ensure this variable is declared in variables.tf
}

# DB Instance
resource "aws_instance" "db" {
  ami           = data.aws_ami.latest_amazon_linux_img.id
  instance_type = var.db_instance_type  # Ensure this variable is declared in variables.tf
  key_name      = var.key_name         # Ensure this variable is declared in variables.tf

  root_block_device {
    volume_type = "gp2"
    volume_size = var.db_volume_size  # Ensure this variable is declared in variables.tf
  }

  network_interface {
    network_interface_id = aws_network_interface.db_network_interface.id
    device_index         = 0
  }

  tags = {
    Name = "${var.vendor}-${var.environment}-db"
  }

  lifecycle {
    ignore_changes = [
      ami,
    ]
  }
}
# Create Internet Gateway
resource "aws_internet_gateway" "gw" {
  vpc_id = var.vpc_id
  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-ig"
  }
}

# Create Elastic IP for NAT Gateway
resource "aws_eip" "nat_eip" {
  network_border_group = "us-west-2"  # Replace with the appropriate AWS region
  depends_on           = [aws_internet_gateway.gw]
  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-nat-eip"
  }
}

# Create NAT Gateway
resource "aws_nat_gateway" "natgw" {
  allocation_id = aws_eip.nat_eip.id
  subnet_id     = var.public_subnet_id
  depends_on    = [aws_internet_gateway.gw]
  tags = {
    Name = "${lower(var.vendor)}-${lower(var.environment)}-nat-gw"
  }
}

