resource "google_storage_bucket_object" "this" {
  name   = "gcsToCoralogix.zip"
  bucket = var.bucket
  source = data.archive_file.this.output_path
}

resource "random_string" "this" {
  length  = 12
  special = false
}

resource "google_cloudfunctions_function" "this" {
  name                  = local.function_name
  description           = "Send GCS logs to Coralogix."
  runtime               = "python38"
  available_memory_mb   = 1024
  timeout               = 60
  source_archive_bucket = google_storage_bucket_object.this.bucket
  source_archive_object = google_storage_bucket_object.this.name
  entry_point           = "to_coralogix"
  environment_variables = {
    CORALOGIX_LOG_URL        = "https://${local.coralogix_url}/api/v1/logs"
    CORALOGIX_TIME_DELTA_URL = "https://${local.coralogix_url}/sdk/v1/time"
    private_key              = var.private_key
    app_name                 = var.application_name
    sub_name                 = var.subsystem_name
    newline_pattern          = var.newline_pattern
  }
  event_trigger {
    event_type = "google.storage.object.finalize"
    resource   = var.bucket
  }
  labels = merge(var.labels, local.labels)
}

resource "google_cloudfunctions_function_iam_member" "this" {
  project        = google_cloudfunctions_function.this.project
  region         = google_cloudfunctions_function.this.region
  cloud_function = google_cloudfunctions_function.this.name
  role           = "roles/cloudfunctions.invoker"
  member         = var.function_iam_member
}

#File: outputs.tf
output "project" {
  description = "GCP Project ID"
  value       = google_cloudfunctions_function.this.project
}

output "region" {
  description = "GCP Region"
  value       = google_cloudfunctions_function.this.region
}

output "function" {
  description = "GCP Cloud Function Name"
  value       = google_cloudfunctions_function.this.name
}

#File: variables.tf
variable "coralogix_region" {
  description = "The Coralogix location region, possible options are [Europe, Europe2, India, Singapore, US]"
  type        = string
  default     = "Europe"
  validation {
    condition = contains(["Europe","Europe2","India","Singapore","US","US2"], var.coralogix_region)
    error_message = "The coralogix region must be on of these values: [Europe, Europe2, India, Singapore, US, US2]."
  }
}

variable "private_key" {
  description = "The Coralogix private key which is used to validate your authenticity"
  type        = string
  sensitive   = true
}

variable "application_name" {
  description = "The name of your application"
  type        = string
}

variable "subsystem_name" {
  description = "The subsystem name of your application"
  type        = string
}

variable "newline_pattern" {
  description = "The pattern for lines splitting"
  type        = string
  default     = "(?:\\r\\n|\\r|\\n)"
}

variable "bucket" {
  description = "The name of the GCS Bucket to watch"
  type        = string
}

variable "labels" {
  description = "A map of labels to add to Cloud Function"
  type        = map(string)
  default     = {}
}

variable "function_iam_member" {
  description = "The IAM member to grant the Cloud Function invoker role"
  type        = string
  default     = "allUsers"
}

#File: versions.tf
terraform {
  required_version = ">= 0.13.1"

  required_providers {
    google = {
      source  = "hashicorp/google"
      version = ">= 4.31.0"
    }
    archive = {
      source  = "hashicorp/archive"
      version = ">= 2.2.0"
    }
    random = {
      source  = "hashicorp/random"
      version = ">= 3.3.2"
    }
  }
}

