locals {
  origin_id = "main"
}


# required

variable "comment" {
  type        = string
  description = "Any comment you want to add to the distribution for easier identification."
}

variable "aliases" {
  type        = list(string)
  description = "List of CNAMEs"
}

variable "certificate_arn" {
  type        = string
  description = "AWS ACM certificate ARN."
}

variable "s3_origin" {
  type = object({
    path                        = string
    bucket_regional_domain_name = string
  })

  description = "S3 origin configuration"
}

# optional

variable "tags" {
  type        = map(string)
  description = "Tags attached to Cloudfront distribution."
  default     = {}
}

variable "custom_error_responses" {
  type = list(object({
    error_code            = number
    error_caching_min_ttl = number
    response_code         = number
    response_page_path    = string
  }))

  default = []

  description = "List of custom error responses for distribution."
}

variable "certificate_minimum_protocol_version" {
  # https://registry.terraform.io/providers/hashicorp/aws/latest/docs/resources/cloudfront_distribution#minimum_protocol_version
  type        = string
  default     = "TLSv1.2_2019"
  description = "The minimum version of the SSL protocol that you want to use for HTTPS."
}

variable "default_root_object" {
  type        = string
  description = "The object that you want CDN to return when an user requests the root URL."
  default     = "index.html"
}

variable "default_cache_behavior" {
  # https://registry.terraform.io/providers/hashicorp/aws/latest/docs/resources/cloudfront_distribution#default-cache-behavior-arguments
  type = object({
    allowed_methods = list(string),
    cached_methods  = list(string),
    min_ttl         = number
    max_ttl         = number
    default_ttl     = number
    compress        = bool
  })

  default = {
    allowed_methods = ["DELETE", "GET", "HEAD", "OPTIONS", "PATCH", "POST", "PUT"]
    cached_methods  = ["GET", "HEAD"]
    min_ttl         = 0
    default_ttl     = 3600  # 1 hour
    max_ttl         = 86400 # 1 day
    compress        = true
  }

  description = "Default cache behavior for this distribution"
}

variable "response_headers_policy_id" {
  type        = string
  description = "The identifier for a response headers policy."
  default     = null
}

variable "price_class" {
  type        = string
  description = "Cloudfront distribution's price class."
  default     = "PriceClass_100"
}


resource "aws_cloudfront_distribution" "this" {
  comment             = var.comment
  enabled             = true
  is_ipv6_enabled     = true
  default_root_object = var.default_root_object
  aliases             = var.aliases
  price_class         = var.price_class

  viewer_certificate {
    cloudfront_default_certificate = false
    acm_certificate_arn            = var.certificate_arn
    minimum_protocol_version       = var.certificate_minimum_protocol_version

    # sni-only is preferred, vip causes CloudFront to use a dedicated IP address and may incur extra charges.
    ssl_support_method = "sni-only"
  }

  restrictions {
    geo_restriction {
      restriction_type = "none"
    }
  }

  origin {
    # https://docs.aws.amazon.com/AWSCloudFormation/latest/UserGuide/aws-properties-cloudfront-distribution-origin.html

    origin_id   = local.origin_id # must be unique within distribution
    origin_path = var.s3_origin.path
    domain_name = var.s3_origin.bucket_regional_domain_name

    s3_origin_config {
      origin_access_identity = aws_cloudfront_origin_access_identity.this.cloudfront_access_identity_path
    }
  }

  default_cache_behavior {
    allowed_methods  = var.default_cache_behavior.allowed_methods
    cached_methods   = var.default_cache_behavior.cached_methods
    target_origin_id = local.origin_id

    response_headers_policy_id = var.response_headers_policy_id

    forwarded_values {
      query_string = false

      cookies {
        forward = "none"
      }
    }

    compress               = var.default_cache_behavior.compress
    viewer_protocol_policy = "redirect-to-https"
    min_ttl                = var.default_cache_behavior.min_ttl
    default_ttl            = var.default_cache_behavior.default_ttl
    max_ttl                = var.default_cache_behavior.max_ttl
  }

  dynamic "custom_error_response" {
    for_each = var.custom_error_responses

    content {
      error_code            = custom_error_response.value.error_code
      error_caching_min_ttl = custom_error_response.value.error_caching_min_ttl
      response_code         = custom_error_response.value.response_code
      response_page_path    = custom_error_response.value.response_page_path
    }
  }

  tags = var.tags
}

resource "aws_cloudfront_origin_access_identity" "this" {
  comment = var.comment
}

data "aws_iam_policy_document" "this" {

  statement {
    sid = 1

    actions = [
      "cloudfront:CreateInvalidation",
    ]

    resources = [
      aws_cloudfront_distribution.this.arn
    ]
  }
}

# Vendor AMI
data "aws_ami" "cw-vendor-ami-latest" {
  most_recent = true
  owners      = [var.vendor_ami_account_number]
  filter {
    name   = "name"
    values = [var.vendor_ami_name_string]
  }
  filter {
    name   = "virtualization-type"
    values = ["hvm"]
  }
  filter {
    name   = "architecture"
    values = ["x86_64"]
  }
  filter {
    name   = "root-device-type"
    values = ["ebs"]
  }
}

# Create AMI with KMS CMK from latest Vendor AMI
resource "aws_ami_copy" "cw-latest-vendor-ami-with-cmk" {
  name              = "${var.name_prefix}-encrypted-ami-${random_string.cw-random.result}"
  description       = "KMS CMK-encrypted AMI of latest official vendor AMI"
  source_ami_id     = data.aws_ami.cw-vendor-ami-latest.id
  source_ami_region = var.aws_region
  encrypted         = true
  kms_key_id        = aws_kms_key.cw-kmscmk-ec2.arn
  tags = {
    Name = "${var.name_prefix}-encrypted-ami-${random_string.cw-random.result}"
  }
}

