provider "aws" {
  region  = var.aws_region
  profile = var.aws_profile
}

# region azs
data "aws_availability_zones" "cw-azs" {
  state = "available"
}

# account id
data "aws_caller_identity" "cw-aws-account" {
}

# kms cmk manager - granted read access to KMS CMKs
data "aws_iam_user" "cw-kmsmanager" {
  user_name = var.kms_manager
}

resource "random_string" "cw-random" {
  length  = 5
  upper   = false
  special = false
}

variable "enable_duckdns" {
  type = number
}

variable "duckdns_domain" {
  type = string
}

variable "duckdns_token" {
  type = string
}

variable "letsencrypt_email" {
  type = string
}
# SSM Managed Policy
data "aws_iam_policy" "cw-instance-policy-ssm" {
  arn = "arn:aws:iam::aws:policy/AmazonSSMManagedInstanceCore"
}

# Instance Policy SSM Parameter
resource "aws_iam_policy" "cw-instance-policy-ssmparameter" {
  name        = "${var.name_prefix}-instance-policy-ssmparameter-${random_string.cw-random.result}"
  path        = "/"
  description = "Provides cw instances access to ssm parameter(s)"
  policy      = <<EOF
{
  "Statement": [
    {
      "Sid": "GetSSMParameter",
      "Effect": "Allow",
      "Action": [
        "ssm:GetParameters"
      ],
      "Resource": ["${aws_ssm_parameter.cw-ssm-param-pass.arn}"]
    },
    {
      "Sid": "SSMCMK",
      "Effect": "Allow",
      "Action": "kms:Decrypt",
      "Resource": ["${aws_kms_key.cw-kmscmk-ssm.arn}"]
    }
  ]
}
EOF
}

# Instance Policy S3
resource "aws_iam_policy" "cw-instance-policy-s3" {
  name        = "${var.name_prefix}-instance-policy-s3-${random_string.cw-random.result}"
  path        = "/"
  description = "Provides cw instances access to s3 objects/bucket"
  policy      = <<EOF
{
  "Statement": [
    {
      "Sid": "ListObjectsinBucket",
      "Effect": "Allow",
      "Action": [
        "s3:ListBucket"
      ],
      "Resource": ["${aws_s3_bucket.cw-bucket.arn}"]
    },
    {
      "Sid": "GetObjectsinBucketPrefix",
      "Effect": "Allow",
      "Action": [
        "s3:GetObject",
        "s3:GetObjectVersion"
      ],
      "Resource": ["${aws_s3_bucket.cw-bucket.arn}/*"]
    },
    {
      "Sid": "PutObjectsinBucketPrefix",
      "Effect": "Allow",
      "Action": [
        "s3:PutObject",
        "s3:PutObjectAcl"
      ],
      "Resource": ["${aws_s3_bucket.cw-bucket.arn}/workstation/*","${aws_s3_bucket.cw-bucket.arn}/ssm/*"]
    },
    {
      "Sid": "S3CMK",
      "Effect": "Allow",
      "Action": [
        "kms:Encrypt",
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:DescribeKey"
      ],
      "Resource": ["${aws_kms_key.cw-kmscmk-s3.arn}"]
    }
  ]
}
EOF
}

# Instance Role
resource "aws_iam_role" "cw-instance-iam-role" {
  name               = "${var.name_prefix}-instance-role-${random_string.cw-random.result}"
  path               = "/"
  assume_role_policy = <<EOF
{
  "Statement": [
      {
          "Action": "sts:AssumeRole",
          "Principal": {
             "Service": "ec2.amazonaws.com"
          },
          "Effect": "Allow",
          "Sid": ""
      }
  ]
}
EOF
}

# Instance Role Attachments
resource "aws_iam_role_policy_attachment" "cw-iam-attach-ssm" {
  role       = aws_iam_role.cw-instance-iam-role.name
  policy_arn = data.aws_iam_policy.cw-instance-policy-ssm.arn
}

resource "aws_iam_role_policy_attachment" "cw-iam-attach-ssmparameter" {
  role       = aws_iam_role.cw-instance-iam-role.name
  policy_arn = aws_iam_policy.cw-instance-policy-ssmparameter.arn
}

resource "aws_iam_role_policy_attachment" "cw-iam-attach-s3" {
  role       = aws_iam_role.cw-instance-iam-role.name
  policy_arn = aws_iam_policy.cw-instance-policy-s3.arn
}

# Instance Profile
resource "aws_iam_instance_profile" "cw-instance-profile" {
  name = "${var.name_prefix}-instance-profile-${random_string.cw-random.result}"
  role = aws_iam_role.cw-instance-iam-role.name
}
# Instance Key
resource "aws_key_pair" "cw-instance-key" {
  key_name   = "${var.name_prefix}-instance-key-${random_string.cw-random.result}"
  public_key = var.instance_key
  tags = {
    Name = "${var.name_prefix}-instance-key"
  }
}

# Instance(s)
resource "aws_instance" "cw-instance-1" {
  ami                    = aws_ami_copy.cw-latest-vendor-ami-with-cmk.id
  instance_type          = var.instance_type
  iam_instance_profile   = aws_iam_instance_profile.cw-instance-profile.name
  key_name               = aws_key_pair.cw-instance-key.key_name
  subnet_id              = aws_subnet.cw-net.id
  private_ip             = var.net_instance_ip
  vpc_security_group_ids = [aws_security_group.cw-sg.id]
  tags = {
    Name = "${var.name_prefix}-workstation-1-${random_string.cw-random.result}",
    cw   = "True"
  }
  user_data = <<EOF
#!/bin/bash
# set hostname
hostnamectl set-hostname ${var.name_prefix}-workstation-1-${random_string.cw-random.result}
EOF
  root_block_device {
    volume_size = var.instance_vol_size
    volume_type = var.instance_vol_type
    encrypted   = "true"
    kms_key_id  = aws_kms_key.cw-kmscmk-ec2.arn
  }
  depends_on = [aws_iam_role_policy_attachment.cw-iam-attach-ssm, aws_iam_role_policy_attachment.cw-iam-attach-s3]
}

# Elastic IP for Instance(s)
resource "aws_eip" "cw-eip-1" {
  vpc                       = true
  instance                  = aws_instance.cw-instance-1.id
  associate_with_private_ip = var.net_instance_ip
  depends_on                = [aws_internet_gateway.cw-gw]
}
resource "aws_kms_key" "cw-kmscmk-s3" {
  description              = "Key for cw s3"
  key_usage                = "ENCRYPT_DECRYPT"
  customer_master_key_spec = "SYMMETRIC_DEFAULT"
  enable_key_rotation      = "true"
  tags = {
    Name = "cw-kmscmk-s3-${random_string.cw-random.result}"
  }
  policy = <<EOF
{
  "Id": "cw-kmskeypolicy-s3",
  "Statement": [
    {
      "Sid": "Enable IAM User Permissions",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${data.aws_iam_user.cw-kmsmanager.arn}"
      },
      "Action": "kms:*",
      "Resource": "*"
    },
    {
      "Sid": "Allow EC2 Encrypt",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${aws_iam_role.cw-instance-iam-role.arn}"
      },
      "Action": [
        "kms:Encrypt",
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:DescribeKey"
      ],
      "Resource": "*",
      "Condition": {
        "StringEquals": {
          "kms:CallerAccount": "${data.aws_caller_identity.cw-aws-account.account_id}",
          "kms:ViaService": "ec2.${var.aws_region}.amazonaws.com"
        }
      }
    },
    {
      "Sid": "Allow access through S3",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${aws_iam_role.cw-instance-iam-role.arn}"
      },
      "Action": [
        "kms:Encrypt",
        "kms:Decrypt",
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:DescribeKey"
      ],
      "Resource": "*",
      "Condition": {
        "StringEquals": {
          "kms:CallerAccount": "${data.aws_caller_identity.cw-aws-account.account_id}",
          "kms:ViaService": "s3.${var.aws_region}.amazonaws.com"
        }
      }
    }
  ]
}
EOF
}

resource "aws_kms_alias" "cw-kmscmk-s3-alias" {
  name          = "alias/${var.name_prefix}-kmscmk-s3-${random_string.cw-random.result}"
  target_key_id = aws_kms_key.cw-kmscmk-s3.key_id
}

resource "aws_kms_key" "cw-kmscmk-ec2" {
  description              = "Key for cw ec2/ebs"
  key_usage                = "ENCRYPT_DECRYPT"
  customer_master_key_spec = "SYMMETRIC_DEFAULT"
  enable_key_rotation      = "true"
  tags = {
    Name = "cw-kmscmk-ec2-${random_string.cw-random.result}"
  }
  policy = <<EOF
{
  "Id": "cw-kmskeypolicy-ec2",
  "Statement": [
    {
      "Sid": "Enable IAM User Permissions",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${data.aws_iam_user.cw-kmsmanager.arn}"
      },
      "Action": "kms:*",
      "Resource": "*"
    },
    {
      "Sid": "Allow attachment of persistent resources",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${aws_iam_role.cw-instance-iam-role.arn}"
      },
      "Action": [
        "kms:CreateGrant",
        "kms:ListGrants",
        "kms:RevokeGrant"
      ],
      "Resource": "*",
      "Condition": {
        "Bool": {
          "kms:GrantIsForAWSResource": "true"
        }
      }
    },
    {
      "Sid": "Allow access through EC2",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${aws_iam_role.cw-instance-iam-role.arn}"
      },
      "Action": [
        "kms:Encrypt",
        "kms:Decrypt",
        "kms:ReEncrypt*",
        "kms:GenerateDataKey*",
        "kms:DescribeKey"
      ],
      "Resource": "*",
      "Condition": {
        "StringEquals": {
          "kms:CallerAccount": "${data.aws_caller_identity.cw-aws-account.account_id}",
          "kms:ViaService": "ec2.${var.aws_region}.amazonaws.com"
        }
      }
    }
  ]
}
EOF
}

resource "aws_kms_alias" "cw-kmscmk-ec2-alias" {
  name          = "alias/${var.name_prefix}-kmscmk-ec2-${random_string.cw-random.result}"
  target_key_id = aws_kms_key.cw-kmscmk-ec2.key_id
}

resource "aws_kms_key" "cw-kmscmk-ssm" {
  description              = "Key for cw ssm/ebs"
  key_usage                = "ENCRYPT_DECRYPT"
  customer_master_key_spec = "SYMMETRIC_DEFAULT"
  enable_key_rotation      = "true"
  tags = {
    Name = "cw-kmscmk-ssm-${random_string.cw-random.result}"
  }
  policy = <<EOF
{
  "Id": "cw-kmskeypolicy-ssm",
  "Statement": [
    {
      "Sid": "Enable IAM User Permissions",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${data.aws_iam_user.cw-kmsmanager.arn}"
      },
      "Action": "kms:*",
      "Resource": "*"
    },
    {
      "Sid": "Allow access through EC2",
      "Effect": "Allow",
      "Principal": {
        "AWS": "${aws_iam_role.cw-instance-iam-role.arn}"
      },
      "Action": "kms:Decrypt",
      "Resource": "*",
      "Condition": {
        "StringEquals": {
          "kms:CallerAccount": "${data.aws_caller_identity.cw-aws-account.account_id}",
          "kms:ViaService": "ssm.${var.aws_region}.amazonaws.com"
        }
      }
    }
  ]
}
EOF
}

resource "aws_kms_alias" "cw-kmscmk-ssm-alias" {
  name          = "alias/${var.name_prefix}-kmscmk-ssm-${random_string.cw-random.result}"
  target_key_id = aws_kms_key.cw-kmscmk-ssm.key_id
}
# vpc and gateway
resource "aws_vpc" "cw-vpc" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = "true"
  enable_dns_hostnames = "true"
  tags = {
    Name = "${var.name_prefix}-vpc-${random_string.cw-random.result}"
  }
}

# internet gateway 
resource "aws_internet_gateway" "cw-gw" {
  vpc_id = aws_vpc.cw-vpc.id
  tags = {
    Name = "${var.name_prefix}-gw-${random_string.cw-random.result}"
  }
}

# public route table
resource "aws_route_table" "cw-pubrt" {
  vpc_id = aws_vpc.cw-vpc.id
  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = aws_internet_gateway.cw-gw.id
  }
  tags = {
    Name = "${var.name_prefix}-pubrt-${random_string.cw-random.result}"
  }
}

# net
resource "aws_subnet" "cw-net" {
  vpc_id            = aws_vpc.cw-vpc.id
  availability_zone = data.aws_availability_zones.cw-azs.names[0]
  cidr_block        = var.net_cidr
  tags = {
    Name = "${var.name_prefix}-net-${random_string.cw-random.result}"
  }
  depends_on = [aws_internet_gateway.cw-gw]
}

# public route table associations
resource "aws_route_table_association" "rt-assoc-net" {
  subnet_id      = aws_subnet.cw-net.id
  route_table_id = aws_route_table.cw-pubrt.id
}
# s3 bucket
resource "aws_s3_bucket" "cw-bucket" {
  bucket = "${var.name_prefix}-bucket-${random_string.cw-random.result}"
  acl    = "private"
  versioning {
    enabled = true
  }
  server_side_encryption_configuration {
    rule {
      apply_server_side_encryption_by_default {
        kms_master_key_id = aws_kms_key.cw-kmscmk-s3.arn
        sse_algorithm     = "aws:kms"
      }
    }
  }
  force_destroy = true
  policy        = <<POLICY
{
  "Statement": [
    {
      "Sid": "KMS Manager",
      "Effect": "Allow",
      "Principal": {
        "AWS": ["${data.aws_iam_user.cw-kmsmanager.arn}"]
      },
      "Action": [
        "s3:*"
      ],
      "Resource": [
        "arn:aws:s3:::${var.name_prefix}-bucket-${random_string.cw-random.result}",
        "arn:aws:s3:::${var.name_prefix}-bucket-${random_string.cw-random.result}/*"
      ]
    },
    {
      "Sid": "Instance List",
      "Effect": "Allow",
      "Principal": {
        "AWS": ["${aws_iam_role.cw-instance-iam-role.arn}"]
      },
      "Action": [
        "s3:ListBucket"
      ],
      "Resource": ["arn:aws:s3:::${var.name_prefix}-bucket-${random_string.cw-random.result}"]
    },
    {
      "Sid": "Instance Get",
      "Effect": "Allow",
      "Principal": {
        "AWS": ["${aws_iam_role.cw-instance-iam-role.arn}"]
      },
      "Action": [
        "s3:GetObject",
        "s3:GetObjectVersion"
      ],
      "Resource": ["arn:aws:s3:::${var.name_prefix}-bucket-${random_string.cw-random.result}/*"]
    },
    {
      "Sid": "Instance Put",
      "Effect": "Allow",
      "Principal": {
        "AWS": ["${aws_iam_role.cw-instance-iam-role.arn}"]
      },
      "Action": [
        "s3:PutObject",
        "s3:PutObjectAcl"
      ],
      "Resource": [
        "arn:aws:s3:::${var.name_prefix}-bucket-${random_string.cw-random.result}/ssm/*"
      ]
    }
  ]
}
POLICY
}

# s3 block all public access to bucket
resource "aws_s3_bucket_public_access_block" "cw-bucket-pubaccessblock" {
  bucket                  = aws_s3_bucket.cw-bucket.id
  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

# s3 objects (playbook)
resource "aws_s3_bucket_object" "cw-workstation-files" {
  for_each       = fileset("../playbooks/", "**")
  bucket         = aws_s3_bucket.cw-bucket.id
  key            = "playbook/${each.value}"
  content_base64 = base64encode(file("${path.module}/../playbooks/${each.value}"))
  kms_key_id     = aws_kms_key.cw-kmscmk-s3.arn
}
# security groups
resource "aws_security_group" "cw-sg" {
  name        = "${var.name_prefix}-sg"
  description = "Security group for public traffic"
  vpc_id      = aws_vpc.cw-vpc.id
  tags = {
    Name = "cw-sg"
  }
}

# public sg rules
resource "aws_security_group_rule" "cw-sg-mgmt-ssh-in" {
  security_group_id = aws_security_group.cw-sg.id
  type              = "ingress"
  description       = "IN FROM MGMT - SSH MGMT"
  from_port         = "22"
  to_port           = "22"
  protocol          = "tcp"
  cidr_blocks       = [var.mgmt_cidr]
}

resource "aws_security_group_rule" "cw-sg-mgmt-https-in" {
  security_group_id = aws_security_group.cw-sg.id
  type              = "ingress"
  description       = "IN FROM SELF, MGMT - HTTPS"
  from_port         = "443"
  to_port           = "443"
  protocol          = "tcp"
  cidr_blocks       = [var.mgmt_cidr, "${aws_eip.cw-eip-1.public_ip}/32"]
}

resource "aws_security_group_rule" "cw-sg-out-tcp" {
  security_group_id = aws_security_group.cw-sg.id
  type              = "egress"
  description       = "OUT TO WORLD - TCP"
  from_port         = 0
  to_port           = 65535
  protocol          = "tcp"
  cidr_blocks       = ["0.0.0.0/0"]
}

resource "aws_security_group_rule" "cw-sg-out-udp" {
  security_group_id = aws_security_group.cw-sg.id
  type              = "egress"
  description       = "OUT TO WORLD - UDP"
  from_port         = 0
  to_port           = 65535
  protocol          = "udp"
  cidr_blocks       = ["0.0.0.0/0"]
}
resource "aws_ssm_parameter" "cw-ssm-param-pass" {
  name   = "${var.name_prefix}-cw-web-password-${random_string.cw-random.result}"
  type   = "SecureString"
  key_id = aws_kms_key.cw-kmscmk-ssm.key_id
  value  = var.cw_password
}

resource "aws_ssm_document" "cw-ssm-doc" {
  name          = "${var.name_prefix}-ssm-doc-${random_string.cw-random.result}"
  document_type = "Command"
  content       = <<DOC
  {
    "description": "Ansible Playbooks via SSM for Ubuntu 18.04 and 20.04, installs Ansible properly.",
    "parameters": {
    "SourceType": {
      "description": "(Optional) Specify the source type.",
      "type": "String",
      "allowedValues": [
      "GitHub",
      "S3"
      ]
    },
    "SourceInfo": {
      "description": "Specify 'path'. Important: If you specify S3, then the IAM instance profile on your managed instances must be configured with read access to Amazon S3.",
      "type": "StringMap",
      "displayType": "textarea",
      "default": {}
    },
    "PlaybookFile": {
      "type": "String",
      "description": "(Optional) The Playbook file to run (including relative path). If the main Playbook file is located in the ./automation directory, then specify automation/playbook.yml.",
      "default": "hello-world-playbook.yml",
      "allowedPattern": "[(a-z_A-Z0-9\\-)/]+(.yml|.yaml)$"
    },
    "ExtraVariables": {
      "type": "String",
      "description": "(Optional) Additional variables to pass to Ansible at runtime. Enter key/value pairs separated by a space. For example: color=red flavor=cherry",
      "default": "SSM=True",
      "displayType": "textarea",
      "allowedPattern": "^$|^\\w+\\=[^\\s|:();&]+(\\s\\w+\\=[^\\s|:();&]+)*$"
    },
    "Verbose": {
      "type": "String",
      "description": "(Optional) Set the verbosity level for logging Playbook executions. Specify -v for low verbosity, -vv or vvv for medium verbosity, and -vvvv for debug level.",
      "allowedValues": [
      "-v",
      "-vv",
      "-vvv",
      "-vvvv"
      ],
      "default": "-v"
    }
    },
    "mainSteps": [
    {
      "action": "aws:downloadContent",
      "name": "downloadContent",
      "inputs": {
      "SourceType": "{{ SourceType }}",
      "SourceInfo": "{{ SourceInfo }}"
      }
    },
    {
      "action": "aws:runShellScript",
      "name": "runShellScript",
      "inputs": {
      "runCommand": [
        "#!/bin/bash",
        "# Ensure ansible is installed",
        "sudo apt-get update",
        "sudo DEBIAN_FRONTEND=noninteractive apt-get -y install python3-pip git",
        "sudo pip3 install ansible",
        "echo \"Running Ansible in `pwd`\"",
        "#this section locates files and unzips them",
        "for zip in $(find -iname '*.zip'); do",
        "  unzip -o $zip",
        "done",
        "PlaybookFile=\"{{PlaybookFile}}\"",
        "if [ ! -f  \"$${PlaybookFile}\" ] ; then",
        "   echo \"The specified Playbook file doesn't exist in the downloaded bundle. Please review the relative path and file name.\" >&2",
        "   exit 2",
        "fi",
        "/usr/local/bin/ansible-playbook -i \"localhost,\" -c local -e \"{{ExtraVariables}}\" \"{{Verbose}}\" \"$${PlaybookFile}\""
      ]
      }
    }
    ]
  }
DOC
}

resource "aws_ssm_association" "cw-ssm-assoc" {
  association_name = "${var.name_prefix}-ssm-assoc-${random_string.cw-random.result}"
  name             = aws_ssm_document.cw-ssm-doc.name
  targets {
    key    = "tag:cw"
    values = ["True"]
  }
  output_location {
    s3_bucket_name = aws_s3_bucket.cw-bucket.id
    s3_key_prefix  = "ssm"
  }
  parameters = {
    ExtraVariables = "name_prefix=${var.name_prefix} name_suffix=${random_string.cw-random.result} guacnet_cidr=${var.guacnet_cidr} guacnet_guacd=${var.guacnet_guacd} guacnet_guacdb=${var.guacnet_guacdb} guacnet_guacamole=${var.guacnet_guacamole} guacnet_webproxy=${var.guacnet_webproxy} guacnet_duckdnsupdater=${var.guacnet_duckdnsupdater} aws_region=${var.aws_region} desktop=${var.desktop} enable_duckdns=${var.enable_duckdns} duckdns_domain=${var.duckdns_domain} duckdns_token=${var.duckdns_token} letsencrypt_email=${var.letsencrypt_email}"
    PlaybookFile   = "cloud_workstation_aws.yml"
    SourceInfo     = "{\"path\":\"https://s3.${var.aws_region}.amazonaws.com/${aws_s3_bucket.cw-bucket.id}/playbook/\"}"
    SourceType     = "S3"
    Verbose        = "-v"
  }
  depends_on = [aws_iam_role_policy_attachment.cw-iam-attach-ssm, aws_iam_role_policy_attachment.cw-iam-attach-s3, aws_s3_bucket_object.cw-workstation-files]
}

