}

variable "vm_instance_name" {
  type        = string
  description = "Name of the VM Instance"
}

variable "vm_instance_machine_type" {
  type        = string
  description = "Machine type for the VM Instance"
  sensitive   = true
}

variable "resoucre_tags" {
  type        = map(string)
