#File: main.tf
################################################################################
# Create Service Account to be assigned to Workload client
################################################################################
resource "google_service_account" "service_account_workload" {
  account_id   = "${var.name_prefix}-wkld-sa-${var.resource_tag}"
  display_name = "${var.name_prefix}-wkld-sa-${var.resource_tag}"
}


################################################################################
# Create workload instance host with automatic public IP association
################################################################################
resource "google_compute_instance" "server_host" {
  count        = var.workload_count
  name         = "${var.name_prefix}-workload-${count.index + 1}-${var.resource_tag}"
  machine_type = var.instance_type
  zone         = element(var.zones, count.index)
  tags         = ["zscc-workload"]
  network_interface {
    subnetwork = var.subnet
  }
  metadata = {
    ssh-keys = "ubuntu:${var.ssh_key}"
  }
  metadata_startup_script = "sudo apt install net-tools"
  boot_disk {
    initialize_params {
      image = var.workload_image_name
      type  = "pd-ssd"
      size  = "10"
    }
  }
  service_account {
    email  = google_service_account.service_account_workload.email
    scopes = ["cloud-platform"]
  }
}

################################################################################
# Create pre-defined GCP Firewall rules for Workload
################################################################################
resource "google_compute_firewall" "ssh_intranet_workload" {
  name    = "${var.name_prefix}-fw-ssh-for-workload-${var.resource_tag}"
  network = var.vpc_network
  allow {
    protocol = "tcp"
    ports    = ["22"]
  }
  source_ranges           = var.allowed_ssh_from_internal_cidr
  target_service_accounts = [google_service_account.service_account_workload.email]
}

#File: outputs.tf
output "private_ip" {
  description = "Instance Private IP"
  value       = google_compute_instance.server_host[*].network_interface[0].network_ip
}

output "workload_network_tag" {
  description = "Network tag as the source of a route rule"
  value       = google_compute_instance.server_host[0].tags
}

#File: variables.tf
variable "name_prefix" {
  type        = string
  description = "A prefix to associate to all the workload module resources"
  default     = null
}

variable "resource_tag" {
  type        = string
  description = "A tag to associate to all workload module resources"
  default     = null
}

variable "subnet" {
  type        = string
  description = "A subnet the workload host is in"
}

variable "instance_type" {
  type        = string
  description = "The bastion host instance type"
  default     = "e2-micro"
}

variable "ssh_key" {
  type        = string
  description = "A public key uploaded to the bastion instance"
}

variable "zones" {
  type        = list(string)
  description = "The zone that the machine should be created in. If it is not provided, the provider zone is used"
}

variable "allowed_ssh_from_internal_cidr" {
  type        = list(string)
  description = "CIDR allowed to ssh the bastion host from Intranet"
}

variable "workload_image_name" {
  type        = string
  description = "Custom image name to be used for deploying bastion/workload appliances"
  default     = "ubuntu-os-cloud/ubuntu-2204-lts"
}

variable "vpc_network" {
  type        = string
  description = "Workload VPC network"
}

variable "workload_count" {
  type        = number
  description = "The number of Workload VMs to deploy"
  default     = 1
  validation {
    condition     = var.workload_count >= 1 && var.workload_count <= 250
    error_message = "Input workload_count must be a whole number between 1 and 250."
  }
}

#File: versions.tf
terraform {
  required_providers {
    google = {
      source  = "hashicorp/google"
      version = ">= 5.11.0"
    }
  }
  required_version = ">= 0.13.7, < 2.0.0"
}

locals {
  create = var.create && var.putin_khuylo
}

variable "create" {
  description = "Controls whether to create resources"
  type        = bool
  default     = true
}

variable "create_package" {
  description = "Whether to create Lambda function package (requires Python)"
  type        = string
  default     = false
}

# Tags
variable "tags" {
  description = "A map of tags to assign to all resources"
  type        = map(string)
  default     = {}
}

variable "lambda_tags" {
  description = "A map of tags to assign to all resources created by Lambda module"
  type        = map(string)
  default     = {}
}

variable "eventbridge_tags" {
  description = "A map of tags to assign to all resources created by EventBridge module"
  type        = map(string)
  default     = {}
}

# Lambda
variable "name" {
  description = "Lambda function name"
  type        = string
  default     = "cloudwatch-log-retention-manager"
}

variable "description" {
  description = "Description of Lambda function"
  type        = string
  default     = "CloudWatch Log Retention Manager"
}

variable "source_path" {
  description = "Source path object with instructions on how to build Lambda function package"
  type        = any
  default     = null
}

variable "memory_size" {
  description = "Amount of memory in MB your Lambda Function can use at runtime. Valid value between 128 MB to 10,240 MB (10 GB), in 64 MB increments."
  type        = number
  default     = 256
}

variable "timeout" {
  description = "The amount of time your Lambda Function has to run in seconds."
  type        = number
  default     = 30
}

variable "environment_variables" {
  description = "A map that defines environment variables for the Lambda Function."
  type        = map(string)
  default     = {}
}

variable "cloudwatch_logs_retention_in_days" {
  description = "Specifies the number of days you want to retain log events in the specified log group. Possible values are: 1, 3, 5, 7, 14, 30, 60, 90, 120, 150, 180, 365, 400, 545, 731, 1827, and 3653."
  type        = number
  default     = 90
}

# EventBridge
variable "eventbridge_role_name" {
  description = "Name of EventBridge IAM role to create"
  type        = string
  default     = null
}

variable "schedule_expression" {
  description = "Schedule expression for EventBridge to trigger Lambda function. Can be cron() or rate()."
  type        = string
  default     = "rate(12 hours)"
}

variable "role_arn" {
  description = "ARN of IAM Role used by EventBridge to invoke Lambda Function"
  type        = string
  default     = null
}

variable "putin_khuylo" {
  description = "Do you agree that Putin doesn't respect Ukrainian sovereignty and territorial integrity? More info: https://en.wikipedia.org/wiki/Putin_khuylo!"
  type        = bool
  default     = true
}



module "lambda_function" {
  source  = "terraform-aws-modules/lambda/aws"

  create = local.create

  function_name = var.name
  description   = var.description
  handler       = "log_retention_manager.lambda_handler"
  runtime       = "python3.10"
  publish       = true

  memory_size           = var.memory_size
  timeout               = var.timeout
  environment_variables = var.environment_variables

  cloudwatch_logs_retention_in_days = var.cloudwatch_logs_retention_in_days

  create_package = var.create_package
  source_path    = var.source_path != null ? var.source_path : "${path.module}/src/log_retention_manager.py"

  local_existing_package = var.create_package ? null : "${path.module}/dist/log_retention_manager.zip"

  attach_policy_statements = true
  policy_statements = {
    readonly = {
      effect = "Allow"
      actions = [
        "ec2:DescribeRegions",
        "logs:DescribeLogGroups"
      ]
      resources = ["*"]
    },
    logs_retention_policy = {
      effect    = "Allow",
      actions   = ["logs:PutRetentionPolicy"],
      resources = ["*"]
    }
  }

  create_current_version_allowed_triggers = false
  allowed_triggers = {
    scheduled = {
      principal  = "scheduler.amazonaws.com"
      source_arn = module.eventbridge.eventbridge_schedule_arns["lambda-${var.name}"]
    }
  }

  tags = merge(var.tags, var.lambda_tags)
}

########################
# EventBridge Schedules
########################
module "eventbridge" {
  source  = "terraform-aws-modules/eventbridge/aws"

  create = local.create

  create_bus  = false
  create_role = var.role_arn == null

  role_name = var.eventbridge_role_name

  attach_lambda_policy = true
  lambda_target_arns   = [module.lambda_function.lambda_function_arn]

  schedules = {
    "lambda-${var.name}" = {
      description         = "Trigger Lambda ${var.name}"
      schedule_expression = var.schedule_expression
      arn                 = module.lambda_function.lambda_function_arn
      role_arn            = var.role_arn
    }
  }

  tags = merge(var.tags, var.eventbridge_tags)
}

