  description = "The GCP region for this subnetwork."
}

provider "aws" {
  region = "us-east-1"
}

resource "aws_iam_role" "lambda_exec" {
  name = "lambda_exec"

  assume_role_policy = jsonencode({
    Statement = [{
      Action    = "sts:AssumeRole",
      Principal = {
        Service = "lambda.amazonaws.com"
      },
      Effect = "Allow"
    }]
  })
}

resource "aws_lambda_function" "my_lambda" {
  function_name = "example_lambda"
  handler       = "index.handler"
  runtime       = "python3.9"
  filename      = "lambda.zip"
  role          = aws_iam_role.lambda_exec.arn
}

variable "aws_region" {
  type        = string
  default     = ""
  description = "AWS region for the backend resources. Will default to the session region."
}

variable "s3_bucket_name" {
  type        = string
  default     = null
  description = "Name for the S3 bucket created to store Terraform state."
}

variable "s3_key_prefix" {
  type        = string
  default     = null
  description = "Key prefix for the Terraform state objects in the S3 bucket."
}

variable "dynamo_table_name" {
  type        = string
  default     = "terraform-locking"
  description = "Name for the DynamocDB table created to lock Terraform state."
}

variable "iam_policy_name" {
  type        = string
  default     = "Terraform-S3-Backend"
  description = "Name for the IAM policy enabling access to the backend."
}

variable "parameter_prefix" {
  type    = string
  default = "terraform-state"
}


provider "aws" {
  region = var.aws_region
}


data "aws_region" "current" {}
data "aws_caller_identity" "current" {}

resource "aws_s3_bucket" "terraform_state_bucket" {
  bucket = can(var.s3_bucket_name) ? var.s3_bucket_name : null

  tags = {
    Name        = "Terraform S3 Backend - State"
    application = "Terraform S3 Backend"
  }

  lifecycle {
    prevent_destroy = false
  }
}

resource "aws_s3_bucket_versioning" "terraform_state" {
  bucket = aws_s3_bucket.terraform_state_bucket.id

  versioning_configuration {
    status = "Enabled"
  }
}

resource "aws_s3_bucket_public_access_block" "terraform_state" {
  bucket = aws_s3_bucket.terraform_state_bucket.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

resource "aws_dynamodb_table" "terraform_lock_table" {
  name     = var.dynamo_table_name
  hash_key = "LockID"

  billing_mode = "PAY_PER_REQUEST"

  attribute {
    name = "LockID"
    type = "S"
  }

  tags = {
    Name        = "Terraform S3 Backend - Locking"
    application = "Terraform S3 Backend"
  }

  lifecycle {
    prevent_destroy = false
  }
}

resource "aws_iam_policy" "terraform_s3_backend_policy" {
  name        = var.iam_policy_name
  description = "Terraform S3 backend access."

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Sid    = "Bucket"
        Effect = "Allow"
        Action = [
          "s3:ListBucket"
        ]
        Resource = aws_s3_bucket.terraform_state_bucket.arn
      },
      {
        Sid    = "StateAccess"
        Effect = "Allow"
        Action = [
          "s3:GetObject",
          "s3:PutObject",
          "s3:DeleteObject"
        ]
        Resource = var.s3_key_prefix != null ? "${aws_s3_bucket.terraform_state_bucket.arn}/${var.s3_key_prefix}/*" : "${aws_s3_bucket.terraform_state_bucket.arn}/*"
      },
      {
        Sid    = "Locking"
        Effect = "Allow"
        Action = [
          "dynamodb:GetItem",
          "dynamodb:PutItem",
          "dynamodb:DeleteItem"
        ]
        Resource = aws_dynamodb_table.terraform_lock_table.arn
      },
      {
        Sid    = "Parameters"
        Effect = "Allow"
        Action = [
          "ssm:DescribeParameters",
          "ssm:GetParameter",
          "ssm:GetParameterHistory",
          "ssm:GetParameters",
          "ssm:GetParametersByPath"
        ]
        Resource = "arn:aws:ssm:${var.aws_region}:${data.aws_caller_identity.current.account_id}:parameter/${var.parameter_prefix}/*"
      }
    ]
  })
}

resource "aws_ssm_parameter" "terraform_state_bucket" {
  name        = "/${var.parameter_prefix}/s3-backend-bucket"
  type        = "String"
  description = "Bucket used for Terraform S3 backend deployment(s)."
  value       = aws_s3_bucket.terraform_state_bucket.id

  tags = {
    application = "Terraform S3 Backend"
  }
}

resource "aws_ssm_parameter" "terraform_state_key_prefix" {
  name        = "/${var.parameter_prefix}/s3-backend-prefix"
  type        = "String"
  description = "Key prefix for Terraform state."
  value       = var.s3_key_prefix != null ? var.s3_key_prefix : ""

  tags = {
    application = "Terraform S3 Backend"
  }
}

resource "aws_ssm_parameter" "terraform_lock_table" {
  name        = "/${var.parameter_prefix}/s3-backend-lock-table"
  type        = "String"
  description = "DynamoDB locking table used for Terraform S3 backend deployment(s)."
  value       = aws_dynamodb_table.terraform_lock_table.id

  tags = {
    application = "Terraform S3 Backend"
  }
}

provider "aws" {
  region = var.region
}

variable "region" { default = "eu-west-2" }
variable "vpc_cidr" { default = "10.0.0.0/16" }
variable "vpc_name" { default = "lambdas-vpc" }
variable "private_subnet_cidr" { default = "10.0.0.0/24" }
variable "private_subnet_name" { default = "lambdas-private" }
variable "public_subnet_cidr" { default = "10.0.1.0/24" }
variable "public_subnet_name" { default = "lambdas-public" }
variable "eip_name" {
    default ="Elastic IP for Lambdas VPC"
}
variable "nat_gateway_name" {
    default = "NAT Gateway for Lambdas VPC"
}
variable "custom_tags" {
  description = "A map of custom tags to apply to resources"
  type        = map(string)
  default     = {}
}

//// VPC
resource "aws_vpc" "lambdas" {
  cidr_block = var.vpc_cidr
  tags = merge(
    {
      Name = var.vpc_name
    },
    var.custom_tags
  )
}


//// Networks
resource "aws_subnet" "lambdas-private" {
  cidr_block = var.private_subnet_cidr
  vpc_id     = aws_vpc.lambdas.id
  tags = {
    Name = var.private_subnet_name
  }
}

resource "aws_subnet" "lambdas-public" {
  cidr_block = var.public_subnet_cidr
  vpc_id     = aws_vpc.lambdas.id
  tags = {
    Name = var.public_subnet_name
  }
}


//// Gateways
resource "aws_internet_gateway" "lambdas" {
  vpc_id = aws_vpc.lambdas.id
}

resource "aws_nat_gateway" "lambdas" {
  allocation_id = aws_eip.lambdas.id
  subnet_id     = aws_subnet.lambdas-public.id
  tags = merge(
    {
      Name = var.nat_gateway_name
    },
    var.custom_tags
  )
}


//// Route tables Private
resource "aws_route_table" "lambdas-private" {
  vpc_id = aws_vpc.lambdas.id

  route {
    cidr_block     = "0.0.0.0/0"
    nat_gateway_id = aws_nat_gateway.lambdas.id
  }
}

resource "aws_route_table_association" "lambdas-private" {
  route_table_id = aws_route_table.lambdas-private.id
  subnet_id      = aws_subnet.lambdas-private.id
}


//// Route tables Public
resource "aws_route_table" "lambdas-public" {
  vpc_id = aws_vpc.lambdas.id

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = aws_internet_gateway.lambdas.id
  }
}

resource "aws_route_table_association" "lambdas-public" {
  route_table_id = aws_route_table.lambdas-public.id
  subnet_id      = aws_subnet.lambdas-public.id
}


//// ElasticIp
resource "aws_eip" "lambdas" {
  domain = "vpc"
  tags = merge(
    {
      Name = var.eip_name
    },
    var.custom_tags
  )
}

resource "aws_security_group" "allow_tls" {
  depends_on = [
    aws_vpc.lambdas
  ]
  name        = "allow_tls"
  description = "Allow TLS inbound traffic"
  vpc_id      = aws_vpc.lambdas.id

  ingress {
    description      = "TLS from VPC"
    from_port        = 443
    to_port          = 443
    protocol         = "tcp"
    cidr_blocks      = [var.vpc_cidr]
    ipv6_cidr_blocks = ["::/0"]
  }

  egress {
    from_port        = 0
    to_port          = 0
    protocol         = "-1"
    cidr_blocks      = ["0.0.0.0/0"]
    ipv6_cidr_blocks = ["::/0"]
  }

  tags = {
    Name = "allow_tls"
  }
}

#File: imports.tf
# Steps to take in order to import `data.google_cloud_run_service.atuf` Cloud Run service resource originating from cloudbuild.yaml
# provisioned with `google_cloudbuild_trigger.atuf_trigger` so it could be managed by Terraform and i.e destroyed along with other resources.
#
# Reference:
# Blog post: App architecture with reliability in mind: From Kubernetes to Serverless with GCP Cloud Build & Cloud Run
# https://foolcontrol.org/?p=4621
# 
# Cloud run service "Import" reference: https://registry.terraform.io/providers/hashicorp/google/latest/docs/resources/cloud_run_service#import

# 1. In import block, replace {{project}}, {{location}}, {{name}} with actual service project, location, name from Outputs, i.e:
# id = "projects/fooctrl-312814/locations/europe-west4/services/atuf"
# and uncomment import block

# 2. Run Terraform plan with `generate-config-out` flag:
# terraform plan -generate-config-out=generated.tf

# 3. Review generated.tf (optional)

# 4. Run Terraform plan without any flags (there should be 1 new import):
# terraform plan

# 5. Run Terraform apply to import & Terraform manage previously external resource:
# terraform apply -auto-approve

# import {
#   id = "projects/ {{project}}/locations/{{location}}/services/atuf"
#   to = google_cloud_run_v2_service.default
# }
#File: main.tf
# Automatically deploy app hosted in Github to Cloud Run using Cloud Build (cloudbuild.yaml) & avoid any ClickOps with Terraform
#
# Process explained in detail as part of following blog post:
# App architecture with reliability in mind: From Kubernetes to Serverless with GCP Cloud Build & Cloud Run
# https://foolcontrol.org/?p=4621
# 
# Youtube video: 
# https://youtu.be/ksz1Vfg3ZQI?si=TIDL_p7sYS7oExPv

# Providers setup
terraform {
  required_providers {
    google = {
      source = "hashicorp/google"
      version = "5.13.0"
    }
    google-beta = {
      source  = "hashicorp/google-beta"
      version = "5.13.0"
    }
  }
}

provider "google" {
    # Provider config options
    project = var.project
    region = var.region
    credentials = file(var.gcp_credentials)
}

provider "google-beta" {
    # Provider config options for google-beta
    project = var.project
    region = var.region
    credentials = file(var.gcp_credentials)
}


# Requires Service Usage API & Identity to be manually enabled: 
module "project-services" {

  source  = "terraform-google-modules/project-factory/google//modules/project_services"
  version = "~> 14.4"

  project_id = var.project

  activate_apis = [
    "iam.googleapis.com", # Identity and Access Management (IAM) API
    "cloudresourcemanager.googleapis.com", # Cloud Resource Manager API
    "cloudbuild.googleapis.com", # Cloud Build API
    "artifactregistry.googleapis.com", # Artifacts Registry API
    "run.googleapis.com" # Cloud Run (Admin) API
  ]

  disable_services_on_destroy = true
}

# Create "cloud-sa" service account for Cloud Build & Run use
resource "google_service_account" "cloudbuild_service_account" {

  depends_on = [ module.project-services ]
  account_id = "cloud-sa"
}

# Define additional roles to assign to cloud-sa
variable "roles_to_assign" {
  description = "List of roles to assign"
  type        = list(string)
  default     = [
    "roles/iam.serviceAccountUser", # "default" role needed for Cloud Build & Cloud run
    "roles/logging.logWriter", # needed for logging
    "roles/artifactregistry.admin", # needed for "Authenticate with  GCP Artifacts Registry" in cloudbuild.yaml to work
    "roles/run.developer", # needed for cloud run to work
    "roles/run.admin" # needed for "Allow public (unauthenticated) access" in cloudbuild.yaml to work
    ] 
}

# Set additional roles to cloud-sa
resource "google_project_iam_binding" "cloud_sa_roles" {

  depends_on = [ module.project-services ]

  project = var.project
  count   = length(var.roles_to_assign)
  role    = var.roles_to_assign[count.index]

  members = [
    "serviceAccount:${google_service_account.cloudbuild_service_account.email}",
  ]
}

# Create atuf repo & add a cleanup policy for artifacts repo to keep only the most recent 3 versions of artifacts
resource "google_artifact_registry_repository" "atuf_repo" {

  depends_on = [ 
    google_project_iam_binding.cloud_sa_roles,
    module.project-services
  ]

  provider = google-beta
  project = var.project
  location = var.region
  repository_id = var.project_name
  description   = "${var.project_name_description} container repository"
  format        = "DOCKER"
  cleanup_policy_dry_run = false

  cleanup_policies {
    id     = "keep-minimum-versions"
    action = "KEEP"
    most_recent_versions {
      keep_count  = 3
    }
  }
}

# Create Cloud Build trigger
resource "google_cloudbuild_trigger" "atuf_trigger" {

  depends_on = [
    google_project_iam_binding.cloud_sa_roles,
    google_artifact_registry_repository.atuf_repo,
    module.project-services
  ]

  name     = var.cloudbuild_trigger_name
  location = var.region
  description = "Terraform generated trigger for ${var.project_name_description} GitHub repository"
  filename = "cloudbuild.yaml"
  service_account = google_service_account.cloudbuild_service_account.id

  # Please note: 
  # Connecting to Github repo using CloudBuild trigger won't be possible using Terraform and will have to be done using Google Console for the first time
  # otherwise will run into following error:
  # Error creating Trigger: googleapi: Error 400: Repository mapping does not exist. 
  # Please visit https://console.cloud.google.com/cloud-build/triggers;region=europe-west4/connect?project=fooctrl-312814 to connect a repository to your project
  # 
  # Explained in detail in Step 6 of:
  # "Automatically deploy app hosted in Github to Cloud Run using Cloud Build (cloudbuild.yaml) & avoid any ClickOps with Terraform" section on:
  # https://foolcontrol.org/?p=4621 & following Youtube video: ToDo: add link here
  github {
    owner = var.github_owner
    name  = var.github_repo_connection
    push {
      branch = "^main"
    }
  }
}

# Trigger the Cloud Build trigger
#
# Explained in detail in Step 5 of:
# "Automatically deploy app hosted in Github to Cloud Run using Cloud Build (cloudbuild.yaml) & avoid any ClickOps with Terraform" section on:
# https://foolcontrol.org/?p=4621 & following Youtube video: ToDo: add link here
resource "null_resource" "build_trigger_run" {

  depends_on = [google_cloudbuild_trigger.atuf_trigger]

  provisioner "local-exec" {
    command = "gcloud builds triggers run ${var.cloudbuild_trigger_name} --branch=main --region=${var.region}"
  }
}

# Polling mechanism to wait until "atuf-tf-trigger" build run has been completed successfully and there's a running Cloud Run service
#
# Explained in detail in Step 5 of:
# "Automatically deploy app hosted in Github to Cloud Run using Cloud Build (cloudbuild.yaml) & avoid any ClickOps with Terraform" section on:
# https://foolcontrol.org/?p=4621 & following Youtube video: ToDo: add link here
resource "null_resource" "build_trigger_run_wait" {

  depends_on = [
    google_cloudbuild_trigger.atuf_trigger,
    null_resource.build_trigger_run,
    google_project_iam_binding.cloud_sa_roles,
    module.project-services
  ]

  provisioner "local-exec" {
    command = "./scripts/wait_for_cloud_run.sh"
    interpreter = ["/bin/bash", "-c"]

    environment = {
      REGION  = var.region
      PROJECT = var.project
    }
  }
}

# Get data about running ATUF Cloud Run service deployed from cloudbuild.yaml
data "google_cloud_run_v2_service" "atuf" {
  depends_on = [
    google_cloudbuild_trigger.atuf_trigger, 
    null_resource.build_trigger_run,
    null_resource.build_trigger_run_wait,
    module.project-services
  ]

  location = var.region
  name     = var.project_name
}

# Map a custom domain using Cloud Run domain mapping (Limited availability and Preview)
# Reference: https://cloud.google.com/run/docs/mapping-custom-domains
#
# Explained in detail in Step 11 of:
# "Automatically deploy app hosted in Github to Cloud Run using Cloud Build (cloudbuild.yaml) & avoid any ClickOps with Terraform" section on:
# https://foolcontrol.org/?p=4621 & following Youtube video: ToDo: add link here
resource "google_cloud_run_domain_mapping" "default" {
  name     = var.cloud_run_domain
  location = data.google_cloud_run_v2_service.atuf.location
  metadata {
    namespace = var.project
  }
  spec {
    route_name = data.google_cloud_run_v2_service.atuf.name
  }

  depends_on = [
    google_cloudbuild_trigger.atuf_trigger, 
    null_resource.build_trigger_run_wait,
    data.google_cloud_run_v2_service.atuf,
    module.project-services
  ]
}

# Output info about Cloud Run deployment
output "atuf_cloud_run_service_info" {
  value = {
    service_name = data.google_cloud_run_v2_service.atuf.name
    service_location = data.google_cloud_run_v2_service.atuf.location
    service_project = data.google_cloud_run_v2_service.atuf.project
    service_url = data.google_cloud_run_v2_service.atuf.uri
    service_domain_value = google_cloud_run_domain_mapping.default.name
  }

  depends_on = [ 
    google_cloudbuild_trigger.atuf_trigger, 
    null_resource.build_trigger_run_wait,
    data.google_cloud_run_v2_service.atuf,
    google_cloud_run_domain_mapping.default
  ]
}
