      spec[0].container[0].env["AZURE_CLIENT_ID"],
      spec[0].container[0].env["AZURE_TENANT_ID"],
      spec[0].container[0].env["AZURE_FEDERATED_TOKEN_FILE"],
      spec[0].container[0].env["AZURE_AUTHORITY_HOST"],
      spec[0].container[0].volume_mount
    ]
  }
}

