# Simple website serving at naked domain / www. subdomain
provider "aws" {
  profile = var.aws_profile
  region  = var.aws_region
}


module "website" {
  source  = "worldofprasanna/staticwebsite/aws"
  version = "1.0.0"
  domain  = var.domain
}

# AWS Specific variables
variable "aws_region" {
  type        = string
  description = "AWS Region to create the resource"
  default     = "us-east-1"
}

variable "aws_profile" {
  type        = string
  description = "AWS Profile to choose"
  default     = "default"
}
locals {
  route53_domain = var.route53_domain != "" ? var.route53_domain : var.domain
}

resource "aws_route53_zone" "main" {
  name = "${local.route53_domain}"
}

resource "aws_route53_record" "app" {
  zone_id = "${aws_route53_zone.main.zone_id}"
  name    = "${local.bucket_name}"
  type    = "A"

  alias {
    name                   = "${aws_cloudfront_distribution.main.domain_name}"
    zone_id                = "${aws_cloudfront_distribution.main.hosted_zone_id}"
    evaluate_target_health = false
  }
}

resource "aws_route53_record" "cert_validation" {
  name    = "${aws_acm_certificate.cert.domain_validation_options.0.resource_record_name}"
  type    = "${aws_acm_certificate.cert.domain_validation_options.0.resource_record_type}"
  zone_id = "${aws_route53_zone.main.id}"
  records = ["${aws_acm_certificate.cert.domain_validation_options.0.resource_record_value}"]
  ttl     = 60
}
locals {
  bucket_name = var.bucket_name != "" ? var.bucket_name : var.domain
}

resource "aws_s3_bucket" "main" {
  bucket = local.bucket_name
  acl    = "private"
  policy = data.aws_iam_policy_document.bucket_policy.json

  website {
    index_document = var.index_document
    error_document = var.error_document
  }

  force_destroy = var.s3_force_destroy

  tags = {
    "Name" = local.bucket_name
  }
}

data "aws_iam_policy_document" "bucket_policy" {
  statement {
    sid = "AllowReadFromAll"

    actions = [
      "s3:GetObject",
    ]

    resources = [
      "arn:aws:s3:::${local.bucket_name}/*",
    ]

    principals {
      type        = "*"
      identifiers = ["*"]
    }
  }
}

# Refactor it to use loop
resource "aws_s3_bucket_object" "index" {
  bucket       = local.bucket_name
  key          = "index.html"
  source       = "initial_files/index.html"
  content_type = "text/html"
}

resource "aws_s3_bucket_object" "error" {
  bucket       = local.bucket_name
  key          = "error.html"
  source       = "initial_files/error.html"
  content_type = "text/html"
}
variable "bucket_name" {
  type        = string
  description = "The Fully Qualified domain name should be the bucket name"
  default     = ""
}

variable "route53_domain" {
  type        = string
  description = "Route53 hostedzone name"
  default     = ""
}

variable "domain" {
  type        = string
  description = "simple website where route53 hosted zone and bucket name are same. Naked domain"
  default     = ""
}

variable "s3_force_destroy" {
  type        = string
  description = "Destroy the s3 bucket inspite of contents in it."
  default     = "false"
}

variable "index_document" {
  type        = string
  description = "Index page to be used for website. Defaults to index.html"
  default     = "index.html"
}

variable "error_document" {
  type        = string
  description = "Error page to be used for website. Defaults to error.html"
  default     = "error.html"
}

variable "secret_user_agent" {
  type        = string
  description = "secret to authenticate CF requests to s3"
  default     = "SECRET-STRING"
}
resource "aws_acm_certificate" "cert" {
  domain_name       = "${local.bucket_name}"
  validation_method = "DNS"
}

resource "aws_acm_certificate_validation" "cert" {
  certificate_arn         = "${aws_acm_certificate.cert.arn}"
  validation_record_fqdns = ["${aws_route53_record.cert_validation.fqdn}"]
}

resource "aws_cloudfront_distribution" "main" {
  http_version = "http2"

  origin {
    origin_id   = "origin-${local.bucket_name}"
    domain_name = aws_s3_bucket.main.website_endpoint

    custom_origin_config {
      origin_protocol_policy = "http-only"
      http_port              = "80"
      https_port             = "443"
      origin_ssl_protocols   = ["TLSv1.2"]
    }

    custom_header {
      name  = "User-Agent"
      value = var.secret_user_agent
    }
  }

  enabled             = true
  default_root_object = var.index_document

  aliases = concat([local.bucket_name])

  restrictions {
    geo_restriction {
      restriction_type = "none"
    }
  }

  default_cache_behavior {
    target_origin_id = "origin-${local.bucket_name}"
    allowed_methods  = ["GET", "HEAD"]
    cached_methods   = ["GET", "HEAD"]
    compress         = true

    forwarded_values {
      query_string = false

      cookies {
        forward = "none"
      }
    }

    viewer_protocol_policy = "redirect-to-https"
    min_ttl                = 0
    default_ttl            = 300
    max_ttl                = 1200
  }

  viewer_certificate {
    acm_certificate_arn      = "${aws_acm_certificate_validation.cert.certificate_arn}"
    ssl_support_method       = "sni-only"
    minimum_protocol_version = "TLSv1"
  }
}

