            },
            "jwtClaimChecks" : {}
          }
        }
      },
      "login" : {
        "cookieExpiration" : {
          "convention" : "FixedTime",
          "timeToExpiration" : "08:00:00"
        },
        "nonce" : {
          "nonceExpirationInterval" : "00:05:00",
          "validateNonce" : true
        },
        "preserveUrlFragmentsForLogins" : false,
        "routes" : {},
        "tokenStore" : {
