#File: main.tf
terraform {
  required_providers {
    google = {
      source  = "hashicorp/google"
      version = "6.8.0"
    }
  }
}

provider "google" {
  project = "add-gcp-project-id-here"
  region  = "northamerica-south1"
  zone    = "northamerica-south1-c"
}

resource "google_compute_network" "vpc_network" {
  name                    = "lfclass"
  auto_create_subnetworks = false
}

resource "google_compute_subnetwork" "vpc_subnetwork" {
  name                     = "lfclass"
  network                  = google_compute_network.vpc_network.name
  ip_cidr_range            = "10.2.0.0/16"
  region                   = "northamerica-south1"
  private_ip_google_access = false
}

resource "google_compute_firewall" "vpc_firewall" {
  name    = "lfclass"
  network = google_compute_network.vpc_network.name

  # allow all ports and protocols
  allow {
    protocol = "all"
  }

  # allow traffic from anywhere
  source_ranges = ["0.0.0.0/0"]
}

resource "google_compute_project_metadata" "ssh_keys" {
  metadata = {
    ssh-keys = "${var.username}:${var.public_key}"
  }
}

resource "google_compute_instance" "vm_instances" {
  for_each     = toset(["cp", "worker"])
  name         = each.key
  machine_type = "e2-standard-2"
  tags         = ["k8s", "dev"]

  boot_disk {
    initialize_params {
      image = "ubuntu-os-cloud/ubuntu-2004-lts"
      size  = 20
    }
  }

  network_interface {
    network    = google_compute_network.vpc_network.name
    subnetwork = google_compute_subnetwork.vpc_subnetwork.name
    access_config {
    }
  }
}

#File: variables.tf
variable "public_key" {
  description = "The public key to be used for SSH access to the instances"
  type        = string
  default     = "add-your-public-key-here"
}

variable "username" {
  description = "The username to be used for SSH access to the instances"
  type        = string
  default     = "add-your-login-for-virtual-machines-here"
}

#File: main.tf
resource "azurerm_public_ip" "jumpboxPip" {
    name                         = "jumpbox-pip"
    location                     = var.location
    resource_group_name          = var.rg
    allocation_method            = "Dynamic"
}

resource "azurerm_network_security_group" "nsg" {
  name                = "jumpbox-nsg"
  location                     = var.location
  resource_group_name          = var.rg

  security_rule {
    name                       = "SSH"
    priority                   = 100
    direction                  = "Inbound"
    access                     = "Allow"
    protocol                   = "Tcp"
    source_port_range          = "*"
    destination_port_range     = "22"
    source_address_prefix      = "*"
    destination_address_prefix = "*"
  }
}

resource "azurerm_network_interface" "nic" {
    name                = "jumpbox-nic"
    resource_group_name = var.rg
    location            = var.location

    ip_configuration {
        name                          = "jumpbox-config"
        subnet_id                     = var.subnetId
        private_ip_address_allocation = "Dynamic"
        public_ip_address_id          = azurerm_public_ip.jumpboxPip.id
    }
}

resource "azurerm_network_interface_security_group_association" "nsgLink" {
    network_interface_id      = azurerm_network_interface.nic.id
    network_security_group_id = azurerm_network_security_group.nsg.id
}

resource "azurerm_linux_virtual_machine" "vm" {
  name                            = "jumpbox-vm"
  resource_group_name             = var.rg
  location                        = var.location
  size                            = "Standard_D2as_v4"
  admin_username                  = "adminuser"
  admin_password                  = "samplePassword!"
  disable_password_authentication = false

  network_interface_ids = [
    azurerm_network_interface.nic.id,
  ]

  os_disk {
    caching              = "ReadWrite"
    storage_account_type = "Standard_LRS"
  }

  source_image_reference {
    publisher = "Canonical"
    offer     = "UbuntuServer"
    sku       = "18.04-LTS"
    version   = "latest"
  }
}
