# Enables access to provider details
# https://www.terraform.io/docs/providers/google/d/datasource_client_config.html
data "google_client_config" "context" {}


# Loads into locals for less typing!
# https://www.terraform.io/docs/configuration/locals.html
locals {
  project = data.google_client_config.context.project
}

#File: archive.tf
# Create the archive project
# https://registry.terraform.io/providers/hashicorp/archive/latest/docs/data-sources/archive_file
data "archive_file" "function_archive" {
  type        = "zip"
  output_path = "/tmp/${var.name}-function.zip"
  source_dir  = var.function_source
}

# Archive file containing the function
# https://registry.terraform.io/providers/hashicorp/google/latest/docs/resources/storage_bucket_object
resource "google_storage_bucket_object" "function_archive" {
  name   = "${var.name}-function.zip"
  bucket = "${data.google_client_config.context.project}-functions"
  source = "/tmp/${var.name}-function.zip"
}

#File: function.tf
# Creates a service account for jobs
# https://www.terraform.io/docs/providers/google/r/google_service_account.html
resource "google_service_account" "function_service_account" {
  account_id   = "${var.name}-function"
  display_name = "${var.name} service account"
  description  = "service account for executing the ${var.name} function"
}

# Waits for the service account
# https://www.terraform.io/docs/providers/google/r/google_service_account.html
# https://github.com/hashicorp/terraform/issues/17726#issuecomment-377357866
resource "null_resource" "function_service_account_delay" {
  provisioner "local-exec" {
    command = "sleep 30"
  }
  triggers = {
    "function_service_account" = google_service_account.function_service_account.unique_id
  }
}

# IAM permissions to invoke the function
# https://registry.terraform.io/providers/hashicorp/google/latest/docs/resources/cloudfunctions_cloud_function_iam
resource "google_cloudfunctions_function_iam_member" "function_invoker" {
  cloud_function = google_cloudfunctions_function.function.name
  role           = "roles/cloudfunctions.invoker"
  member         = "serviceAccount:${google_service_account.function_service_account.email}"
  depends_on     = [null_resource.function_service_account_delay]
}


# Creates the function from the archive
# https://registry.terraform.io/providers/hashicorp/google/latest/docs/resources/cloudfunctions_function
resource "google_cloudfunctions_function" "function" {
  name                  = "${var.name}-function"
  description           = "cloud function '${var.name}'"
  runtime               = var.runtime
  available_memory_mb   = var.available_memory_mb
  source_archive_bucket = "${data.google_client_config.context.project}-functions"
  source_archive_object = google_storage_bucket_object.function_archive.name
  trigger_http          = true
  entry_point           = var.entry_point
  service_account_email = google_service_account.function_service_account.email
  depends_on            = [null_resource.function_service_account_delay]
}

#File: scheduler.tf
# Creates the scheduler job for the requirement
# https://www.terraform.io/docs/providers/google/r/cloud_scheduler_job.html
resource "google_cloud_scheduler_job" "scheduler_job" {

  name             = "${var.name}-scheduler-job"
  description      = "Calls the '${google_cloudfunctions_function.function.name}' cloud function"
  time_zone        = var.time_zone
  schedule         = var.schedule
  attempt_deadline = var.attempt_deadline

  http_target {
    uri         = google_cloudfunctions_function.function.https_trigger_url
    http_method = var.http_method
    body        = base64encode("{\"name\":\"HTTP\"}")
    oidc_token {
      service_account_email = google_service_account.function_service_account.email
    }
  }

  depends_on = [null_resource.function_service_account_delay]
}

