  #tfsec:ignore:aws-ec2-no-public-ip-subnet
  map_public_ip_on_launch = true

  tags = {
    "Name"                                            = "public-eks-1-${var.region}b"
    "kubernetes.io/role/elb"                          = "1"
    "kubernetes.io/cluster/${var.eks_cluster_1_name}" = "owned"
  }

  depends_on = [aws_vpc.eks_vpc_1]
}
# Internet gateway creation
resource "aws_internet_gateway" "eks_igw_1" {
  vpc_id = aws_vpc.eks_vpc_1.id

  tags = {
    Name = "igw-eks-1"
  }

  depends_on = [aws_vpc.eks_vpc_1]
}
# Elastic IP allocation
resource "aws_eip" "eks_nat_eip_1" {
  domain = "vpc"

  tags = {
    Name = "nat-eip-eks-1"
  }

  depends_on = [aws_vpc.eks_vpc_1]
}

# Public NAT creation
resource "aws_nat_gateway" "eks_nat_1" {
  allocation_id = aws_eip.eks_nat_eip_1.id
  subnet_id     = aws_subnet.eks_public_subnet_1a.id

  tags = {
    Name = "nat-eks-1"
  }

  depends_on = [
    aws_internet_gateway.eks_igw_1,
    aws_eip.eks_nat_eip_1,
    aws_subnet.eks_public_subnet_1a
  ]
}
# Private route table creation
resource "aws_route_table" "eks_private_route_1" {
  vpc_id = aws_vpc.eks_vpc_1.id

  route {
    cidr_block     = var.private_route_1_cidr
    nat_gateway_id = aws_nat_gateway.eks_nat_1.id
  }

  tags = {
    Name = "private-route-eks-1"
  }

  depends_on = [aws_nat_gateway.eks_nat_1]
}

# Public route table creation
resource "aws_route_table" "eks_public_route_1" {
  vpc_id = aws_vpc.eks_vpc_1.id

  route {
    cidr_block = var.public_route_1_cidr
    gateway_id = aws_internet_gateway.eks_igw_1.id
  }

  tags = {
    Name = "public-route-eks-1"
  }

  depends_on = [aws_internet_gateway.eks_igw_1]
}

# Private route table association
resource "aws_route_table_association" "eks_private_subnet_1a" {
  subnet_id      = aws_subnet.eks_private_subnet_1a.id
  route_table_id = aws_route_table.eks_private_route_1.id

  depends_on = [
    aws_subnet.eks_private_subnet_1a,
    aws_route_table.eks_private_route_1
  ]
}

# Private route table association
resource "aws_route_table_association" "eks_private_subnet_1b" {
  subnet_id      = aws_subnet.eks_private_subnet_1b.id
  route_table_id = aws_route_table.eks_private_route_1.id

  depends_on = [
    aws_subnet.eks_private_subnet_1b,
    aws_route_table.eks_private_route_1
  ]
}

# Public route table association
resource "aws_route_table_association" "eks_public_subnet_1a" {
  subnet_id      = aws_subnet.eks_public_subnet_1a.id
  route_table_id = aws_route_table.eks_public_route_1.id

  depends_on = [
    aws_subnet.eks_public_subnet_1a,
    aws_route_table.eks_public_route_1
  ]
}

# Public route table association
resource "aws_route_table_association" "eks_public_subnet_1b" {
  subnet_id      = aws_subnet.eks_public_subnet_1b.id
  route_table_id = aws_route_table.eks_public_route_1.id

  depends_on = [
    aws_subnet.eks_public_subnet_1b,
    aws_route_table.eks_public_route_1
  ]
}
# IAM role creation
resource "aws_iam_role" "eks_cluster_1_role" {
  name = "eks-cluster-${var.eks_cluster_1_name}"

  assume_role_policy = <<POLICY
{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Effect": "Allow",
      "Principal": {
        "Service": "eks.amazonaws.com"
      },
      "Action": "sts:AssumeRole"
    }
  ]
}
POLICY
}

# EKS cluster policy attachment
resource "aws_iam_role_policy_attachment" "eks_cluster_1_role_AmazonEKSClusterPolicy" {
  policy_arn = "arn:aws:iam::aws:policy/AmazonEKSClusterPolicy"
  role       = aws_iam_role.eks_cluster_1_role.name

  depends_on = [aws_iam_role.eks_cluster_1_role]
}

# EKS cluster creation
resource "aws_eks_cluster" "eks_cluster_1" {
  name     = var.eks_cluster_1_name
  version  = var.eks_cluster_version
  role_arn = aws_iam_role.eks_cluster_1_role.arn

  encryption_config {
    resources = ["secrets"]
    provider {
      key_arn = aws_kms_key.kms_key_3.arn
    }
  }

  #tfsec:ignore:aws-eks-no-public-cluster-access-to-cidr
  vpc_config {
    subnet_ids = [
      aws_subnet.eks_private_subnet_1a.id,
      aws_subnet.eks_private_subnet_1b.id,
      aws_subnet.eks_public_subnet_1a.id,
      aws_subnet.eks_public_subnet_1b.id
    ]

    #tfsec:ignore:aws-eks-no-public-cluster-access
    endpoint_public_access  = true
    endpoint_private_access = true
  }

  # Enable logging for the EKS control plane
  enabled_cluster_log_types = [
    "api",
    "audit",
    "authenticator",
    "controllerManager",
    "scheduler"
  ]

  depends_on = [
    aws_subnet.eks_private_subnet_1a,
    aws_subnet.eks_private_subnet_1b,
    aws_subnet.eks_public_subnet_1a,
    aws_subnet.eks_public_subnet_1b,
    aws_iam_role_policy_attachment.eks_cluster_1_role_AmazonEKSClusterPolicy
  ]
}
# IAM role creation
resource "aws_iam_role" "eks_node_1_role" {
  name = "node-role-1-eks-1"

  assume_role_policy = jsonencode({
    Statement = [{
      Action = "sts:AssumeRole"
      Effect = "Allow"
      Principal = {
        Service = "ec2.amazonaws.com"
      }
    }]
    Version = "2012-10-17"
  })
}

# Node group policy attachment
resource "aws_iam_role_policy_attachment" "eks_node_1_role_AmazonEKSWorkerNodePolicy" {
  policy_arn = "arn:aws:iam::aws:policy/AmazonEKSWorkerNodePolicy"
  role       = aws_iam_role.eks_node_1_role.name

  depends_on = [aws_iam_role.eks_node_1_role]
}

# Node group policy attachment
resource "aws_iam_role_policy_attachment" "eks_node_1_role_AmazonEKS_CNI_Policy" {
  policy_arn = "arn:aws:iam::aws:policy/AmazonEKS_CNI_Policy"
  role       = aws_iam_role.eks_node_1_role.name

  depends_on = [aws_iam_role.eks_node_1_role]
}

# Node group policy attachment
resource "aws_iam_role_policy_attachment" "eks_node_1_role_AmazonEC2ContainerRegistryReadOnly" {
  policy_arn = "arn:aws:iam::aws:policy/AmazonEC2ContainerRegistryReadOnly"
  role       = aws_iam_role.eks_node_1_role.name

  depends_on = [aws_iam_role.eks_node_1_role]
}

# Node group policy attachment for Amazon EBS CSI driver
resource "aws_iam_role_policy_attachment" "eks_node_1_role_AmazonEBSCSIDriverPolicy" {
  policy_arn = "arn:aws:iam::aws:policy/service-role/AmazonEBSCSIDriverPolicy"
  role       = aws_iam_role.eks_node_1_role.name

  depends_on = [aws_iam_role.eks_node_1_role]
}

# Node group creation
resource "aws_eks_node_group" "private_eks_node_group_1" {
  cluster_name    = var.eks_cluster_1_name
  node_group_name = var.eks_node_group_1_name
  node_role_arn   = aws_iam_role.eks_node_1_role.arn

  subnet_ids = [
    aws_subnet.eks_private_subnet_1a.id,
    aws_subnet.eks_private_subnet_1b.id
  ]

  capacity_type  = var.eks_node_group_1_capacity_type
  instance_types = [var.eks_node_group_1_instance_types]

  scaling_config {
    desired_size = var.eks_node_group_1_desired_size
    max_size     = var.eks_node_group_1_max_size
    min_size     = var.eks_node_group_1_min_size
  }

  update_config {
    max_unavailable = var.eks_node_group_1_max_unavailable
  }

  labels = var.eks_node_group_1_labels

  depends_on = [
    aws_eks_cluster.eks_cluster_1,
    aws_iam_role_policy_attachment.eks_node_1_role_AmazonEKSWorkerNodePolicy,
    aws_iam_role_policy_attachment.eks_node_1_role_AmazonEKS_CNI_Policy,
    aws_iam_role_policy_attachment.eks_node_1_role_AmazonEC2ContainerRegistryReadOnly
  ]
}
# KMS key creation
resource "aws_kms_key" "kms_key_1" {
  description             = "KMS key for terraform state storage bucket"
  deletion_window_in_days = var.kms_key_1_default_retention_days
  enable_key_rotation     = true
}

# Creating an AWS KMS alias
resource "aws_kms_alias" "kms_key_1_alias" {
  name          = "alias/terraform-bucket-key-eks-1"
  target_key_id = aws_kms_key.kms_key_1.key_id

  depends_on = [aws_kms_key.kms_key_1]
}

# Creating an AWS KMS alias
resource "aws_kms_key" "kms_key_2" {
  description             = "KMS key for DynamoDB table encryption"
  deletion_window_in_days = var.kms_key_2_default_retention_days
  enable_key_rotation     = true
}

# Creating an AWS KMS alias
resource "aws_kms_alias" "kms_key_2_alias" {
  name          = "alias/dynamodb-table-encryption-eks-1"
  target_key_id = aws_kms_key.kms_key_2.key_id

  depends_on = [aws_kms_key.kms_key_2]
}

# Creating an AWS KMS alias
resource "aws_kms_key" "kms_key_3" {
  description             = "KMS key for EKS"
  deletion_window_in_days = var.kms_key_3_default_retention_days
  enable_key_rotation     = true
}

# Creating an AWS KMS alias
resource "aws_kms_alias" "kms_key_3_alias" {
  name          = "alias/encryption-eks-1"
  target_key_id = aws_kms_key.kms_key_3.key_id

  depends_on = [aws_kms_key.kms_key_3]
}
# Random string generation
resource "random_id" "random_id_1" {
  byte_length = 5
}

# S3 bucket creation
resource "aws_s3_bucket" "bucket_1" {
  # S3 bucket name
  # Note that S3 buckets in AWS are global, which means that it is not possible to use a name that has been used by someone else
  bucket              = "terraform-state-storage-eks-1-${random_id.random_id_1.hex}"
  object_lock_enabled = true

  tags = {
    Name = "terraform-state-storage-eks-1"
  }

  depends_on = [
    random_id.random_id_1,
    aws_kms_key.kms_key_1,
    aws_s3_bucket.log_bucket_1
  ]
}

# S3 bucket versioning
resource "aws_s3_bucket_versioning" "bucket_1_versioning" {
  bucket = aws_s3_bucket.bucket_1.id

  versioning_configuration {
    status = "Enabled"
  }

  depends_on = [aws_s3_bucket.bucket_1]
}

# S3 bucket public access configuration
resource "aws_s3_bucket_public_access_block" "bucket_1_public_access_block_block" {
  bucket = aws_s3_bucket.bucket_1.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true

  depends_on = [aws_s3_bucket.bucket_1]
}

# S3 bucket server-side encryption configuration
resource "aws_s3_bucket_server_side_encryption_configuration" "bucket_1_sse_configuration" {
  bucket = aws_s3_bucket.bucket_1.id

  rule {
    apply_server_side_encryption_by_default {
      kms_master_key_id = aws_kms_key.kms_key_1.arn
      sse_algorithm     = "aws:kms"
    }
  }

  depends_on = [
    aws_s3_bucket.bucket_1,
    aws_kms_key.kms_key_1
  ]
}

# Create a new S3 bucket to store logs
# tfsec:ignore:aws-s3-enable-bucket-logging
resource "aws_s3_bucket" "log_bucket_1" {
  bucket              = "terraform-state-storage-logs-eks-1-${random_id.random_id_1.hex}"
  object_lock_enabled = true
}

# S3 bucket versioning
resource "aws_s3_bucket_versioning" "log_bucket_1_versioning" {
  bucket = aws_s3_bucket.log_bucket_1.id

  versioning_configuration {
    status = "Enabled"
  }

  depends_on = [aws_s3_bucket.log_bucket_1]
}

# S3 bucket public access configuration
resource "aws_s3_bucket_public_access_block" "log_bucket_1_public_access_block_block" {
  bucket = aws_s3_bucket.log_bucket_1.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true

  depends_on = [aws_s3_bucket.log_bucket_1]
}

# Log bucket policy to allow write access for log delivery
resource "aws_s3_bucket_policy" "log_bucket_1_policy" {
  bucket = aws_s3_bucket.log_bucket_1.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Principal = {
          Service = "delivery.logs.amazonaws.com"
        }
        Action   = "s3:PutObject"
        Resource = "${aws_s3_bucket.log_bucket_1.arn}/*"
      }
    ]
  })

  depends_on = [aws_s3_bucket.log_bucket_1]
}

# S3 bucket server-side encryption configuration
resource "aws_s3_bucket_server_side_encryption_configuration" "log_bucket_1_sse_configuration" {
  bucket = aws_s3_bucket.log_bucket_1.id

  rule {
    apply_server_side_encryption_by_default {
      kms_master_key_id = aws_kms_key.kms_key_1.arn
      sse_algorithm     = "aws:kms"
    }
  }

  depends_on = [
    aws_s3_bucket.log_bucket_1,
    aws_kms_key.kms_key_1
  ]
}

# Enable logging for the main S3 bucket and set the log bucket as the target for log storage
resource "aws_s3_bucket_logging" "log_bucket_1_logging" {
  bucket = aws_s3_bucket.bucket_1.id

  # Specify the target bucket for storing logs and the prefix for log files
  target_bucket = aws_s3_bucket.log_bucket_1.id
  target_prefix = "log/"

  depends_on = [
    aws_s3_bucket.log_bucket_1,
    aws_s3_bucket.bucket_1
  ]
}

# Create a new S3 bucket to store logs
# tfsec:ignore:aws-s3-enable-bucket-logging
resource "aws_s3_bucket" "log_bucket_2" {
  bucket              = "vpc-flow-logs-eks-1-${random_id.random_id_1.hex}"
  object_lock_enabled = true
}

# S3 bucket versioning
resource "aws_s3_bucket_versioning" "log_bucket_2_versioning" {
  bucket = aws_s3_bucket.log_bucket_2.id

  versioning_configuration {
    status = "Enabled"
  }

  depends_on = [aws_s3_bucket.log_bucket_2]
}

# S3 bucket public access configuration
resource "aws_s3_bucket_public_access_block" "log_bucket_2_public_access_block_block" {
  bucket = aws_s3_bucket.log_bucket_2.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true

  depends_on = [aws_s3_bucket.log_bucket_2]
}

# Log bucket policy to allow write access for log delivery
resource "aws_s3_bucket_policy" "log_bucket_2_policy" {
  bucket = aws_s3_bucket.log_bucket_2.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow",
        Principal = {
          Service = "logs.amazonaws.com"
        },
        Action   = "s3:PutObject",
        Resource = "${aws_s3_bucket.log_bucket_2.arn}/*"
      }
    ]
  })

  depends_on = [aws_s3_bucket.log_bucket_2]
}

# S3 bucket server-side encryption configuration
resource "aws_s3_bucket_server_side_encryption_configuration" "log_bucket_2_sse_configuration" {
  bucket = aws_s3_bucket.log_bucket_2.id

  rule {
    apply_server_side_encryption_by_default {
      kms_master_key_id = aws_kms_key.kms_key_1.arn
      sse_algorithm     = "aws:kms"
    }
  }

  depends_on = [
    aws_s3_bucket.log_bucket_2,
    aws_kms_key.kms_key_1
  ]
}

# Create a VPC Flow Log resource to enable flow logs for the VPC
resource "aws_flow_log" "vpc_1_flow_logs" {
  log_destination      = aws_s3_bucket.log_bucket_2.arn
  log_destination_type = "s3"
  traffic_type         = "ALL"
  vpc_id               = aws_vpc.eks_vpc_1.id

  depends_on = [aws_s3_bucket.log_bucket_2]
}
# DynamoDB table creation
resource "aws_dynamodb_table" "dynamodb_terraform_state_lock_1" {
  name         = "dynamodb-terraform-state-lock-eks-1"
  billing_mode = var.dynamodb_terraform_state_lock_1_billing_mode
  hash_key     = "LockID"

  attribute {
    name = "LockID"
    type = "S"
  }

  server_side_encryption {
    enabled     = true
    kms_key_arn = aws_kms_key.kms_key_2.arn
  }

  point_in_time_recovery {
    enabled = true
  }

  tags = {
    Name = "dynamodb-terraform-state-lock-eks-1"
  }

  depends_on = [aws_kms_key.kms_key_2]
}
# Amazon EBS CSI driver installation
resource "helm_release" "aws_ebs_csi_driver" {
  name       = "aws-ebs-csi-driver"
  chart      = "aws-ebs-csi-driver"
  namespace  = "kube-system"
  repository = "https://kubernetes-sigs.github.io/aws-ebs-csi-driver"
  version    = var.helm_ebs_csi_driver_version
  timeout    = var.helm_timeout_seconds

  depends_on = [
    aws_eks_cluster.eks_cluster_1,
    aws_iam_role_policy_attachment.eks_node_1_role_AmazonEBSCSIDriverPolicy
  ]
}
output "bucket_1_name" {
  description = "The name of the S3 bucket that is being used to store the Terraform state file"
  value       = aws_s3_bucket.bucket_1.bucket
}

output "log_bucket_1_name" {
  description = "The name of the S3 bucket that is being used to store the Terraform state file"
  value       = aws_s3_bucket.log_bucket_1.bucket
}

output "eks_cluster_name" {
  description = "The name of the EKS cluster"
  value       = aws_eks_cluster.eks_cluster_1.name
}

#File: import.tf

#File: main.tf

provider "azurerm" {
  features {}
}


resource "azurerm_resource_group" "Azuredevops" {
   name =  "Azuredevops"
   location = "South Central US"
   tags = {
    environment = "Production"
    project= "project01"
  }
}


data "azurerm_platform_image" "ubunti1804LTS" {
  location  = azurerm_resource_group.Azuredevops.location
  publisher = "Canonical"
  offer     = "UbuntuServer"
  sku       = "18.04-LTS"
}

resource "azurerm_virtual_network" "Azuredevops" {
  name                = "${var.prefix}-network"
  address_space       = ["10.0.0.0/16"]
  location            = azurerm_resource_group.Azuredevops.location
  resource_group_name = azurerm_resource_group.Azuredevops.name
  tags = {
    environment = "Production"
    project= "project01"
  }
}


resource "azurerm_subnet" "internal" {
  name                 = "internal"
  resource_group_name  = azurerm_resource_group.Azuredevops.name
  virtual_network_name = azurerm_virtual_network.Azuredevops.name
  address_prefixes     = ["10.0.2.0/24"]
}

resource "azurerm_public_ip" "Azuredevops" {
  name                = "TestPublicIp1"
  resource_group_name = azurerm_resource_group.Azuredevops.name
  location            = azurerm_resource_group.Azuredevops.location
  allocation_method   = "Static"

  tags = {
    environment = "Production"
    project= "project01"  
  }
}

resource "azurerm_lb" "vm_lb" {
  name                = "${var.prefix}-lb"
  location            = azurerm_resource_group.Azuredevops.location
  resource_group_name = "${azurerm_resource_group.Azuredevops.name}"

  frontend_ip_configuration {
    name                 = "FrontAddressPool"
    public_ip_address_id = azurerm_public_ip.Azuredevops.id
  }
    tags = {
    environment = "Production"
    project= "project01"  
  }
}

resource "azurerm_lb_backend_address_pool" "bpepool" {
 loadbalancer_id     = azurerm_lb.vm_lb.id
 name                = "BackEndAddressPool"
}

resource "azurerm_network_security_group" "Azuredevops" {
  name                = "Azuredevops-NSG"
  location            = "${azurerm_resource_group.Azuredevops.location}"
  resource_group_name = "${azurerm_resource_group.Azuredevops.name}"  

  tags = {
    environment = "Production"
    project= "project01"  
  }
}
resource "azurerm_network_interface" "nic" {
  count               = var.vm_count
  name                 = "nic-${count.index}"
  resource_group_name = azurerm_resource_group.Azuredevops.name
  location            = azurerm_resource_group.Azuredevops.location


  ip_configuration {
    name                          = "nic-${count.index}"
    subnet_id                     = azurerm_subnet.internal.id
    private_ip_address_allocation = "Dynamic"

  }
  tags = {
    environment = "Production"
    project= "project01"
  }
}

resource "azurerm_network_interface_backend_address_pool_association" "nic_bapa" {
  count                   = var.vm_count  
  ip_configuration_name   = "nic-${count.index}"
  network_interface_id    = azurerm_network_interface.nic[count.index].id
  backend_address_pool_id = "${azurerm_lb_backend_address_pool.bpepool.id}"
}

resource "tls_private_key" "Azuredevops" {
  algorithm   = "RSA"
  rsa_bits    = 4096
}

resource "azurerm_availability_set" "abilityset" {
  name                = "${var.prefix}-abset"
  location            = azurerm_resource_group.Azuredevops.location
  resource_group_name = azurerm_resource_group.Azuredevops.name
  platform_fault_domain_count = 3
  platform_update_domain_count = 3  
  depends_on = [
    azurerm_resource_group.Azuredevops
  ]

  tags = {
    environment = "Production"
    project= "project01"
  }
}


resource "azurerm_linux_virtual_machine" "Azuredevops" {
  count                           = var.vm_count 
  name                            = "${var.prefix}-${count.index}-vm"
  resource_group_name             = azurerm_resource_group.Azuredevops.name
  location                        = azurerm_resource_group.Azuredevops.location
  size                            = "Standard_D2s_v3"
  admin_username                  = "${var.username}"
  admin_password                  = "${var.password}"
  availability_set_id             = azurerm_availability_set.abilityset.id 
  network_interface_ids           = [element(azurerm_network_interface.nic.*.id, count.index)]


  disable_password_authentication = false


admin_ssh_key {
    username   = "${var.username}"
    public_key = tls_private_key.Azuredevops.public_key_openssh
  }
