#File: variables.tf
variable "gcp_svc_key" {}
variable "gcp_project" {}
variable "gcp_region" {}

variable "lab_tag" {
    default = "Auto-Scaling-Lab"
}

variable "instance_ami" {
    default = "ami-02354e95b39ca8dec"
}
variable "instance_type" {
    default = "t2.micro"
}

variable "aws_access_key"{}

variable "aws_secret_key" {}

variable "aws_key_pair_pub"{}

variable "web_user_data" {
    default = <<EOT
        #!/bin/bash
        yum update -y
        yum install httpd -y
        service httpd start
        chkconfig httpd on
        cd /var/www/html
        echo "<html><h1>This is WebServer" `curl http://169.254.169.254/latest/meta-data/local-ipv4` "</h1></html>" > index.html
    EOT
}

############################################################3
provider "aws" {
  access_key = var.aws_access_key 
  secret_key = var.aws_secret_key 
  region     = "us-east-1"
}

# create the VPC
resource "aws_vpc" "vpc" {
  cidr_block           = "192.168.64.0/20"
  instance_tenancy     = "default"
  enable_dns_support   = true
  enable_dns_hostnames = true
} # end resource

# Subnets
resource "aws_subnet" "pub_us_east_1a" {
  vpc_id                  = aws_vpc.vpc.id
  cidr_block              = "192.168.64.0/24"
  map_public_ip_on_launch = true
  availability_zone       = "us-east-1a"
} 

resource "aws_subnet" "pub_us_east_1b" {
  vpc_id                  = aws_vpc.vpc.id
  cidr_block              = "192.168.65.0/24"
  map_public_ip_on_launch = true
  availability_zone       = "us-east-1b"
} 

resource "aws_subnet" "pub_us_east_1c" {
  vpc_id                  = aws_vpc.vpc.id
  cidr_block              = "192.168.66.0/24"
  map_public_ip_on_launch = true
  availability_zone       = "us-east-1c"
} 

# Create the Internet Gateway
resource "aws_internet_gateway" "ig" {
  vpc_id                  = aws_vpc.vpc.id
} # end resource

# Create the Route Table
resource "aws_route_table" "rt_main" {
  vpc_id                  = aws_vpc.vpc.id
  route {
     cidr_block = "0.0.0.0/0"
     gateway_id = aws_internet_gateway.ig.id
  }
} # end resource

# Associate the Route Table with the Subnet
resource "aws_route_table_association" "rt_ass_us_east_1a" {
  subnet_id      = aws_subnet.pub_us_east_1a.id
  route_table_id = aws_route_table.rt_main.id 
} # end resource
resource "aws_route_table_association" "rt_ass_us_east_1b" {
  subnet_id      = aws_subnet.pub_us_east_1b.id
  route_table_id = aws_route_table.rt_main.id 
} # end resource
resource "aws_route_table_association" "rt_ass_us_east_1c" {
  subnet_id      = aws_subnet.pub_us_east_1c.id
  route_table_id = aws_route_table.rt_main.id 
} # end resource

# Security Groups for instances
resource "aws_security_group" "web_server" {
    vpc_id                  = aws_vpc.vpc.id
    name = "SG Web Server"
    description = "SG Web Server"
    ingress {
        description = "Allow HTTP from anywere"
        from_port = 80
        to_port = 80
        protocol = "TCP"
        cidr_blocks = ["0.0.0.0/0"]
    }
    ingress {
        description = "Allow SSH from anywere"
        from_port = 22
        to_port = 22
        protocol = "TCP"
        cidr_blocks = ["0.0.0.0/0"]
    }
    egress {
        description = "Allow egress traffic"
        from_port = 0
        to_port = 0
        protocol = "-1"
        cidr_blocks = ["0.0.0.0/0"]
    }
}

# Import in AWS pub key
resource "aws_key_pair" "lab" {
    key_name   = "lab"
    public_key = var.aws_key_pair_pub
}




# Security Groups for instances
resource "aws_security_group" "elb_web" {
    vpc_id                  = aws_vpc.vpc.id
    name = "ELB Web Server"
    description = "ELB  Web Server"
    ingress {
        description = "Allow HTTP from anywere"
        from_port = 80
        to_port = 80
        protocol = "TCP"
        cidr_blocks = ["0.0.0.0/0"]
    }
    egress {
        description = "Allow egress traffic"
        from_port = 0
        to_port = 0
        protocol = "-1"
        cidr_blocks = ["0.0.0.0/0"]
    }
}

resource "aws_lb_target_group" "web" {
    name = "tg-web"
    target_type = "instance"
    protocol = "HTTP"
    port = "80"
    vpc_id = aws_vpc.vpc.id

    health_check {
        enabled=true
        interval = 5
        path = "/index.html"
        port = 80
        protocol = "HTTP"
        timeout = 4
        healthy_threshold = 2
        unhealthy_threshold = 3
        matcher = "200"
    }

}

resource "aws_lb" "web" {
    name = "WebFront"
    internal = false
    load_balancer_type = "application"
    ip_address_type = "ipv4"
   
    security_groups = [aws_security_group.elb_web.id]

    subnets = [ aws_subnet.pub_us_east_1a.id , aws_subnet.pub_us_east_1b.id  , aws_subnet.pub_us_east_1c.id  ]

}

resource "aws_lb_listener" "Web80" {
    load_balancer_arn = aws_lb.web.arn
    port = 80
    protocol = "HTTP"

    default_action {
        type = "forward"
        target_group_arn = aws_lb_target_group.web.arn
    }
}

resource "aws_launch_configuration" "as_conf" {
    name_prefix = "WebServers-"
    image_id = var.instance_ami
    instance_type = var.instance_type
    key_name = aws_key_pair.lab.id
    user_data = var.web_user_data
    security_groups = [ aws_security_group.web_server.id ]

    root_block_device {
        volume_size = 8
        delete_on_termination = true
    }
     
    lifecycle {
        create_before_destroy = true
    }
}

resource "aws_autoscaling_group" "as_grp" {
    name = "WebASG"    
    launch_configuration = aws_launch_configuration.as_conf.id

    max_size = 6
    min_size = 3

    target_group_arns = [aws_lb_target_group.web.arn]
    vpc_zone_identifier = [ aws_subnet.pub_us_east_1a.id , aws_subnet.pub_us_east_1b.id  , aws_subnet.pub_us_east_1c.id  ]

}

resource "aws_autoscaling_policy" "as_pol_cpu" {
    name = "ASG-policy-cpu"
    autoscaling_group_name = aws_autoscaling_group.as_grp.name 
    policy_type = "TargetTrackingScaling"
    estimated_instance_warmup = 120

    target_tracking_configuration {
            predefined_metric_specification {
                predefined_metric_type = "ASGAverageCPUUtilization"
            }
            target_value = 50
    }
}

#File: firewalls.tf
resource "google_compute_firewall" "only_internal_traffic" {
  name    = "only-internal-traffic"
  network = google_compute_network.gke_vpc.name

  allow {
    protocol = "all"
  }

  source_ranges = ["10.0.0.0/8"]
}

resource "google_compute_firewall" "master_webhooks" {
  name    = "gke-allow-master-webhooks"
  network = google_compute_network.gke_vpc.name

  allow {
    protocol = "tcp"
    ports = ["443", "8443"]
  }

  source_ranges = ["172.16.0.0/28"]
}

#File: gke_masters.tf
resource "google_container_cluster" "gke_masters" {
  name                     = "devops-demo-gke"
  location                 = "${var.region}"
  min_master_version       = "1.24"
  remove_default_node_pool = true
  initial_node_count       = 1
  network                  = google_compute_network.gke_vpc.self_link
  subnetwork               = google_compute_subnetwork.gke_private.self_link
  logging_service          = "logging.googleapis.com/kubernetes"
  monitoring_service       = "monitoring.googleapis.com/kubernetes"
  networking_mode          = "VPC_NATIVE"

  node_locations = [
    "${var.region}-a",
    "${var.region}-b"
  ]

  addons_config {
    http_load_balancing {
      disabled = false
    }

    horizontal_pod_autoscaling {
      disabled = false
    }

    network_policy_config {
      disabled = false
    }
  }

  network_policy {
    provider = "CALICO"
    enabled = true
  }

