#File: bastion.tf
data "google_compute_default_service_account" "default" {
}

# This is used to set local variable google_zone.
data "google_compute_zones" "available" {
  region = var.region
}

resource "random_shuffle" "az" {
  input        = data.google_compute_zones.available.names
  result_count = 1
}

locals {
  google_zone = random_shuffle.az.result[0]
}

# Subnet
resource "google_compute_subnetwork" "bastion_subnet" {
  name          = "${var.db_name}-bastion-subnet"
  region        = var.region
  network       = google_compute_network.db_vpc.name
  ip_cidr_range = "10.128.0.0/20"
}

resource "google_compute_firewall" "bastion_ssh" {
  name    = "bastion-allow-ssh"
  network = google_compute_network.db_vpc.name

  allow {
    protocol = "tcp"
  }

  allow {
    protocol = "tcp"
    ports    = ["22"]
  }

  source_ranges = ["0.0.0.0/0"]
  target_tags   = ["bastion"]
}

resource "google_compute_instance" "sql_client_vm" {
  name         = "sql-client"
  machine_type = "e2-medium"
  zone         = local.google_zone

  tags = ["sql-client", "bastion"]

  boot_disk {
    initialize_params {
      # gcloud compute images list
      image = "debian-cloud/debian-11"
    }
  }

  network_interface {
    network    = google_compute_network.db_vpc.id
    subnetwork = google_compute_subnetwork.bastion_subnet.id

    access_config {
      // Ephemeral public IP
    }
  }


  metadata_startup_script = <<EOF
#! /bin/bash
apt-get update
apt-get install -y  postgresql-client
apt-get install -y wget
wget -q https://dl.google.com/cloudsql/cloud_sql_proxy.linux.amd64 -O /bin/cloud_sql_proxy
chmod +x /bin/cloud_sql_proxy
EOF

  service_account {
    # Google recommends custom service accounts that have cloud-platform scope and permissions granted via IAM Roles.
    email  = data.google_compute_default_service_account.default.email
    scopes = ["cloud-platform"]
  }
}
#File: db.tf
resource "google_sql_database" "db" {
  name     = var.db_name
  instance = google_sql_database_instance.db_instance.name
}

resource "random_id" "db_name_suffix" {
  byte_length = 4
}

resource "google_sql_database_instance" "db_instance" {
  name             = "${var.db_name}-${random_id.db_name_suffix.hex}"
  region           = var.region
  database_version = var.db_version

  settings {
    tier = var.db_tier

    ip_configuration {
      ipv4_enabled    = false
      private_network = google_compute_network.db_vpc.id
    }

    database_flags {
      name  = "cloudsql.iam_authentication"
      value = "on"
    }
  }

  depends_on = [
    google_service_networking_connection.db_vpc_private_connection
  ]

  # delete the instance via terraform
  deletion_protection = false
}

resource "google_service_account" "db_admin_service_account" {
  account_id   = "dbadmin-${random_id.db_name_suffix.hex}"
  display_name = "dbadmin-${random_id.db_name_suffix.hex}"
