#File: variables.tf
variable "project" {
  default = null
  type    = string
}

variable "region" {
  description = "Region to deploy VPN gateway in"
  type        = string
}

variable "vpn_gateway_name" {
  description = "VPN gateway name. Gateway created by the module"
  type        = string
}

variable "router_name" {
  description = "Cloud router name. The router is created by the module"
  type        = string
  default     = null
}

variable "network" {
  description = "VPC network ID that should be used for deployment"
  type        = string
}

variable "labels" {
  description = "Labels for VPN components"
  type        = map(string)
  default     = {}
}

variable "vpn_config" {
  type        = any
  description = <<-EOF
  VPN configuration from GCP to on-prem or from GCP to GCP.
  If you'd like secrets to be randomly generated set `shared_secret` to empty string ("").

  Example:

  ```
  vpn_config = {
    router_asn    = 65000
    local_network = "vpc-vpn"

    router_advertise_config = {
      ip_ranges = {
        "10.10.0.0/16" : "GCP range 1"
      }
      mode   = "CUSTOM"
      groups = null
    }

    instances = {
      vpn-to-onprem = {
        name = "vpn-to-onprem",
        peer_external_gateway = {
          redundancy_type = "TWO_IPS_REDUNDANCY"
          interfaces = [{
            id         = 0
            ip_address = "1.1.1.1"
            }, {
            id         = 1
            ip_address = "2.2.2.2"
          }]
        },
        tunnels = {
          remote0 = {
            bgp_peer = {
              address = "169.254.1.2"
              asn     = 65001
            }
            bgp_peer_options                = null
            bgp_session_range               = "169.254.1.1/30"
            ike_version                     = 2
            vpn_gateway_interface           = 0
            peer_external_gateway_interface = 0
            shared_secret                   = "secret"
          }
          remote1 = {
            bgp_peer = {
              address = "169.254.1.6"
              asn     = 65001
            }
            bgp_peer_options                = null
            bgp_session_range               = "169.254.1.5/30"
            ike_version                     = 2
            vpn_gateway_interface           = 1
            peer_external_gateway_interface = 1
            shared_secret                   = "secret"
          }
        }
      }
    }
  }
  ```
  EOF
}
#File: versions.tf
terraform {
  required_version = ">= 1.3, < 2.0"
  required_providers {
    google = {
      version = ">= 4.58"
    }
  }
}

#File: Main.tf
# Variables
variable "resource_group_name" {
  description = "The name of the resource group"
  default     = "Test-RG"
}

variable "location" {
  description = "The location of the resources"
  default     = "East US"
}

variable "vm_size" {
  description = "The size of the virtual machine"
  default     = "Standard_B2ms"
}

variable "admin_username" {
  description = "The admin username for the virtual machine"
  default     = "azureadmin"
}

variable "admin_password" {
  description = "The admin password for the virtual machine"
  default     = "Tf@$$w0rd1234!"
  sensitive   = true
}

# Create a Azure Resource Group
resource "azurerm_resource_group" "RG" {
  name     = var.resource_group_name
  location = var.location

  tags = {
    environment = "Test"
  }
}

# Create a Azure Virtual Network
resource "azurerm_virtual_network" "VNET" {
  name                = "Test-VNET"
  address_space       = ["10.0.0.0/16"]
  location            = azurerm_resource_group.RG.location
  resource_group_name = azurerm_resource_group.RG.name

  tags = {
    environment = "Test"
  }
}

# Create a Subnet 
resource "azurerm_subnet" "AzureSubnet" {
  name                 = "AzureSubnet"
  resource_group_name  = azurerm_resource_group.RG.name
  virtual_network_name = azurerm_virtual_network.VNET.name
  address_prefixes     = ["10.0.1.0/24"]
}

# Create a NIC 
resource "azurerm_network_interface" "nic" {
  name                = "Test-nic"
  location            = azurerm_resource_group.RG.location
  resource_group_name = azurerm_resource_group.RG.name

  ip_configuration {
    name                          = "internal"
    subnet_id                     = azurerm_subnet.AzureSubnet.id
    private_ip_address_allocation = "Dynamic"
  }

  tags = {
    environment = "Test"
  }
}

# Create a Public IP
resource "azurerm_public_ip" "PIP" {
  name                = "Public-IP"
  resource_group_name = azurerm_resource_group.RG.name
  location            = azurerm_resource_group.RG.location
  allocation_method   = "Static"

  tags = {
    environment = "Test"
  }
}

# Create a Network Security Group 
resource "azurerm_network_security_group" "NSG" {
  name                = "Test-NSG"
  location            = azurerm_resource_group.RG.location
  resource_group_name = azurerm_resource_group.RG.name

  tags = {
    environment = "Test"
  }
}

# Create a Azure Virtual Machine
resource "azurerm_windows_virtual_machine" "VM" {
  name                = "Test-VM"
  resource_group_name = azurerm_resource_group.RG.name
  location            = azurerm_resource_group.RG.location
  size                = var.vm_size
  admin_username      = var.admin_username
  admin_password      = var.admin_password
  network_interface_ids = [
    azurerm_network_interface.nic.id,
  ]

  os_disk {
    caching              = "ReadWrite"
    storage_account_type = "Standard_LRS"
  }

  source_image_reference {
    publisher = "MicrosoftWindowsServer"
    offer     = "WindowsServer"
    sku       = "2022-Datacenter"
    version   = "latest"
  }

  tags = {
    environment = "Test"
  }
}

# Outputs
output "resource_group_name" {
  value = azurerm_resource_group.RG.name
}

output "virtual_network_name" {
  value = azurerm_virtual_network.VNET.name
}

output "subnet_name" {
  value = azurerm_subnet.AzureSubnet.name
}

output "public_ip" {
  value = azurerm_public_ip.PIP.ip_address
}

output "virtual_machine_id" {
  value = azurerm_windows_virtual_machine.VM.id
}
