#File: main.tf
################################################################################
# Create NSG and Rules for worload VMs
################################################################################
resource "azurerm_network_security_group" "workload_nsg" {
  count               = var.workload_count
  name                = "${var.name_prefix}-workload-${count.index + 1}-nsg-${var.resource_tag}"
  location            = var.location
  resource_group_name = var.resource_group

  security_rule {
    name                       = "SSH_VNET"
    priority                   = 4000
    direction                  = "Inbound"
    access                     = "Allow"
    protocol                   = "Tcp"
    source_port_range          = "*"
    destination_port_range     = "22"
    source_address_prefix      = "VirtualNetwork"
    destination_address_prefix = "*"
  }

  security_rule {
    name                       = "ICMP_VNET"
    priority                   = 4001
    direction                  = "Inbound"
    access                     = "Allow"
    protocol                   = "Icmp"
    source_port_range          = "*"
    destination_port_range     = "*"
    source_address_prefix      = "VirtualNetwork"
    destination_address_prefix = "*"
  }

  security_rule {
    name                       = "OUTBOUND"
    priority                   = 4000
    direction                  = "Outbound"
    access                     = "Allow"
    protocol                   = "*"
    source_port_range          = "*"
    destination_port_range     = "*"
    source_address_prefix      = "*"
    destination_address_prefix = "*"
  }

  tags = var.global_tags
}


################################################################################
# Create Network Interface and association NSG
################################################################################
resource "azurerm_network_interface" "workload_nic" {
  count               = var.workload_count
  name                = "${var.name_prefix}-workload-${count.index + 1}-nic-${var.resource_tag}"
  location            = var.location
  resource_group_name = var.resource_group

  ip_configuration {
    name                          = "${var.name_prefix}-workload-${count.index + 1}-nic-conf-${var.resource_tag}"
    subnet_id                     = var.subnet_id
    private_ip_address_allocation = "Dynamic"
  }

  dns_servers = var.dns_servers

  tags = var.global_tags
}

resource "azurerm_network_interface_security_group_association" "workload_nic_association" {
  count                     = var.workload_count
  network_interface_id      = azurerm_network_interface.workload_nic[count.index].id
  network_security_group_id = azurerm_network_security_group.workload_nsg[count.index].id
}


################################################################################
# Create Workload VMs
################################################################################
resource "azurerm_linux_virtual_machine" "workload_vm" {
  count               = var.workload_count
  name                = "${var.name_prefix}-workload-vm-${count.index + 1}-${var.resource_tag}"
  location            = var.location
  resource_group_name = var.resource_group

  network_interface_ids = [azurerm_network_interface.workload_nic[count.index].id]
  size                  = local.instance_size_selection
  admin_username        = var.server_admin_username
  computer_name         = "${var.name_prefix}-workload-${count.index + 1}-${var.resource_tag}"
  admin_ssh_key {
    username   = var.server_admin_username
    public_key = "${trimspace(var.ssh_key)} ${var.server_admin_username}@me.io"
  }

  os_disk {
    caching              = "ReadWrite"
    storage_account_type = contains(local.unsupported_regions, lower(var.location)) ? "Standard_LRS" : "Premium_LRS"
  }

  source_image_reference {
    publisher = var.instance_image_publisher
    offer     = var.instance_image_offer
    sku       = var.instance_image_sku
    version   = var.instance_image_version
  }

  tags = var.global_tags

  depends_on = [
    azurerm_network_interface.workload_nic,
    azurerm_network_interface_security_group_association.workload_nic_association
  ]
}

#File: outputs.tf
output "private_ip" {
  description = "Instance Private IP Address"
  value       = azurerm_network_interface.workload_nic[*].private_ip_address
}
output "admin_username" {
  description = "Instance Admin Username"
  value       = azurerm_linux_virtual_machine.workload_vm[0].admin_username
}

#File: variables.tf
variable "name_prefix" {
  type        = string
  description = "A prefix to associate to all the workload module resources"
  default     = null
}

variable "resource_tag" {
  type        = string
  description = "A tag to associate to all the workload module resources"
  default     = null
}

variable "global_tags" {
  type        = map(string)
  description = "Populate any custom user defined tags from a map"
  default     = {}
}

variable "resource_group" {
  type        = string
  description = "Main Resource Group Name"
}

variable "location" {
  type        = string
  description = "Cloud Connector Azure Region"
}

variable "subnet_id" {
  type        = string
  description = "The id of subnet where the workload host has to be attached"
}

variable "server_admin_username" {
  type        = string
  description = "Username configured for the workload root/admin account"
  default     = "almalinux"
}

variable "ssh_key" {
  type        = string
  description = "SSH Key for instances"
}

variable "instance_size" {
  type        = string
  description = "The Azure image type/size"
  default     = "Standard_B1s"
}

locals {
  unsupported_regions          = ["chinanorth", "chinaeast", "china east", "china north", "usgovirginia"]
  custom_size_standard_a3      = contains(["chinanorth", "chinaeast", "china east", "china north"], var.location) ? "Standard_A3" : ""
  custom_size_standard_b2as_v2 = contains(["usgovvirginia"], var.location) ? "Standard_DS1_v2" : ""
  instance_size_selection      = coalesce(local.custom_size_standard_a3, local.custom_size_standard_b2as_v2, var.instance_size)
}

variable "instance_image_publisher" {
  type        = string
  description = "The workload image publisher"
  default     = "almalinux"
}

variable "instance_image_offer" {
  type        = string
  description = "The workload image offer"
  default     = "almalinux-x86_64"
}

variable "instance_image_sku" {
  type        = string
  description = "The workload image sku"
  default     = "9-gen1"
}

variable "instance_image_version" {
  type        = string
  description = "The workload image version"
  default     = "latest"
}

variable "workload_count" {
  type        = number
  description = "The number of Workload VMs to deploy"
  default     = 1
  validation {
    condition     = var.workload_count >= 1 && var.workload_count <= 250
    error_message = "Input workload_count must be a whole number between 1 and 250."
  }
}

# If no values specified, this defaults to Azure DNS 
variable "dns_servers" {
  type        = list(string)
  description = "The DNS servers configured for workload VMs."
  default     = []
}

