    function_name = var.producer_lambda_function_name

    package_type = "Image"
    image_uri = module.producer_docker_image.image_uri
    role = aws_iam_role.lambda_exec.arn
    timeout = 120

    environment {
        variables = {
            "QUEUE_NAME" = aws_sqs_queue.queue.name,
            "S3_BUCKET_NAME" = aws_s3_bucket.lambda_bucket.id,
        }
    }
}

resource "aws_cloudwatch_log_group" "producer_lambda_log_group" {
    name = "/aws/lambda/${aws_lambda_function.producer_lambda.function_name}"

    retention_in_days = 30
}

resource "aws_apigatewayv2_api" "lambda" {
    name = var.producer_api_gateway_name
    protocol_type = "HTTP"
}

resource "aws_apigatewayv2_stage" "lambda" {
    api_id = aws_apigatewayv2_api.lambda.id

    name = var.producer_apigateway_stage_name
    auto_deploy = true

    access_log_settings {
        destination_arn = aws_cloudwatch_log_group.api_gw.arn

        format = jsonencode({
            requestId = "$context.requestId",
            sourceIp = "$context.identity.sourceIp",
            requestTime = "$context.requestTime",
            protocol = "$context.protocol",
            httpMethod = "$context.httpMethod",
            resourcePath = "$context.resourcePath",
            routeKey = "$context.routeKey",
            status = "$context.status",
            responseLength = "$context.responseLength",
            integrationErrorMessage = "$context.integrationErrorMessage",
        })
    }
}

resource "aws_apigatewayv2_integration" "queue" {
    api_id = aws_apigatewayv2_api.lambda.id

    integration_uri = aws_lambda_function.producer_lambda.invoke_arn
    integration_type = "AWS_PROXY"
    integration_method = "POST"
}

resource "aws_apigatewayv2_route" "queue" {
    api_id = aws_apigatewayv2_api.lambda.id

    route_key = var.producer_invocation_route_key
    target = "integrations/${aws_apigatewayv2_integration.queue.id}"
}

resource "aws_cloudwatch_log_group" "api_gw" {
    name = "/aws/api_gw/${aws_apigatewayv2_api.lambda.name}"

    retention_in_days = 30
}

resource "aws_lambda_permission" "api_gw" {
    statement_id = "AllowExecutionFromAPIGateway"
    action = "lambda:InvokeFunction"
    function_name = aws_lambda_function.producer_lambda.function_name
    principal = "apigateway.amazonaws.com"

    source_arn = "${aws_apigatewayv2_api.lambda.execution_arn}/*/*"
}
resource "aws_s3_bucket" "lambda_bucket" {
    bucket = var.lambda_bucket
    acl = "private"
    force_destroy = true
}

resource "aws_iam_role" "lambda_exec" {
    name = "lambda_role"

    assume_role_policy = jsonencode({
        Version = "2012-10-17",
        Statement = [{
            Action = "sts:AssumeRole",
            Effect = "Allow",
            Sid = "",
            Principal = {
                Service = "lambda.amazonaws.com"
            }
        }]
    })
}

data "aws_iam_policy_document" "lambda_policy_document" {
    statement {
        sid = "LambdaPolicyQueueAccess"
        actions = [
            "sqs:ReceiveMessage",
            "sqs:DeleteMessage",
            "sqs:GetQueueAttributes",
            "sqs:SendMessage",
            "sqs:GetQueueUrl"
        ]
        resources = [
            aws_sqs_queue.queue.arn
        ]
    }

    statement {
        sid = "LambdaPolicyS3Access"
        actions = [
            "s3:GetObject",
            "s3:PutObject",
            "s3:DeleteObject",
            "s3:ListBucket",
            "s3:GetBucketLocation",
            "s3:GetBucketPolicy",
            "s3:GetBucketTagging",
            "s3:GetBucketVersioning",
            "s3:GetBucketWebsite",
            "s3:GetLifecycleConfiguration",
        ]
        resources = [
            aws_s3_bucket.lambda_bucket.arn,
            format("%s%s", aws_s3_bucket.lambda_bucket.arn, "/*")
        ]
    }
}

resource "aws_iam_policy" "lambda_policy" {
    name = "lambda_policy"
    policy = data.aws_iam_policy_document.lambda_policy_document.json
}

resource "aws_iam_role_policy_attachment" "lambda_sqs_policy" {
    role = aws_iam_role.lambda_exec.name
    policy_arn = aws_iam_policy.lambda_policy.arn
}

resource "aws_iam_role_policy_attachment" "lambda_basic_policy" {
    role = aws_iam_role.lambda_exec.name
    policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

resource "aws_sqs_queue" "queue" {
    name = var.queue_name

    visibility_timeout_seconds = var.consumer_timeout_seconds
}
module "consumer_docker_image" {
    source = "terraform-aws-modules/lambda/aws//modules/docker-build"

    create_ecr_repo = true
    ecr_repo = var.consumer_ecr_repo
    source_path = abspath(var.consumer_lambda_source_path)
}

resource "aws_lambda_function" "consumer_lambda" {
    function_name = var.consumer_lambda_function_name

    package_type = "Image"
    image_uri = module.consumer_docker_image.image_uri
    role = aws_iam_role.lambda_exec.arn
    timeout = var.consumer_timeout_seconds
    memory_size = 3009

    environment {
        variables = {
            "QUEUE_NAME" = aws_sqs_queue.queue.name,
            "S3_BUCKET_NAME" = aws_s3_bucket.lambda_bucket.id,
        }
    }
}

resource "aws_lambda_event_source_mapping" "event_source_mapping" {
    event_source_arn = aws_sqs_queue.queue.arn
    enabled = true
    function_name = aws_lambda_function.consumer_lambda.arn
    batch_size = 1
}


resource "aws_cloudwatch_log_group" "consumer_lambda_log_group" {
    name = "/aws/lambda/${aws_lambda_function.consumer_lambda.function_name}"

    retention_in_days = 30
}

#File: main.tf
# author: Rudy Corradetti
terraform {
  required_version = ">=0.12"
  required_providers {
    azurerm = {
      source  = "hashicorp/azurerm"
      version = "~>3.0"
    }
    tls = {
      source  = "hashicorp/tls"
      version = "~>4.0"
    }
    http = {
      source  = "hashicorp/http"
      version = "~>3.0"
    }
    random = {
      source  = "hashicorp/random"
      version = "~>3.0"
    }
  }
}

provider "azurerm" {
  features {}
  subscription_id = var.sub
}

# Data source to fetch latest release information
data "http" "latest_release" {
  url = "https://api.github.com/repos/jagrosh/MusicBot/releases/latest"
  request_headers = {
    Accept = "application/vnd.github.v3+json"
  }
}

locals {
  latest_version = jsondecode(data.http.latest_release.response_body).tag_name
  jar_filename   = "JMusicBot-${local.latest_version}.jar"
  download_url   = "https://github.com/jagrosh/MusicBot/releases/download/${local.latest_version}/${local.jar_filename}"
}

# Create a random string for the storage account name
resource "random_string" "sa_suffix" {
  length  = 5
  special = false
  upper   = false
  numeric = true
  lower   = true
}

# Resource group
resource "azurerm_resource_group" "rg1" {
  name     = var.rg
  location = var.rg_loc
}

# Virtual network
resource "azurerm_virtual_network" "vnet1" {
  name                = var.net
  address_space       = ["10.0.0.0/23"]
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name
}

# Subnet
resource "azurerm_subnet" "subnet1" {
  name                 = var.subnet
  resource_group_name  = azurerm_resource_group.rg1.name
  virtual_network_name = azurerm_virtual_network.vnet1.name
  address_prefixes     = ["10.0.1.0/24"]
}

# Public IP
resource "azurerm_public_ip" "public_ip" {
  name                = var.pub_ip
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name
  allocation_method   = var.pub_allocation_method
}

# Network security group
resource "azurerm_network_security_group" "nsg" {
  name                = var.nsg
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name

  security_rule {
    name                       = "ssh"
    priority                   = 1001
    direction                  = "Inbound"
    access                     = "Allow"
    protocol                   = "Tcp"
    source_port_range          = "*"
    destination_port_range     = "22"
    source_address_prefix      = "*"
    destination_address_prefix = "*"
  }
}

# Network interface
resource "azurerm_network_interface" "nic" {
  name                = var.nic_name
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name

  ip_configuration {
    name                          = var.nic_priv_ip_name
    subnet_id                     = azurerm_subnet.subnet1.id
    private_ip_address_allocation = var.priv_allocation_method
    public_ip_address_id          = azurerm_public_ip.public_ip.id
  }
}

# Connect the security group to the network interface
resource "azurerm_network_interface_security_group_association" "nsg_nic_assoc" {
  network_interface_id      = azurerm_network_interface.nic.id
  network_security_group_id = azurerm_network_security_group.nsg.id
}

# SSH key
resource "tls_private_key" "linux_test_ssh" {
  algorithm = "RSA"
  rsa_bits  = 4096
}

# Virtual machine
resource "azurerm_linux_virtual_machine" "vm1" {
  name                            = var.vm_name
  location                        = azurerm_resource_group.rg1.location
  resource_group_name             = azurerm_resource_group.rg1.name
  network_interface_ids           = [azurerm_network_interface.nic.id]
  size                            = var.vm_size
  admin_username                  = var.vm_admin_username
  disable_password_authentication = true

  admin_ssh_key {
    username   = var.vm_admin_username
    public_key = tls_private_key.linux_test_ssh.public_key_openssh
  }

  os_disk {
    name                 = var.os_disk_name
    caching              = "ReadWrite"
    storage_account_type = "Standard_LRS"
  }

  source_image_reference {
    publisher = var.vm_image_publisher
    offer     = var.vm_image_offer
    sku       = var.vm_image_sku
    version   = var.vm_image_version
  }

  identity {
    type = "SystemAssigned"
  }

  lifecycle {
    ignore_changes = [boot_diagnostics]
  }
}

# VM extension to set up JMusicBot
resource "azurerm_virtual_machine_extension" "setup_jmusicbot" {
  name                 = "setup_jmusicbot"
  virtual_machine_id   = azurerm_linux_virtual_machine.vm1.id
  publisher            = "Microsoft.Azure.Extensions"
  type                 = "CustomScript"
  type_handler_version = "2.1"

  settings = jsonencode({
    "script" : base64encode(<<-EOT
#!/bin/bash
set -e
echo "Starting JMusicBot setup..."

# Install Java and curl
sudo apt-get update
sudo apt-get install -y default-jre curl

# Create directory for JMusicBot
sudo mkdir -p /opt/jmusicbot
cd /opt/jmusicbot

# Define JMusicBot version and download URL
JMUSICBOT_VERSION="0.4.3"
JAR_FILENAME="JMusicBot-$${JMUSICBOT_VERSION}.jar"
DOWNLOAD_URL="https://github.com/jagrosh/MusicBot/releases/download/$${JMUSICBOT_VERSION}/$${JAR_FILENAME}"

# Download JAR file directly from GitHub
curl -L -o "$${JAR_FILENAME}" "$${DOWNLOAD_URL}"

# Create config file
cat << EOF > config.txt
token = ${var.discord_bot_token}
owner = ${var.discord_bot_owner}
prefix = "${var.discord_bot_prefix}"
EOF

# Create systemd service file
cat << EOF | sudo tee /etc/systemd/system/jmusicbot.service
[Unit]
Description=JMusicBot Service
After=network.target

[Service]
ExecStart=/usr/bin/java -Dnogui=true -jar /opt/jmusicbot/$${JAR_FILENAME}
WorkingDirectory=/opt/jmusicbot
User=nobody
Group=nogroup
Restart=always

[Install]
WantedBy=multi-user.target
EOF

# Set proper permissions
sudo chown -R nobody:nogroup /opt/jmusicbot
sudo chmod 644 /opt/jmusicbot/$${JAR_FILENAME}
sudo chmod 644 /opt/jmusicbot/config.txt

# Reload systemd, enable and start the service
sudo systemctl daemon-reload
sudo systemctl enable jmusicbot.service
sudo systemctl start jmusicbot.service

echo "JMusicBot setup completed."
EOT
    )
  })

  depends_on = [
    azurerm_linux_virtual_machine.vm1
  ]
}
# Storage account for function app
resource "azurerm_storage_account" "functionapp_sa" {
  name                     = "jdiscord${random_string.sa_suffix.result}"
  resource_group_name      = azurerm_resource_group.rg1.name
  location                 = azurerm_resource_group.rg1.location
  account_tier             = "Standard"
  account_replication_type = "LRS"
}

resource "azurerm_storage_container" "functionapp_container" {
  name                  = "jdiscord-code"
  storage_account_name  = azurerm_storage_account.functionapp_sa.name
  container_access_type = "private"
}

# Linux app service plan
resource "azurerm_service_plan" "functionapp_plan" {
  name                = "jdiscord-app-service-plan"
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name
  os_type             = "Linux"
  sku_name            = "Y1"
}

# Application insights for monitoring
resource "azurerm_application_insights" "app_insights" {
  name                = "jdiscord-appinsights"
  location            = azurerm_resource_group.rg1.location
  resource_group_name = azurerm_resource_group.rg1.name
  application_type    = "web"
}

# Linux function app
resource "azurerm_linux_function_app" "jdiscord_function" {
  name                       = "jdiscord-function"
  location                   = azurerm_resource_group.rg1.location
  resource_group_name        = azurerm_resource_group.rg1.name
  service_plan_id            = azurerm_service_plan.functionapp_plan.id
  storage_account_name       = azurerm_storage_account.functionapp_sa.name
  storage_account_access_key = azurerm_storage_account.functionapp_sa.primary_access_key

  app_settings = {
    "FUNCTIONS_WORKER_RUNTIME"       = "node"
    "GENERAL_CHANNEL_ID"             = var.general_channel_id
    "AFK_CHANNEL_ID"                 = var.afk_channel_id
    "MUSIC_CHANNEL_ID"               = var.music_channel_id
    "DISCORD_BOT_TOKEN"              = var.discord_bot_token
    "AZURE_TENANT_ID"                = var.azure_tenant_id
    "AZURE_CLIENT_ID"                = var.azure_client_id
    "AZURE_CLIENT_SECRET"            = var.azure_client_secret
    "SUBSCRIPTION_ID"                = var.sub
    "RESOURCE_GROUP_NAME"            = azurerm_resource_group.rg1.name
    "VM_NAME"                        = var.vm_name
    "APPINSIGHTS_INSTRUMENTATIONKEY" = azurerm_application_insights.app_insights.instrumentation_key
  }

  site_config {
    application_stack {
      node_version = "18"
    }
  }

  lifecycle {
    ignore_changes = [
      app_settings["APPINSIGHTS_INSTRUMENTATIONKEY"],
      site_config[0].application_insights_key,
    ]
  }
}
