#File: variables.tf
variable "aws_region" {
  type    = string
  default = "us-east-1"
}

variable "azure_location" {
  type    = string
  default = "eastus"
}

variable "workload" {
  type    = string
  default = "pluralsight"
}

variable "cloudtrail_bucket" {
  type = object({
    name   = string
    arn    = string
  })
  description = "Bucket containing the Cloudtrail logs that you want to process."
}

variable "excluded_accounts" {
  type        = list(string)
  description = "List of accounts that be excluded for scans on manual actions."
  default     = []
}


variable "included_accounts" {
  type        = list(string)
  description = "List of accounts that be scanned to manual actions."
  default     = []
}

variable event_processing_timeout {
  type = number
  description = "Maximum number of seconds the lambda is allowed to run and number of seconds events should be hidden in SQS after being picked up my Lambda."
  default = 60
}

variable log_retention_in_days {
  type = number
  description = "Number of days to keep CloudWatch logs"
  default = 30
}
resource "aws_sqs_queue" "bucket_notifications" {
  name = local.naming_prefix
  visibility_timeout_seconds = var.event_processing_timeout + 5
}

resource "aws_sqs_queue_policy" "bucket_notifications" {
  queue_url = aws_sqs_queue.bucket_notifications.id

  policy = <<POLICY
{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Effect": "Allow",
      "Principal": "*",
      "Action": "sqs:SendMessage",
      "Resource": "${aws_sqs_queue.bucket_notifications.arn}",
      "Condition": {
        "ArnEquals": { "aws:SourceArn": "${var.cloudtrail_bucket.arn}" }
      }
    }
  ]
}
POLICY
}

resource "aws_s3_bucket_notification" "bucket_notification" {
  bucket = var.cloudtrail_bucket.name

  queue {
    queue_arn = aws_sqs_queue.bucket_notifications.arn
    events    = ["s3:ObjectCreated:*"]
    filter_suffix = ".json.gz"
  }

  depends_on = [
    aws_sqs_queue_policy.bucket_notifications
  ]
}
terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "3.49.0"
    }
  }
}

provider "aws" {
  alias  = "reference"
  region = var.region
}

provider "aws" {
  region = var.region

  default_tags {
    tags = local.mandatory_tags
  }
}

resource "aws_cloudwatch_log_group" "func" {
  name = "/aws/lambda/${aws_lambda_function.func.function_name}"
    retention_in_days = var.log_retention_in_days
}
resource "aws_lambda_function" "func" {

  function_name = local.naming_prefix
  role          = aws_iam_role.lambda.arn

  handler = "main.handler"
  runtime = "python3.8"

  filename         = data.archive_file.func.output_path
  source_code_hash = filebase64sha256(data.archive_file.func.output_path)

  timeout     = var.event_processing_timeout
  memory_size = 128

  layers = [local.python_layers[var.region]]

  environment {
    variables = {
      WEBHOOK_PARAMETER = aws_ssm_parameter.slack_webhook.name
      EXCLUDED_ACCOUNTS = jsonencode(var.excluded_accounts)
      INCLUDED_ACCOUNTS = jsonencode(var.included_accounts)
    }
  }
}

data "archive_file" "func" {
  type             = "zip"
  source_dir       = "${path.module}/lambda"
  output_file_mode = "0666"
  output_path      = "${path.module}/lambda.zip"
}

resource "aws_lambda_event_source_mapping" "bucket_notifications" {
  event_source_arn = aws_sqs_queue.bucket_notifications.arn
  function_name    = aws_lambda_function.func.arn
}

resource "aws_ssm_parameter" "slack_webhook" {

  name        = "/${local.naming_prefix}/slack-webhook"
  description = "Slack Incomming Webhook. https://api.slack.com/messaging/webhooks"

  type  = "SecureString"
  value = "REPLACE_ME"

  lifecycle {
    ignore_changes = [
      value,
    ]
  }
}
resource "aws_iam_role" "lambda" {
  name = local.naming_prefix

  assume_role_policy = data.aws_iam_policy_document.lambda_role_trust.json
}

data "aws_iam_policy_document" "lambda_role_trust" {
  statement {
    sid = "LambdaTrust"

    actions = [
      "sts:AssumeRole"
    ]

    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role_policy" "lambda_permissions" {
  name   = local.naming_prefix
  role   = aws_iam_role.lambda.id
  policy = data.aws_iam_policy_document.lambda_permissions.json
}


data "aws_iam_policy_document" "lambda_permissions" {
  statement {
    sid = "Logging"

    actions = [
      "logs:CreateLogGroup",
      "logs:CreateLogStream",
      "logs:PutLogEvents"
    ]

    resources = [
      "arn:aws:logs:*:*:*"
    ]
  }

  statement {
    sid = "S3Access"

    actions = [
      "s3:Get*",
      "s3:List*",
      "s3:Describe*",
    ]

    resources = [
      "${var.cloudtrail_bucket.arn}/",
      "${var.cloudtrail_bucket.arn}/*",
    ]
  }

  statement {
    sid = "SSMAccess"

    actions = [
      "ssm:Get*",
    ]

    resources = [
      aws_ssm_parameter.slack_webhook.arn
    ]
  }

  statement {
    sid = "SQSAccess"

    actions = [
      "sqs:*",
    ]

    resources = [
      aws_sqs_queue.bucket_notifications.arn
    ]
  }

}

variable "region" {
  type        = string
  description = "The default region for the application / deployment"

  validation {
    condition = contains([
      "eu-central-1",
      "eu-west-1",
      "eu-west-2",
      "eu-south-1",
      "eu-west-3",
      "eu-north-1",
      "af-south-1"
    ], var.region)
    error_message = "Invalid region provided."
  }
}

variable "environment" {
  type        = string
  description = "Will this deploy a development (dev) or production (prod) environment"

  validation {
    condition     = contains(["dev", "prd"], var.environment)
    error_message = "Stage must be either 'dev' or 'prd'."
  }
}

variable "code_repo" {
  type        = string
  description = "Points to the source code used to deploy the resources {{repo}} [{{branch}}]"
}

variable "namespace" {
  type        = string
  description = "Used to identify which part of the application these resources belong to (auth, infra, api, web, data)"

  validation {
    condition     = contains(["sec", "auth", "infra", "api", "web", "data"], var.namespace)
    error_message = "Namespace needs to be : \"sec\", \"auth\", \"infra\", \"api\" or \"web\"."
  }
}

variable "application_name" {
  type = object({
    short = string
    long  = string
  })
  description = "Used in naming conventions, expecting an object"

  validation {
    condition     = length(var.application_name["short"]) <= 5
    error_message = "The application_name[\"short\"] needs to be less or equal to 5 chars."
  }
}

variable "nukeable" {
  type        = bool
  description = "Can these resources be cleaned up. Will be ignored for prod environments"
}

variable "client_name" {
  type = object({
    short = string
    long  = string
  })
  description = "Used in naming conventions, expecting an object"

  validation {
    condition     = length(var.client_name["short"]) <= 5
    error_message = "The client_name[\"short\"] needs to be less or equal to 5 chars."
  }
}

variable "purpose" {
  type        = string
  description = "Used for cost allocation purposes"

  validation {
    condition     = contains(["rnd", "client", "product", "self"], var.purpose)
    error_message = "Purpose needs to be : \"rnd\", \"client\", \"product\", \"self\"."
  }
}

variable "owner" {
  type        = string
  description = "Used to find resources owners, expects an email address"

  validation {
    condition     = can(regex("^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$", var.owner))
    error_message = "Owner needs to be a valid email address."
  }
}

variable "aws_account_id" {
  type        = string
  description = "Needed for Guards to ensure code is being deployed to the correct account"
}

variable "tags" {
  type        = map(string)
  description = "Tags added to all resources, this will be added to the list of mandatory tags"
}


data "aws_caller_identity" "current" {
  provider = aws.reference
}


resource "null_resource" "tf_guard_provider_account_match" {
  count = tonumber(data.aws_caller_identity.current.account_id == var.aws_account_id ? "1" : "fail")
}

locals {
  mandatory_tags = merge(var.tags, {
    "cat:application" = var.application_name.long
    "cat:client"      = var.client_name.long
    "cat:purpose"     = var.purpose
    "cat:owner"       = var.owner
    "cat:repo"        = var.code_repo
    "cat:nukeable"    = var.nukeable

    "tf:account_id" = data.aws_caller_identity.current.account_id
    "tf:caller_arn" = data.aws_caller_identity.current.arn
    "tf:user_id"    = data.aws_caller_identity.current.user_id

    "app:region"      = var.region
    "app:namespace"   = var.namespace
    "app:environment" = var.environment
  })

  naming_prefix = join("-", [
    var.client_name.short,
    var.application_name.short,
    var.environment,
    var.namespace
  ])
}

locals {
  // https://aws.amazon.com/blogs/compute/upcoming-changes-to-the-python-sdk-in-aws-lambda/
  python_layers = {
    "ap-northeast-1" = "arn:aws:lambda:ap-northeast-1:249908578461:layer:AWSLambda-Python-AWS-SDK:4"
    "us-east-1"      = "arn:aws:lambda:us-east-1:668099181075:layer:AWSLambda-Python-AWS-SDK:4"
    "ap-southeast-1" = "arn:aws:lambda:ap-southeast-1:468957933125:layer:AWSLambda-Python-AWS-SDK:4"
