#File: provider.tf
terraform {
  required_providers {
    azurerm = {
      source  = "hashicorp/azurerm"
      version = "3.3.0"
    }
  }
}

# Configure the Microsoft Azure Provider
provider "azurerm" {
  features {}
}
#File: variables.tf
# Lista de variaveis utilizadas nos arquivos de terraform
variable "resource_group_name" {
  default = "learn-ee657660-217b-49cb-ad8d-f9ba4dbafa1f"
}

variable "location" {
  default = "westus"
}

terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "5.16.2"
    }
    confluent = {
      source  = "confluentinc/confluent"
      version = "1.51.0"
    }
    cloudamqp = {
      source  = "cloudamqp/cloudamqp"
      version = "1.28.0"
    }
    mongodbatlas = {
      source  = "mongodb/mongodbatlas"
      version = "1.12.1"
    }
  }
}


provider "confluent" {
  cloud_api_key    = var.confluent_cloud_api_key
  cloud_api_secret = var.confluent_cloud_api_secret
}

provider "confluent" {
  # https://developer.hashicorp.com/terraform/language/providers/configuration#alias-multiple-provider-configurations
  alias = "kafka"

  cloud_api_key    = var.confluent_cloud_api_key
  cloud_api_secret = var.confluent_cloud_api_secret

  #   kafka_id            = confluent_kafka_cluster.basic.id
  kafka_rest_endpoint = confluent_kafka_cluster.basic.rest_endpoint
  kafka_api_key       = confluent_api_key.app-manager-kafka-api-key.id
  kafka_api_secret    = confluent_api_key.app-manager-kafka-api-key.secret
}

provider "aws" {
  region = var.region
}

# Configure the CloudAMQP Provider
provider "cloudamqp" {
  apikey = var.cloudamqp_customer_api_key
}

# Configure the MongoDB Atlas Provider 
provider "mongodbatlas" {
  public_key  = var.mongodbatlas_public_key
  private_key = var.mongodbatlas_private_key
}

resource "confluent_environment" "demo" {
  display_name = "Demo_Database_Modernization"
}

data "confluent_schema_registry_region" "sg_package" {
  cloud   = "AWS"
  region  = "us-east-2"
  package = var.sg_package
}

resource "confluent_schema_registry_cluster" "sr_package" {
  package = data.confluent_schema_registry_region.sg_package.package

  environment {
    id = confluent_environment.demo.id
  }

  region {
    # See https://docs.confluent.io/cloud/current/stream-governance/packages.html#stream-governance-regions
    id = data.confluent_schema_registry_region.sg_package.id
  }
}

resource "confluent_kafka_cluster" "basic" {
  display_name = "demo_kafka_cluster"
  availability = "SINGLE_ZONE"
  cloud        = "AWS"
  region       = "us-west-2"
  basic {}

  environment {
    id = confluent_environment.demo.id
  }
}

# 'app-manager' service account is required in this configuration to create 'rabbitmq_transactions' topic and grant ACLs
# to 'app-producer' and 'app-consumer' service accounts.
resource "confluent_service_account" "app-manager" {
  display_name = "app-manager"
  description  = "Service account to manage 'demo' Kafka cluster"
}

resource "confluent_role_binding" "app-manager-kafka-cluster-admin" {
  principal   = "User:${confluent_service_account.app-manager.id}"
  role_name   = "CloudClusterAdmin"
  crn_pattern = confluent_kafka_cluster.basic.rbac_crn
}

resource "confluent_api_key" "app-manager-kafka-api-key" {
  display_name = "app-manager-kafka-api-key"
  description  = "Kafka API Key that is owned by 'app-manager' service account"
  owner {
    id          = confluent_service_account.app-manager.id
    api_version = confluent_service_account.app-manager.api_version
    kind        = confluent_service_account.app-manager.kind
  }

  managed_resource {
    id          = confluent_kafka_cluster.basic.id
    api_version = confluent_kafka_cluster.basic.api_version
    kind        = confluent_kafka_cluster.basic.kind

    environment {
      id = confluent_environment.demo.id
    }
  }

  # The goal is to ensure that confluent_role_binding.app-manager-kafka-cluster-admin is created before
  # confluent_api_key.app-manager-kafka-api-key is used to create instances of
  # confluent_kafka_topic, confluent_kafka_acl resources.

  # 'depends_on' meta-argument is specified in confluent_api_key.app-manager-kafka-api-key to avoid having
  # multiple copies of this definition in the configuration which would happen if we specify it in
  # confluent_kafka_topic, confluent_kafka_acl resources instead.
  depends_on = [
    confluent_role_binding.app-manager-kafka-cluster-admin
  ]
}

# Create rabbitmq_transactions topic 
resource "confluent_kafka_topic" "rabbitmq_transactions" {
  kafka_cluster {
    id = confluent_kafka_cluster.basic.id
  }

  topic_name       = "rabbitmq_transactions"
  rest_endpoint    = confluent_kafka_cluster.basic.rest_endpoint
  partitions_count = 1
  credentials {
    key    = confluent_api_key.app-manager-kafka-api-key.id
    secret = confluent_api_key.app-manager-kafka-api-key.secret
  }
}

# Create Oracle redo log topic 
resource "confluent_kafka_topic" "oracle_redo_log" {
  kafka_cluster {
    id = confluent_kafka_cluster.basic.id
  }

  topic_name       = "OracleCdcSourceConnector-dbmod-redo-log"
  rest_endpoint    = confluent_kafka_cluster.basic.rest_endpoint
  partitions_count = 1
  credentials {
    key    = confluent_api_key.app-manager-kafka-api-key.id
    secret = confluent_api_key.app-manager-kafka-api-key.secret
  }
}

# Create a service account for ksqlDB 
resource "confluent_service_account" "app-ksql" {
  display_name = "app-ksql"
  description  = "Service account to manage 'demo-ksql' ksqlDB cluster"
}

resource "confluent_role_binding" "app-ksql-kafka-cluster-admin" {
  principal   = "User:${confluent_service_account.app-ksql.id}"
  role_name   = "CloudClusterAdmin"
  crn_pattern = confluent_kafka_cluster.basic.rbac_crn
}

resource "confluent_role_binding" "app-ksql-schema-registry-resource-owner" {
  principal   = "User:${confluent_service_account.app-ksql.id}"
  role_name   = "ResourceOwner"
  crn_pattern = format("%s/%s", confluent_schema_registry_cluster.sr_package.resource_name, "subject=*")

  lifecycle {
    prevent_destroy = false
  }
}

# Create ksqlDB cluster  
resource "confluent_ksql_cluster" "demo-ksql" {
  display_name = "demo-ksql"
  csu          = 1
  kafka_cluster {
    id = confluent_kafka_cluster.basic.id
  }
  credential_identity {
    id = confluent_service_account.app-ksql.id
  }
  environment {
    id = confluent_environment.demo.id
  }
  depends_on = [
    confluent_role_binding.app-ksql-kafka-cluster-admin,
    confluent_role_binding.app-ksql-schema-registry-resource-owner,
    confluent_schema_registry_cluster.sr_package
  ]
}

# Create Amazon RDS (Oracle)
resource "aws_db_instance" "demo-db-mod" {
  identifier     = var.rds_instance_identifier
  engine         = "oracle-se2"
  engine_version = "19"
  instance_class = var.rds_instance_class
  # name           = "ORCL"
  username       = var.rds_username
  password       = var.rds_password
  port           = 1521
  license_model  = "license-included"
  #   parameter_group_name = "default.oracle-se2-19.0"
  allocated_storage   = 20
  storage_encrypted   = false
  skip_final_snapshot = true
  publicly_accessible = true
  tags = {
    name       = "demo-db-mod"
    created_by = "terraform"
  }
}

# Create a new cloudamqp instance
resource "cloudamqp_instance" "instance" {
  name        = "demo-db-mod"
  plan        = "lemur"
  region      = "amazon-web-services::us-west-2"
  tags        = ["terraform"]
  rmq_version = "3.12.6"
}

# To retrieve the password for the newly created instance
data "cloudamqp_credentials" "credentials" {
  instance_id = cloudamqp_instance.instance.id
}

# Create a Project
resource "mongodbatlas_project" "atlas-project" {
  org_id = var.mongodbatlas_org_id
  name   = var.mongodbatlas_project_name
}

# Create MongoDB Atlas resources
resource "mongodbatlas_cluster" "demo-database-modernization" {
  project_id = mongodbatlas_project.atlas-project.id
  name       = "demo-db-mod"

  # Provider Settings "block"
  provider_instance_size_name = "M0"
  provider_name               = "TENANT"
  backing_provider_name       = "AWS"
  provider_region_name        = var.mongodbatlas_region
}

resource "mongodbatlas_project_ip_access_list" "demo-database-modernization-ip" {
  project_id = mongodbatlas_project.atlas-project.id
  cidr_block = "0.0.0.0/0"
  comment    = "Allow connections from anywhere for demo purposes"
}

# Create a MongoDB Atlas Admin Database User
resource "mongodbatlas_database_user" "demo-database-modernization-db-user" {
  username           = var.mongodbatlas_database_username
  password           = var.mongodbatlas_database_password
  project_id         = mongodbatlas_project.atlas-project.id
  auth_database_name = "admin"

  roles {
    role_name     = "readWrite"
    database_name = mongodbatlas_cluster.demo-database-modernization.name
  }
}

#File: honey-sqldb.tf
data "azuread_user" "entra_sqladmin" {
  count               = var.enable_sqldb ? 1 : 0
  user_principal_name = var.sql_admin
}

resource "azurerm_mssql_server" "sqlsrv_credit_cards" {
  count               = var.enable_sqldb ? 1 : 0
  name                = "creditcardsdata${random_integer.random_int.id}"
  resource_group_name = azurerm_resource_group.rg_honey_test.name
  location            = azurerm_resource_group.rg_honey_test.location
  version             = "12.0"

  identity {
    type = "SystemAssigned"
  }

  azuread_administrator {
    login_username              = data.azuread_user.entra_sqladmin[0].user_principal_name
    object_id                   = data.azuread_user.entra_sqladmin[0].object_id
    azuread_authentication_only = true
  }

  public_network_access_enabled = true
  depends_on                    = [azurerm_resource_group.rg_honey_test]
}

resource "azurerm_mssql_firewall_rule" "sqlsrv_firewall_rule" {
  count            = var.enable_sqldb ? 1 : 0
  name             = "AllowAllWindowsAzureIps"
  server_id        = azurerm_mssql_server.sqlsrv_credit_cards[0].id
  start_ip_address = "0.0.0.0"
  end_ip_address   = "255.255.255.255"
  depends_on       = [azurerm_mssql_server.sqlsrv_credit_cards]
}

resource "azurerm_mssql_database" "sqldb_credit_cards" {
  count     = var.enable_sqldb ? 1 : 0
  name      = "CreditCardsData"
  server_id = azurerm_mssql_server.sqlsrv_credit_cards[0].id
  sku_name  = "Basic"

  storage_account_type = "Local"
  depends_on           = [azurerm_mssql_server.sqlsrv_credit_cards]
}

resource "azurerm_mssql_server_extended_auditing_policy" "sqlsrv_auditing_policy" {
  count                  = var.enable_sqldb ? 1 : 0
  server_id              = azurerm_mssql_server.sqlsrv_credit_cards[0].id
  log_monitoring_enabled = true
  depends_on             = [azurerm_mssql_server.sqlsrv_credit_cards]
}

resource "azurerm_mssql_database_extended_auditing_policy" "sqldb_auditing_policy" {
  count                  = var.enable_sqldb ? 1 : 0
  database_id            = azurerm_mssql_database.sqldb_credit_cards[0].id
  log_monitoring_enabled = true
  depends_on             = [azurerm_mssql_database.sqldb_credit_cards, azurerm_mssql_server_extended_auditing_policy.sqlsrv_auditing_policy]
}

resource "azurerm_monitor_diagnostic_setting" "ds_sqldb" {
  count                          = var.enable_sqldb ? 1 : 0
  name                           = "ds_sqldb"
  target_resource_id             = azurerm_mssql_database.sqldb_credit_cards[0].id
  log_analytics_workspace_id     = azurerm_log_analytics_workspace.monitor.id
  log_analytics_destination_type = "Dedicated"

  log {
    category = "SQLSecurityAuditEvents"
    enabled  = true
  }

  depends_on = [azurerm_mssql_database_extended_auditing_policy.sqldb_auditing_policy]
}

resource "azurerm_monitor_scheduled_query_rules_alert" "alert_sql" {
  count               = var.enable_sqldb ? 1 : 0
  name                = "alert_sql"
  location            = azurerm_resource_group.rg_honey_test.location
  resource_group_name = azurerm_resource_group.rg_honey_test.name

  action {
    action_group = [azurerm_monitor_action_group.alert_email_action.id]
  }
  data_source_id = azurerm_log_analytics_workspace.monitor.id
  description    = "Alert when the SQL DB is accessed"
  enabled        = true
  query          = <<-QUERY
AzureDiagnostics
| where client_ip_s != "Internal"
| extend RequesterUser = iff(session_server_principal_name_s == server_principal_name_s, tostring(session_server_principal_name_s), strcat(session_server_principal_name_s, ", ", server_principal_name_s))
| project TimeGenerated, RequesterUser, client_ip_s, ResourceGroup, LogicalServerName_s, database_name_s, action_name_s
  QUERY
  severity       = 1
  frequency      = 5
  time_window    = 30
  trigger {
    operator  = "GreaterThan"
    threshold = 0
  }
  depends_on = [azurerm_monitor_action_group.alert_email_action]
}
#File: honey-storage.tf
# Azure Storage Account
resource "azurerm_storage_account" "sa_customers_data" {
  count                    = var.enable_storage ? 1 : 0
  name                     = "customersdata${random_integer.random_int.id}"
  resource_group_name      = azurerm_resource_group.rg_honey_test.name
  location                 = azurerm_resource_group.rg_honey_test.location
  account_tier             = "Standard"
  account_replication_type = "LRS"
  depends_on               = [azurerm_resource_group.rg_honey_test]
}

# Azure Container
resource "azurerm_storage_container" "cnt_customers_data" {
  count                 = var.enable_storage ? 1 : 0
  name                  = "content"
  storage_account_name  = azurerm_storage_account.sa_customers_data[0].name
  container_access_type = "private"
  depends_on            = [azurerm_storage_account.sa_customers_data]
}

# Azure Blob
resource "azurerm_storage_blob" "users_data" {
  count                  = var.enable_storage ? 1 : 0
  name                   = "users_data.csv"
  storage_account_name   = azurerm_storage_account.sa_customers_data[0].name
  storage_container_name = azurerm_storage_container.cnt_customers_data[0].name
  type                   = "Block"
  access_tier            = "Cool"
  source                 = "users_data.csv"
  depends_on             = [azurerm_storage_container.cnt_customers_data]
}

# Monitor Diagnostic Settings for the blob (this enables logging on the blob)
resource "azurerm_monitor_diagnostic_setting" "ds_blob" {
  count                      = var.enable_storage ? 1 : 0
  name                       = split("/", azurerm_log_analytics_workspace.monitor.id)[length(split("/", azurerm_log_analytics_workspace.monitor.id)) - 1]
  target_resource_id         = "${azurerm_storage_account.sa_customers_data[0].id}/blobServices/default/"
  log_analytics_workspace_id = azurerm_log_analytics_workspace.monitor.id

  log {
    category = "StorageRead"
    enabled  = true
  }
  log {
    category = "StorageWrite"
    enabled  = true
  }
  log {
    category = "StorageDelete"
    enabled  = true
  }
  depends_on = [azurerm_storage_account.sa_customers_data]
}

# Alert Rule
resource "azurerm_monitor_scheduled_query_rules_alert" "alert_blob" {
  count               = var.enable_storage ? 1 : 0
  name                = "alert_blob"
  location            = azurerm_resource_group.rg_honey_test.location
  resource_group_name = azurerm_resource_group.rg_honey_test.name

  action {
    action_group = [azurerm_monitor_action_group.alert_email_action.id]
  }
  data_source_id = azurerm_log_analytics_workspace.monitor.id
  description    = "Alert when the blob is accessed"
  enabled        = true
  # Count all requests with server error result code grouped into 5-minute bins
  query       = <<-QUERY
  StorageBlobLogs 
| where isnotempty(RequesterUpn) 
| project RequesterUpn,CallerIpAddress, Category, OperationName, _ResourceId, StatusText
  QUERY
  severity    = 1
  frequency   = 5
  time_window = 30
  trigger {
    operator  = "GreaterThan"
    threshold = 0
  }
  depends_on = [azurerm_monitor_action_group.alert_email_action]
}

#File: main.tf
terraform {
  required_version = ">= 1.6.5"

  required_providers {
    azurerm = {
      source  = "hashicorp/azurerm"
      version = "=3.0.0"
    }
    azuread = {
      source  = "hashicorp/azuread"
      version = "~> 2.15.0"
    }
  }
}

provider "azuread" {}

provider "azurerm" {
  skip_provider_registration = true # This is only required when the User, Service Principal, or Identity running Terraform lacks the permissions to register Azure Resource Providers.
  features {}
}

provider "random" {}

resource "random_integer" "random_int" {
  min = 1000
  max = 9999
}


resource "azurerm_resource_group" "rg_honey_test" {
  name     = "honey_test"
  location = "Italy North"
}

# Azure Analytics Workspace 

resource "azurerm_log_analytics_workspace" "monitor" {
  name                = "log-analytics-workspace"
  resource_group_name = azurerm_resource_group.rg_honey_test.name
  location            = azurerm_resource_group.rg_honey_test.location
  sku                 = "PerGB2018"
  retention_in_days   = 30
  depends_on          = [azurerm_resource_group.rg_honey_test]
}

# Action Group (it specifies the receiver of the alert)

resource "azurerm_monitor_action_group" "alert_email_action" {
  name                = "EmailAction"
  resource_group_name = azurerm_resource_group.rg_honey_test.name
  short_name          = "admin-mail"
  email_receiver {
    name                    = "sendtoUser"
    email_address           = var.destination_email
    use_common_alert_schema = true
  }
  depends_on = [azurerm_resource_group.rg_honey_test, azurerm_log_analytics_workspace.monitor]
}

#File: variables.tf
variable "destination_email" {
  description = "Destination email for the alert"
}

variable "sql_admin" {
  description = "Entra ID email of the user designed to be the administrator of the SQL Server"
}

variable "enable_storage" {
  description = "Specify the creation of the honey storage"
}

variable "enable_sqldb" {
  description = "Specify the creation of the honey sqldb"
}

provider "aws" {
  region = var.dev_machine_region
}

data "aws_vpc" "default" {
  default = true
}

variable "name_prefix" {
  type        = string
  description = "A common prefix to apply to the names of all AWS resources."
}

variable "tags" {
  type        = map(string)
  description = "A set of tags to apply to all respective AWS resources."
}

variable "iam_role" {
  type = object({
    name = string
  })
  description = "The IAM role to which the policy to read the Google Workspace credentials should be attached."
}

variable "enabled" {
  type        = bool
  description = "Whether or not the module is enabled."
}

variable "actor" {
  type        = string
  description = "The email address of the administrator in Google Workspace that the service account is acting on behalf of."
}

variable "bridgeAddress" {
  type        = string
  description = "The URL of 1Password SCIM Bridge."
}


data "aws_subnet" "default" {
  availability_zone = var.dev_efs_az
}

resource "random_string" "random" {
  length = 12
  special = false
  lower = true
  upper = false
}

resource "aws_security_group" "dev_machine_firewall" {
  name = "dev_machine_sg"
  vpc_id = data.aws_vpc.default.id

  ingress {
    description      = "SSH"
    from_port        = 22
    to_port          = 22
    protocol         = "tcp"
    cidr_blocks      = concat([ var.dev_my_ip ], var.dev_cloud9_ips)
  }

  egress {
    from_port        = 0
    to_port          = 0
    protocol         = "-1"
    cidr_blocks      = [ "0.0.0.0/0" ]
  }
}

resource "aws_security_group" "dev_efs_firewall" {
  name = "dev_efs_sg"
  vpc_id = data.aws_vpc.default.id

  ingress {
    description      = "NFSv4"
    from_port        = 2049
    to_port          = 2049
    protocol         = "tcp"
    security_groups = [ aws_security_group.dev_machine_firewall.id ]
  }

  egress {
    from_port        = 0
    to_port          = 0
    protocol         = "-1"
    cidr_blocks      = [ "0.0.0.0/0" ]
  }
}

resource "aws_efs_file_system" "dev_efs" {
  availability_zone_name = var.dev_efs_az
  creation_token = "dev_machine_files"

  lifecycle_policy {
    transition_to_ia = "AFTER_7_DAYS"
  }
  
  lifecycle_policy {
    transition_to_primary_storage_class = "AFTER_1_ACCESS"
  }

  lifecycle {
    prevent_destroy = true
  }
}

resource "aws_efs_access_point" "dev_efs_main_ap" {
  file_system_id = aws_efs_file_system.dev_efs.id
  posix_user {
    uid = 1000
    gid = 1000
  }

  root_directory {
    creation_info {
      owner_uid = 1000
      owner_gid = 1000
      permissions = 0755
    }
    path = "/data"
  }
}

resource "aws_efs_access_point" "dev_efs_docker_ap" {
  file_system_id = aws_efs_file_system.dev_efs.id
  posix_user {
    uid = 0
    gid = 0
  }

  root_directory {
    creation_info {
      owner_uid = 0
      owner_gid = 0
      permissions = 0755
    }
    path = "/docker"
  }
}

resource "aws_efs_mount_target" "dev_efs_mount" {
  file_system_id = aws_efs_file_system.dev_efs.id
  subnet_id = data.aws_subnet.default.id
  security_groups = [ aws_security_group.dev_efs_firewall.id ]
}

resource "aws_eip" "dev_machine_ip" {
  vpc = true
}

resource "aws_ssm_parameter" "dev_machine_ip" {
  name  = "dev_ip_allocation_id"
  type  = "String"
  value = aws_eip.dev_machine_ip.allocation_id
}

resource "aws_ssm_parameter" "dev_efs_az" {
  name  = "dev_efs_az"
  type  = "String"
  value = var.dev_efs_az
}

resource "aws_ssm_parameter" "dev_efs" {
  name  = "dev_efs_id"
  type  = "String"
  value = aws_efs_file_system.dev_efs.id
}

resource "aws_ssm_parameter" "dev_efs_main_ap" {
  name  = "dev_efs_data_ap"
  type  = "String"
  value = aws_efs_access_point.dev_efs_main_ap.id
}

resource "aws_ssm_parameter" "dev_efs_docker_ap" {
  name  = "dev_efs_docker_ap"
  type  = "String"
  value = aws_efs_access_point.dev_efs_docker_ap.id
}

resource "aws_ssm_parameter" "dev_ssh_key" {
  name  = "dev_ssh_key"
  type  = "String"
  value = var.dev_ssh_public_key
}

data "aws_ami" "amzn_linux2" {
  owners = ["amazon"]
  most_recent = "true"

  filter {
    name = "name"
    values = [ "amzn2-ami-kernel-*" ]
  }

  filter {
    name   = "virtualization-type"
    values = ["hvm"]
  }
}

resource "aws_iam_instance_profile" "dev_ec2_profile" {
  name = "dev_instance_profile-${random_string.random.result}"
  role = aws_iam_role.dev_machine_role.name
}

resource "aws_key_pair" "dev_ssh_key" {
  key_name = "dev_ssh_key"
  public_key = var.dev_ssh_public_key
}

resource "aws_spot_fleet_request" "dev_spot_request" {
  iam_fleet_role = aws_iam_role.dev_spot_fleet_role.arn
  spot_price = var.dev_spot_price
  allocation_strategy = "diversified"
  target_capacity = 1
  fleet_type = "maintain"
  on_demand_target_capacity = 0
  instance_interruption_behaviour = "terminate"
  terminate_instances_with_expiration = true
  wait_for_fulfillment = true
  depends_on = [
    aws_efs_file_system.dev_efs,
    aws_efs_mount_target.dev_efs_mount,
    aws_efs_access_point.dev_efs_main_ap,
    aws_efs_access_point.dev_efs_docker_ap
  ]

  # similar with aws_instance
  launch_specification {
    key_name = aws_key_pair.dev_ssh_key.id
    instance_type = var.dev_instance_type
    ami = data.aws_ami.amzn_linux2.id
    spot_price = var.dev_spot_price
    iam_instance_profile = aws_iam_instance_profile.dev_ec2_profile.name
    vpc_security_group_ids = [aws_security_group.dev_machine_firewall.id]

    user_data = <<EOF
#!/bin/sh

curl -L -s ${var.dev_user_data_url} | bash
EOF
  }
}

resource "aws_s3_bucket" "dev_main_bucket" {
  bucket = var.dev_bucket_name != "" ? var.dev_bucket_name : lower("dev-spot-machine-${random_string.random.result}")
  acl = "private"
}

data "aws_caller_identity" "current" {}

resource "aws_iam_role" "dev_spot_fleet_role" {
  name = "EC2DevSpotFleetRole-${random_string.random.result}"
  assume_role_policy = jsonencode({
    Statement = [
      {
        Effect = "Allow",
        Principal = {
            Service = "spotfleet.amazonaws.com"
        },
        Action = "sts:AssumeRole"
      }
    ]
  })

  inline_policy {
    name = "dev_spot_fleet"
    policy = jsonencode({
      Statement = [
        {
          Effect = "Allow",
          Action: [
              "ec2:DescribeImages",
              "ec2:DescribeSubnets",
              "ec2:RequestSpotInstances",
              "ec2:TerminateInstances",
              "ec2:DescribeInstanceStatus",
              "ec2:CreateTags",
              "ec2:RunInstances"
          ],
          Resource = "*"
        },
        {
          Effect = "Allow",
          Action = "iam:PassRole",
          Condition = {
              StringEquals = {
                  "iam:PassedToService" = [
                    "ec2.amazonaws.com",
                    "ec2.amazonaws.com.cn"
                  ]
              }
          },
          Resource = "*"
        },
        {
          Effect = "Allow",
          Action = "elasticloadbalancing:RegisterInstancesWithLoadBalancer",
          Resource = "arn:aws:elasticloadbalancing:*:*:loadbalancer/*"
        },
        {
          Effect = "Allow",
          Action = "elasticloadbalancing:RegisterTargets",
          Resource = "arn:aws:elasticloadbalancing:*:*:*/*"
        }
      ]
    })
  }
}

resource "aws_iam_role" "dev_machine_role" {
  name = "EC2DevMachineRole-${random_string.random.result}"

  assume_role_policy = jsonencode({
    Statement = [
      {
        Action = "sts:AssumeRole"
        Effect = "Allow"
        Principal = {
          Service = "ec2.amazonaws.com"
        }
      },
      {
        Action = "sts:AssumeRole"
        Effect = "Allow"
        Principal = {
          AWS = data.aws_caller_identity.current.arn
        }
      }
    ]
  })

  inline_policy {
    name = "dev_s3_bucket"
    policy = jsonencode({
      Statement = [
        {
          Effect = "Allow",
          Action = [
            "s3:ListBucket",
            "s3:CreateBucket",
            "s3:DeleteBucket"
          ]
          Resource = "arn:aws:s3:::${var.dev_bucket_name}"
        },
        {
          Effect = "Allow",
          Action = [
            "s3:*",
            "s3-object-lambda:*"
          ]
          Resource = "arn:aws:s3:::${var.dev_bucket_name}/*"
        }
      ]
    })
  }
  # S3

  inline_policy {
    name = "dev_ssm_access"
    policy = jsonencode({
      Statement = [
        {
          Effect = "Allow",
          Action = [
            "ssm:PutParameter",
            "ssm:DeleteParameter",
            "ssm:GetParameterHistory",
            "ssm:GetParametersByPath",
            "ssm:GetParameters",
            "ssm:GetParameter",
            "ssm:DeleteParameters"
          ]
          Resource = "arn:aws:ssm:${var.dev_machine_region}:${data.aws_caller_identity.current.account_id}:parameter/dev_*"
        },
        {
          Effect = "Allow",
          Action = [
            "ssm:DescribeParameters"
          ]
          Resource = "*"
        }
      ]
    })
  }
  # SSM

  inline_policy {
    name = "dev_ec2_access"
    policy = jsonencode({
      Statement = [
        {
          Effect = "Allow"
          Action = [
            "ec2:AssociateAddress"
          ]
          Resource = [
            "arn:aws:ec2:${var.dev_machine_region}:${data.aws_caller_identity.current.account_id}:instance/*",
            "arn:aws:ec2:${var.dev_machine_region}:${data.aws_caller_identity.current.account_id}:elastic-ip/${aws_eip.dev_machine_ip.allocation_id}"
          ]
        }
      ]
    })
  }
  # EC2/EIP

}

# This role should be used only if you need access to all AWS Services
resource "aws_iam_role" "dev_machine_admin_role" {
  name = "EC2DevMachineAdminRole-${random_string.random.result}"

  assume_role_policy = jsonencode({
    Statement = [
      {
        Action = "sts:AssumeRole"
        Effect = "Allow"
        Principal = {
          Service = "ec2.amazonaws.com"
        }
      },
      {
        Action = "sts:AssumeRole"
        Effect = "Allow"
        Principal = {
          AWS = data.aws_caller_identity.current.arn
        }
      }
    ]
  })

  inline_policy {
    name = "dev_admin_access"
    policy = jsonencode({
      Statement = [
        {
          Effect = "Allow",
          Action = "*"
          Resource = "*"
        }
      ]
    })
  }
  # admin
}

