provider "aws" {
  region  = "${var.aws_region}"
  profile = "${var.aws_profile}"
}

## VPC:
resource "aws_vpc" "new-vpc" {
  count = "${var.create_vpc == 1 ? 1 : 0}"

  cidr_block                       = "${var.vpc_cidr}"
  enable_dns_support               = true
  enable_dns_hostnames             = true
  assign_generated_ipv6_cidr_block = "${var.use_ipv6 == 0 ? true : false}"

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

data "aws_vpc" "vpc" {
  id = "${var.create_vpc == 0 ? var.vpc_id : "${join("", aws_vpc.new-vpc.*.id)}"}"
}

## Subnets

resource "aws_subnet" "public-subnet" {
  count                           = "${var.create_vpc == 1 ? 1 : 0}"
  vpc_id                          = "${data.aws_vpc.vpc.id}"
  cidr_block                      = "${var.subnet_cidr_block}"
  assign_ipv6_address_on_creation = "${var.use_ipv6 == true ? true : false}"

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }

  depends_on = ["aws_vpc.new-vpc"]
}

data "aws_subnet" "subnet" {
  id = "${var.subnet_id != "" ? var.subnet_id : "${join("", aws_subnet.public-subnet.*.id)}"}"
}

## Internet Gateway
resource "aws_internet_gateway" "igw" {
  count  = "${var.create_vpc == 1 ? 1 : 0}"
  vpc_id = "${data.aws_vpc.vpc.id}"

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

## Route Table

resource "aws_route_table" "rt-ipv6" {
  count  = "${var.create_vpc * var.use_ipv6 == 1 ? 1 : 0}"
  vpc_id = "${aws_vpc.new-vpc[count.index].id}"

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = "${aws_internet_gateway.igw[count.index].id}"
  }

  route {
    ipv6_cidr_block = "::/0"
    gateway_id      = "${aws_internet_gateway.igw[count.index].id}"
  }

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

resource "aws_route_table" "rt-ipv4only" {
  count  = "${var.create_vpc * var.use_ipv4only == 1 ? 1 : 0}"
  vpc_id = "${aws_vpc.new-vpc[count.index].id}"

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = "${aws_internet_gateway.igw[count.index].id}"
  }

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

resource "aws_route_table_association" "rtassoc" {
  count = "${var.create_vpc == 1 ? 1 : 0}"

  subnet_id      = "${aws_subnet.public-subnet[count.index].id}"
  route_table_id = "${var.use_ipv6 == 1 ? "${join("", aws_route_table.rt-ipv6.*.id)}" : "${join("", aws_route_table.rt-ipv4only.*.id)}"}"
}

## Security Group

resource "aws_security_group" "sg-ipv6" {
  count       = "${var.use_ipv6 == 1 ? 1 : 0}"
  name        = "kali-ipv6"
  description = "Allow all IPv4 and IPv6 for kali"
  vpc_id      = "${data.aws_vpc.vpc.id}"

  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  ingress {
    from_port        = 0
    to_port          = 0
    protocol         = "-1"
    ipv6_cidr_blocks = ["::/0"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  egress {
    from_port        = 0
    to_port          = 0
    protocol         = "-1"
    ipv6_cidr_blocks = ["::/0"]
  }

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

resource "aws_security_group" "sg-ipv4-only" {
  count       = "${var.use_ipv4only == 1 ? 1 : 0}"
  name        = "kali-ipv4only"
  description = "Allow all IPv4 for kali"
  vpc_id      = "${data.aws_vpc.vpc.id}"

  ingress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}

# data "aws_security_group" "sg" {
#   id = "${var.create_vpc * var.use_ipv6 == 0 ? aws_security_group.sg-ipv6.id : aws_security_group.sg-ipv4-only.id}"
# }

## SSH Key pair

resource "aws_key_pair" "ssh_key_pair" {
  count      = "${var.ssh_key_pair_name == "" ? 1 : 0}"
  key_name   = "kali-ssh-key"
  public_key = "${file("${var.public_key_path}")}"
}

locals {
  key_pair_name = "${var.ssh_key_pair_name == "" ? aws_key_pair.ssh_key_pair.0.key_name : var.ssh_key_pair_name}"
}

## EC2 instance

## TODO: check why the AMI doesnt use this userdata:
locals {
  kali-userdata = <<USERDATA
#!/bin/bash
apt-get update
apt-get dist-upgrade -y
USERDATA
}

resource "aws_instance" "kali_machine" {
  ami                         = "${var.packer_ami}"
  instance_type               = "${var.ec2_instance_type}"
  monitoring                  = false
  vpc_security_group_ids      = ["${var.use_ipv6 == 1 ? "${join("", aws_security_group.sg-ipv6.*.id)}" : "${join("", aws_security_group.sg-ipv4-only.*.id)}"}"]
  associate_public_ip_address = true
  subnet_id                   = "${data.aws_subnet.subnet.id}"
  key_name                    = "${local.key_pair_name}"
  source_dest_check           = false
  user_data_base64            = "${base64encode(local.kali-userdata)}"

  tags = {
    Project   = "kali"
    ManagedBy = "terraform"
  }
}
variable "aws_region" {
  description = "AWS region"
  default     = "eu-central-1"
}

variable "aws_profile" {
  description = "AWS cli profile"
  default     = "default"
}

variable "vpc_cidr" {
  description = "VPC CIDR block for newly created AWS VPC (e.g. `10.23.0.0/16` or `172.31.0.0/16`) - The Subnet CIDR below must match this VPC CIDR"
  default     = "10.23.0.0/16"
}

variable "subnet_cidr_block" {
  description = "The CIDR block to use for the newly created subnet (e.g. `10.23.0.0/24` or `172.31.0.0/20`) - Must be in range of VPC CIDR"
  default     = "10.23.1.0/24"
}

variable "ssh_key_pair_name" {
  description = "AWS Key pair name of existing SSH Key pair on AWS (e.g. `my-key`)"
  default     = ""
}

variable "public_key_path" {
  description = "Path to your SSH public key (e.g. `~/.ssh/id_rsa.pub`)"
  default     = "~/.ssh/id_rsa.pub"
}

variable "vpc_id" {
  description = "Use an existing VPC (please set create_vpc to false when using this)"
  default     = ""
}

variable "subnet_id" {
  description = "Use an existting Subnet in an existing VPC (please set create_vpc to false when using this)"
  default     = ""
}

variable "create_vpc" {
  description = "Create new VPC (e.g. `1` => true or `0` => false) - Please set to false when setting an existing vpc_id above"
  default     = 1
}

variable "use_ipv6" {
  description = "Use IPv4 AND IPv6 (e.g. `1` => true or `0` => false) - NOTE: no doublequotes around the true or false"
  default     = 1
}

variable "use_ipv4only" {
  description = "Use IPv4 only (e.g. `1` => true or `0` => false) - Please set use_ipv6 to false when enabling this"
  default     = 0
}

variable "ec2_instance_type" {
  description = "EC2 instance type (e.g. `t2.medium` or `t2.small`)"
  default     = "t2.medium"
}

variable "packer_ami" {
  description = "Packer AMI ID to use for EC2 instance (NOTE: run `packer buidl packer.json` and use the generated AMI ID here)"
  default = "ami-10e00b6d"
}

#File: main.tf

# --- BUCKET

# Create a bucket
# see https://registry.terraform.io/providers/hashicorp/google/latest/docs/resources/storage_bucket
resource "google_storage_bucket" "website" {
  provider = google
  name = "${var.gcp_project}-static-website"  # has to be globally unique
  location = "EU"
}

# Upload index.html to the bucket
resource "google_storage_bucket_object" "static_site_index" {
  name = "index.html"  # name in the bucket
  source = "../website/index.html"  # local path
  bucket = google_storage_bucket.website.name
}

# Make the bucket's index.html publicly accessible
resource "google_storage_object_access_control" "public_index_rule" {
  object = google_storage_bucket_object.static_site_index.name
  bucket = google_storage_bucket.website.name
  role = "READER"
  entity = "allUsers"
}

# --- CDN

# Set the bucket as a CDN backend
resource "google_compute_backend_bucket" "website_backend" {
  name = "website-bucket"
  bucket_name = google_storage_bucket.website.name
  description = "Contains files for the website"
  enable_cdn = true
}

# --- DNS

# Reserve a static external IP address
resource "google_compute_global_address" "website_ip" {
  name = "website-loadbalancer-ip"
}

# Retrieve our DNS zone data
data "google_dns_managed_zone" "dns_zone" {
  name = var.gcp_dns_zone
}

# Add the reserved external IP address to our DNS zone
resource "google_dns_record_set" "website" {
  managed_zone = data.google_dns_managed_zone.dns_zone.name
  type = "A"
  ttl = 300
  name = "website.${data.google_dns_managed_zone.dns_zone.dns_name}" # domain name
  rrdatas = [google_compute_global_address.website_ip.address]
}

# --- LOAD BALANCER

# Create ingress rule
resource "google_compute_url_map" "website" {
  name = "website-bucket-redirect"
  default_service = google_compute_backend_bucket.website_backend.self_link

  host_rule  {
    hosts = ["*"]
    path_matcher = "allpaths"
  }
  path_matcher {
    name = "allpaths"
    default_service = google_compute_backend_bucket.website_backend.self_link
  }
}

# Create Load Balancer
resource "google_compute_target_http_proxy" "website" {
  name = "website-load-balancer"
  url_map = google_compute_url_map.website.self_link
}

# Link IP address to Load Balancer
resource "google_compute_global_forwarding_rule" "default" {
  name = "website-forwarding-rule"
  load_balancing_scheme = "EXTERNAL"
  ip_address = google_compute_global_address.website_ip.address
  ip_protocol = "TCP"
  port_range = "80"
  target = google_compute_target_http_proxy.website.self_link
}

