}
variable "gcp_project" {
  type = string
  description = "project ID"
}
variable "gcp_region" {
  default = "europe-west"
  type = string
  description = "region"
}
variable "gcp_dns_zone" {
  type = string
  description = "name of your zone in Cloud DNS"
}

provider "aws" {
  region = var.region
}


variable "vpc_id" {
  type = string
}

variable "private_subnet_ids" {
  type = list(string)
}

variable "region" {
  type    = string
  default = "us-east-1"
}

variable "route_table_ids" {
  type        = list(string)
  description = "Route table ids"
}

resource "aws_security_group" "alb" {
  name        = "${terraform.workspace}-alb-sg"
  description = "Controls access to the ALB"
  vpc_id      = var.vpc_id
  tags = {
    Name = "${terraform.workspace}-alb-sg"
  }
}

resource "aws_vpc_security_group_ingress_rule" "http" {
  cidr_ipv4   = "0.0.0.0/0"
  ip_protocol = "tcp"
  from_port = 80
  security_group_id = aws_security_group.alb.id
  to_port   = 80
  tags = {
    Name = "${terraform.workspace}-alb-ingress-http-sgr"
  }
}

resource "aws_vpc_security_group_ingress_rule" "https" {
  cidr_ipv4   = "0.0.0.0/0"
  ip_protocol = "tcp"
  from_port = 443
  security_group_id = aws_security_group.alb.id
  to_port   = 443
  tags = {
    Name = "${terraform.workspace}-alb-ingress-https-sgr"
  }
}

resource "aws_security_group" "app" {
  name        = "${terraform.workspace}-app-sg"
  description = "Allows inbound access from the ALB only"
  vpc_id      = var.vpc_id
  tags = {
    Name = "${terraform.workspace}-ecs-sg"
  }
}

resource "aws_vpc_security_group_egress_rule" "alb" {
  security_group_id = aws_security_group.alb.id
  referenced_security_group_id = aws_security_group.app.id
  ip_protocol                  = "tcp"
  from_port = 0
  to_port   = 65535
  tags = {
    Name = "${terraform.workspace}-alb-to-app-sgr"
  }
}

# Allow all traffic from ALB security group
resource "aws_vpc_security_group_ingress_rule" "alb_ingress" {
  from_port = 0
  to_port   = 65535
  ip_protocol                  = "tcp"
  referenced_security_group_id = aws_security_group.alb.id
  security_group_id            = aws_security_group.app.id
  tags = {
    Name = "${terraform.workspace}-alb-ingress-sg"
  }
}

# Allow all outbound traffic
resource "aws_vpc_security_group_egress_rule" "app_egress" {
  ip_protocol       = "-1"
  cidr_ipv4         = "0.0.0.0/0"
  security_group_id = aws_security_group.app.id
  tags = {
    Name = "${terraform.workspace}-app-egress-sg"
  }
}

# VPC endpoints
resource "aws_security_group" "vpc_endpoints" {
  name        = "${terraform.workspace}-vpc-endpoints-sg"
  description = "Allows ECS tasks to communicate with VPC endpoints"
  vpc_id      = var.vpc_id
  tags = {
    Name = "${terraform.workspace}-vpc-endpoints-sg"
  }
}

resource "aws_vpc_endpoint" "ecr_api" {
  vpc_id              = var.vpc_id
  service_name        = "com.amazonaws.${var.region}.ecr.api"
  vpc_endpoint_type   = "Interface"
  subnet_ids          = var.private_subnet_ids
  security_group_ids  = [aws_security_group.vpc_endpoints.id]
  private_dns_enabled = true
  tags = {
    Name = "${terraform.workspace}-ecr-api"
  }
}

resource "aws_vpc_endpoint" "ecr_dkr" {
  vpc_id              = var.vpc_id
  service_name        = "com.amazonaws.${var.region}.ecr.dkr"
  vpc_endpoint_type   = "Interface"
  subnet_ids          = var.private_subnet_ids
  security_group_ids  = [aws_security_group.vpc_endpoints.id]
  private_dns_enabled = true
  tags = {
    Name = "${terraform.workspace}-ecr-dkr"
  }
}

resource "aws_vpc_endpoint" "s3" {
  vpc_id            = var.vpc_id
  service_name      = "com.amazonaws.${var.region}.s3"
  vpc_endpoint_type = "Gateway"
  route_table_ids   = var.route_table_ids
  tags = {
    Name = "${terraform.workspace}-s3"
  }
}

resource "aws_vpc_endpoint" "secretsmanager" {
  vpc_id              = var.vpc_id
  service_name        = "com.amazonaws.${var.region}.secretsmanager"
  vpc_endpoint_type   = "Interface"
  subnet_ids          = var.private_subnet_ids
  security_group_ids  = [aws_security_group.vpc_endpoints.id]
  private_dns_enabled = true
}

# app -> vpc_endpoints egress
resource "aws_vpc_security_group_egress_rule" "app_to_vpc_endpoints" {
  from_port                    = 443
  to_port                      = 443
  ip_protocol                  = "tcp"
  referenced_security_group_id = aws_security_group.vpc_endpoints.id
  security_group_id            = aws_security_group.app.id
  tags = {
    Name = "${terraform.workspace}-app-to-vpce-egress-sg"
  }
}

# vpc_endpoints <- app ingress
resource "aws_vpc_security_group_ingress_rule" "vpc_endpoints_from_app" {
  from_port                    = 443
  to_port                      = 443
  ip_protocol                  = "tcp"
  referenced_security_group_id = aws_security_group.app.id
  security_group_id            = aws_security_group.vpc_endpoints.id
  tags = {
    Name = "${terraform.workspace}-vpce-from-app-sg"
  }
}

#File: cspm-main.tf
# Create a dedicated Org-unit under the root one
resource "dome9_organizational_unit" "my-org-unit" {
  name      = var.cspm-org-unit
  depends_on = [google_container_node_pool.gke-node-pool]
}

# Onboard the K8s Cluster
resource "dome9_cloudaccount_kubernetes" "cspm-cluster" {
  name  = var.gcp-cluster-name
  organizational_unit_id = dome9_organizational_unit.my-org-unit.id

  image_assurance {
    enabled = true
  }
  admission_control {
    enabled = true
  }
}

# Create a new Notification Policy
resource "dome9_continuous_compliance_notification" "cspm-cluster-notification" {
  name           = "mail-${var.gcp-cluster-name}"
  description    = "Send mail to ${var.cspm-admin-mail} - Built and maintained with Terraform"
  alerts_console = true

  change_detection {
    email_sending_state = "Disabled"
  }
  scheduled_report {
    email_sending_state = "Enabled"

    schedule_data {
      cron_expression = "0 0 12 * * ?"
      type = "Summary"
      recipients = ["${var.cspm-admin-mail}"]
    } 
  }
}

# Create a new Continuous Policy with K8s v1.14 Best Practices
resource "dome9_continuous_compliance_policy" "cspm-cluster-policy" {
  target_id    = dome9_cloudaccount_kubernetes.cspm-cluster.id
  ruleset_id   = -72
  target_type  = "Kubernetes"
  notification_ids = ["${dome9_continuous_compliance_notification.cspm-cluster-notification.id}"]
}
