resource "helm_release" "asset-mgmt" {
  name       = "asset-mgmt"
  repository = "https://raw.githubusercontent.com/CheckPointSW/charts/master/repository/"
  chart      = "cloudguard"
  namespace  = "checkpoint-cspm"
  create_namespace = true

  set {
    name  = "clusterID"
    value = "${dome9_cloudaccount_kubernetes.cspm-cluster.id}"
  }
  set {
    name  = "credentials.user"
    value = "${var.cspm-key-id}"
  }
  set {
    name  = "credentials.secret"
    value = "${var.cspm-key-secret}"
  }
  set {
    name  = "datacenter"
    value = "${var.cspm-residency}"
  }
  set {
    name = "addons.imageScan.enabled"
    value= "true"
  }
  set {
    name = "addons.admissionControl.enabled"
    value= "true"
  }
  depends_on = [dome9_cloudaccount_kubernetes.cspm-cluster]
}

#File: k8s-main.tf
resource "kubernetes_namespace" "app-namespace" {
  metadata {
    name = "${var.app-name}-ns"
  }
  depends_on = [google_container_node_pool.gke-node-pool]
}

resource "kubernetes_deployment" "app-deployment" {
  metadata {
    name      = var.app-name
    namespace = kubernetes_namespace.app-namespace.id
    labels = {
      app = var.app-name
    }
  }
  spec {
    replicas = 3
    selector {
      match_labels = {
        app = var.app-name
      }
    }
    template {
      metadata {
        labels = {
          app = var.app-name
        }
      }
      spec {
        container {
          image = var.app-image
          name  = var.app-name
        }
      }
    }
  }
}

resource "kubernetes_service" "app-svc" {
  metadata {
    name      = var.app-name
    namespace = kubernetes_namespace.app-namespace.id
  }
  spec {
    selector = {
