# ========== VARIABLES ==========
variable "aws_region" {
  description = "AWS region"
  default     = "us-west-2"
}

variable "availability_zone" {
  description = "Availability zone"
  default     = "us-west-2a"
}

variable "key_name" {
  description = "SSH key pair name"
  type        = string
}

variable "vpc_cidr" {
  description = "CIDR block for VPC"
  default     = "10.0.0.0/16"
}

variable "subnet_cidr" {
  description = "CIDR block for subnet"
  default     = "10.0.1.0/24"
}

variable "ssh_cidr" {
  description = "Allowed CIDR for SSH"
  default     = "0.0.0.0/0"
}

variable "ubuntu_docker_ami" {
  description = "Ubuntu AMI ID with Docker preinstalled"
  type        = string
}

variable "instance_type" {
  description = "EC2 instance type"
  default     = "t3.micro"
}

variable "tags" {
  description = "Common tags"
  type        = map(string)
  default     = {
    Project = "TerraformE2E"
    Env     = "Dev"
  }
}

# ========== VPC ==========
resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags                 = var.tags
}

# ========== Subnet ==========
resource "aws_subnet" "public" {
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.subnet_cidr
  availability_zone       = var.availability_zone
  map_public_ip_on_launch = true
  tags                    = var.tags
}

# ========== Internet Gateway ==========
resource "aws_internet_gateway" "igw" {
  vpc_id = aws_vpc.main.id
  tags   = var.tags
}

# ========== Route Table ==========
resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = aws_internet_gateway.igw.id
  }

  tags = var.tags
}

resource "aws_route_table_association" "public" {
  subnet_id      = aws_subnet.public.id
  route_table_id = aws_route_table.public.id
}

# ========== Security Group ==========
resource "aws_security_group" "ssh" {
  name        = "allow-ssh"
  description = "Allow SSH access"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 22
    to_port     = 22
    protocol    = "tcp"
    cidr_blocks = [var.ssh_cidr]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = var.tags
}

# ========== EC2 Instance ==========
resource "aws_instance" "ubuntu" {
  ami                         = var.ubuntu_docker_ami
  instance_type               = var.instance_type
  subnet_id                   = aws_subnet.public.id
  vpc_security_group_ids      = [aws_security_group.ssh.id]
  key_name                    = var.key_name
  associate_public_ip_address = true

  tags = merge(var.tags, {
    Name = "ubuntu-docker"
  })
}

# ========== OUTPUTS ==========
output "instance_public_ip" {
  value = aws_instance.ubuntu.public_ip
}

output "ssh_connection_string" {
  value = "ssh -i ~/.ssh/YOUR_PRIVATE_KEY.pem ubuntu@${aws_instance.ubuntu.public_ip}"
  description = "SSH command to connect (replace key path)"
}

provider "aws" {
  region = "us-east-1"
}

resource "aws_cloudwatch_log_group" "app_logs" {
  name              = "/app/my-service"
  retention_in_days = 14
}

