  bucket = aws_s3_bucket.website.id

  policy = jsonencode({
    Statement = [
      {
        Sid       = "PublicReadGetObject"
        Effect    = "Allow"
        Principal = "*"
        Action    = "s3:GetObject"
        Resource  = "arn:aws:s3:::${aws_s3_bucket.website.bucket}/*"
      }
    ]
  })
}

resource "aws_iam_user" "website_user_gabe" {
  name = "gabe"
  tags = {
    dept      = "developers"
    component = "website"
  }
}

resource "aws_iam_user_policy" "gabe_readonly_policy" {
  name = "website-readonly"
  user = aws_iam_user.website_user_gabe.name

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect   = "Allow"
        Action   = ["s3:GetObject"]
        Resource = "arn:aws:s3:::${aws_s3_bucket.website.bucket}/*"
      }
    ]
  })
