# Variables
variable "snapshot_name" {
  description = "Name of the snapshot to restore from. Leave empty for fresh install."
  type        = string
  default     = ""
}

variable "project_id" {
  description = "GCP Project ID"
  type        = string
  default     = "your-gcp-project-name"
}

# Provider
