}

resource "azurerm_key_vault" "elkvault" {
  name                            = random_string.vault_name.result
  location                        = var.location
  resource_group_name             = var.rg
  tenant_id                       = data.azurerm_client_config.current.tenant_id
  sku_name                        = "premium"
